type GameStatus = 'playing' | 'won' | 'lost'

interface HangmanProps {
  mode: 'solo' | 'multiplayer'
  difficulty: Difficulty
  room?: any
  onScoreUpdate: (score: number) => void
  onExit: () => void
}

const WORD_LISTS = {
  easy: ['CAT', 'DOG', 'SUN', 'TREE', 'BOOK', 'DOOR', 'FISH', 'BIRD', 'CAKE', 'BALL'],
  medium: ['COMPUTER', 'ELEPHANT', 'RAINBOW', 'KITCHEN', 'BIRTHDAY', 'DRAGON', 'GARDEN', 'PLANET'],
  hard: ['JAVASCRIPT', 'ALGORITHM', 'ARCHITECTURE', 'EXTRAORDINARY', 'PHOTOSYNTHESIS', 'INTERNATIONAL']
}

const HANGMAN_STAGES = [
  '',
  '  ___\n  |  |\n     |\n     |\n     |\n_____|',
  '  ___\n  |  |\n  O  |\n     |\n     |\n_____|',
  '  ___\n  |  |\n  O  |\n  |  |\n     |\n_____|',
  '  ___\n  |  |\n  O  |\n /|  |\n     |\n_____|',
  '  ___\n  |  |\n  O  |\n /|\\ |\n     |\n_____|',
  '  ___\n  |  |\n  O  |\n /|\\ |\n /   |\n_____|',
  '  ___\n  |  |\n  O  |\n /|\\ |\n / \\ |\n_____|'
]

export function Hangman({ mode, difficulty, room, onScoreUpdate, onExit }: HangmanProps) {
  const { user } = useAuth()
  const { toast } = useToast()
  const [currentWord, setCurrentWord] = useState('')
  const [guessedLetters, setGuessedLetters] = useState<Set<string>>(new Set())
  const [wrongGuesses, setWrongGuesses] = useState(0)
  const [gameStatus, setGameStatus] = useState<GameStatus>('playing')
  const [score, setScore] = useState(0)
  const [currentGuess, setCurrentGuess] = useState('')
  const [hint, setHint] = useState('')
  const [roomMembers, setRoomMembers] = useState<any[]>([])
  const [isMyTurn, setIsMyTurn] = useState(true)

  const maxWrongGuesses = 6

  useEffect(() => {
    startNewGame()
  }, [difficulty])

  useEffect(() => {
    if (mode === 'multiplayer' && room) {
      fetchRoomMembers()
      subscribeToMoves()
    }
  }, [mode, room])

  const fetchRoomMembers = async () => {
    if (!room) return

    try {
      const { data } = await supabase
        .from('game_room_members')
        .select(`
          *,
          users!game_room_members_user_id_fkey (
            id,
            first_name,
            last_name
          )
        `)
        .eq('room_id', room.id)

      if (data) {
        setRoomMembers(data)
        // Host goes first
        const isHost = data[0]?.user_id === user?.id
        setIsMyTurn(isHost)
      }
    } catch (error) {
      console.error('Error fetching room members:', error)
    }
  }

  const subscribeToMoves = () => {
    if (!room) return

    const channel = supabase
      .channel('hangman-moves')
      .on(
        'postgres_changes',
        {
          event: 'INSERT',
          schema: 'public',
          table: 'game_moves',
          filter: `room_id=eq.${room.id}`
        },
        (payload) => {
          const moveData = payload.new.move_data as any
          if (payload.new.user_id !== user?.id) {
            // Opponent made a move
            processMove(moveData.letter, false)
            setIsMyTurn(true)
          }
        }
      )
      .subscribe()

    return () => {
      supabase.removeChannel(channel)
    }
  }

  const startNewGame = () => {
    const wordList = WORD_LISTS[difficulty]
    const word = wordList[Math.floor(Math.random() * wordList.length)]
    setCurrentWord(word)
    setGuessedLetters(new Set())
    setWrongGuesses(0)
    setGameStatus('playing')
    setCurrentGuess('')
    setHint(getHint(word))
  }

  const getHint = (word: string): string => {
    const hints: Record<string, string> = {
      'CAT': 'A furry pet that meows',
      'DOG': 'Man\'s best friend',
      'SUN': 'Source of light in our solar system',
      'TREE': 'Tall plant with branches and leaves',
      'COMPUTER': 'Electronic device for processing data',
      'ELEPHANT': 'Large gray mammal with a trunk',
      'JAVASCRIPT': 'Popular programming language',
      'ALGORITHM': 'Step-by-step problem solving method'
    }

    return hints[word] || `A ${word.length}-letter word`
  }

  const processMove = (letter: string, isMyMove: boolean = true) => {
    if (gameStatus !== 'playing') return

    const upperLetter = letter.toUpperCase()
    if (guessedLetters.has(upperLetter)) return

    const newGuessedLetters = new Set([...guessedLetters, upperLetter])
    setGuessedLetters(newGuessedLetters)

    if (currentWord.includes(upperLetter)) {
      // Correct guess
      const isWordComplete = currentWord.split('').every(letter => newGuessedLetters.has(letter))
      if (isWordComplete) {
        setGameStatus('won')
        if (isMyMove) {
          const points = difficulty === 'easy' ? 10 : difficulty === 'medium' ? 15 : 20
          updateScore(score + points)
        }
      }
    } else {
      // Wrong guess
      const newWrongGuesses = wrongGuesses + 1
      setWrongGuesses(newWrongGuesses)
      if (newWrongGuesses >= maxWrongGuesses) {
        setGameStatus('lost')
      }
    }
  }

  const makeGuess = async (letter: string) => {
    if (mode === 'multiplayer' && !isMyTurn) {
      toast({
        title: "Not your turn!",
        description: "Wait for your opponent to guess.",
        variant: "destructive"
      })
      return
    }

    if (mode === 'multiplayer' && room) {
      // Send move to other players
      try {
        await supabase
          .from('game_moves')
          .insert({
            room_id: room.id,
            user_id: user?.id,
            move_data: { letter }
          })

        setIsMyTurn(false)
      } catch (error) {
        console.error('Error sending move:', error)
      }
    }

    processMove(letter)
    setCurrentGuess('')
  }

  const handleGuessSubmit = (e: React.FormEvent) => {
    e.preventDefault()
    if (currentGuess.length === 1 && /[A-Za-z]/.test(currentGuess)) {
      makeGuess(currentGuess)
    }
  }

  const updateScore = (newScore: number) => {
    setScore(newScore)
    onScoreUpdate(newScore)
  }

  const getDisplayWord = () => {
    return currentWord
      .split('')
      .map(letter => guessedLetters.has(letter) ? letter : '_')
      .join(' ')
  }

  const getAvailableLetters = () => {
    const alphabet = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'
    return alphabet.split('').filter(letter => !guessedLetters.has(letter))
  }

  const getStatusMessage = () => {
    if (mode === 'multiplayer') {
      if (gameStatus === 'playing') {
        return isMyTurn ? "Your turn to guess!" : "Waiting for opponent..."
      }
    }

    switch (gameStatus) {
      case 'won':
        return 'ðŸŽ‰ You won! Word guessed!'
      case 'lost':
        return `ðŸ˜” You lost! The word was: ${currentWord}`
      default:
        return `Guess the word! (${wrongGuesses}/${maxWrongGuesses} wrong guesses)`
    }
  }

  return (
    <div className="max-w-2xl mx-auto space-y-6">
      <Card>
        <CardHeader>
          <div className="flex justify-between items-center">
            <CardTitle>Hangman</CardTitle>
            <div className="flex gap-2 items-center">
              <Badge variant="outline">
                {mode === 'solo' ? `${difficulty} mode` : 'Multiplayer'}
              </Badge>
              <Badge variant="secondary">Score: {score}</Badge>
            </div>
          </div>
        </CardHeader>
        <CardContent className="space-y-6">
          {/* Game Status */}
          <div className="text-center">
            <p className="text-lg font-medium mb-2">{getStatusMessage()}</p>
            {hint && (
              <p className="text-sm text-muted-foreground">ðŸ’¡ Hint: {hint}</p>
            )}
          </div>

          {/* Hangman Drawing */}
          <div className="flex justify-center">
            <pre className="text-sm font-mono bg-muted p-4 rounded">
              {HANGMAN_STAGES[wrongGuesses]}
            </pre>
          </div>

          {/* Word Display */}
          <div className="text-center">
            <div className="text-3xl font-mono font-bold tracking-wider mb-4">
              {getDisplayWord()}
            </div>
            <p className="text-sm text-muted-foreground">
              {currentWord.length} letters
            </p>
          </div>

          {/* Guess Input */}
          {gameStatus === 'playing' && (mode !== 'multiplayer' || isMyTurn) && (
            <form onSubmit={handleGuessSubmit} className="flex gap-2 justify-center">
              <Input
                type="text"
                value={currentGuess}
                onChange={(e) => setCurrentGuess(e.target.value.toUpperCase())}
                placeholder="Enter a letter"
                maxLength={1}
                className="w-20 text-center text-lg font-bold"
              />
              <Button type="submit" disabled={!currentGuess || guessedLetters.has(currentGuess)}>
                Guess
              </Button>
            </form>
          )}

          {/* Letter Grid */}
          <div className="space-y-4">
            <h4 className="font-semibold text-center">Available Letters</h4>
            <div className="grid grid-cols-6 gap-2">
              {getAvailableLetters().map(letter => (
                <Button
                  key={letter}
                  variant="outline"
                  size="sm"
                  onClick={() => makeGuess(letter)}
                  disabled={gameStatus !== 'playing' || (mode === 'multiplayer' && !isMyTurn)}
                  className="aspect-square"
                >
                  {letter}
                </Button>
              ))}
            </div>
          </div>

          {/* Guessed Letters */}
          {guessedLetters.size > 0 && (
            <div className="space-y-2">
              <h4 className="font-semibold text-center">Guessed Letters</h4>
              <div className="flex flex-wrap gap-1 justify-center">
                {Array.from(guessedLetters).map(letter => (
                  <Badge
                    key={letter}
                    variant={currentWord.includes(letter) ? "default" : "destructive"}
                  >
                    {letter}
                  </Badge>
                ))}
              </div>
            </div>
          )}

          {/* Game Controls */}
          <div className="flex justify-center gap-4">
            <Button onClick={startNewGame} variant="outline">
              New Game
            </Button>
            <Button onClick={onExit} variant="secondary">
              Exit
            </Button>
          </div>

          {/* Multiplayer Info */}
          {mode === 'multiplayer' && roomMembers.length > 0 && (
            <div className="text-center text-sm text-muted-foreground">
              <p>Players: {roomMembers.map(m => m.users.first_name).join(', ')}</p>
              {room && <p>Room Code: {room.room_code}</p>}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  )
}

src/components/FileSharing/FileUploadDia.content:
import { useState, useRef } from 'react'
import { Upload, File, X, Check } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import { Progress } from '@/components/ui/progress'
import { toast } from '@/hooks/use-toast'
import { supabase } from '@/integrations/supabase/client'
import { useAuth } from '@/contexts/AuthContext'

interface FileUploadDialogProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  onFileUpload: (file: File, url: string) => void
}

export function FileUploadDialog({ open, onOpenChange, onFileUpload }: FileUploadDialogProps) {
  const [uploadProgress, setUploadProgress] = useState(0)
  const [isUploading, setIsUploading] = useState(false)
  const [selectedFile, setSelectedFile] = useState<File | null>(null)
  const fileInputRef = useRef<HTMLInputElement>(null)
  const { user } = useAuth()

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]
    if (!file) return

    // Check file size (100MB limit)
    const maxSize = 100 * 1024 * 1024 // 100MB in bytes
    if (file.size > maxSize) {
      toast({
        title: 'File too large',
        description: 'Please select a file smaller than 100MB.',
        variant: 'destructive'
      })
      return
    }

    setSelectedFile(file)
  }

  const uploadFile = async () => {
    if (!selectedFile) return

    setIsUploading(true)
    setUploadProgress(0)

    try {
      // Create unique file path
      const fileExt = selectedFile.name.split('.').pop()
      const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`
      const filePath = `${user?.id}/${fileName}`

      // Upload to Supabase Storage
      const { data, error } = await supabase.storage
        .from('message-files')
        .upload(filePath, selectedFile, {
          cacheControl: '3600',
          upsert: false
        })

      if (error) throw error

      // Get public URL
      const { data: urlData } = supabase.storage
        .from('message-files')
        .getPublicUrl(filePath)

      setUploadProgress(100)
      onFileUpload(selectedFile, urlData.publicUrl)

      toast({
        title: 'File uploaded successfully',
        description: `${selectedFile.name} has been uploaded.`
      })

      // Reset state
      setSelectedFile(null)
      setUploadProgress(0)
      setIsUploading(false)
      onOpenChange(false)
    } catch (error: any) {
      console.error('Upload error:', error)
      toast({
        title: 'Upload failed',
        description: error.message || 'Failed to upload file. Please try again.',
        variant: 'destructive'
      })
      setIsUploading(false)
    }
  }

  const handleDrop = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault()
    const file = event.dataTransfer.files[0]
    if (file) {
      // Check file size
      const maxSize = 100 * 1024 * 1024
      if (file.size > maxSize) {
        toast({
          title: 'File too large',
          description: 'Please select a file smaller than 100MB.',
          variant: 'destructive'
        })
        return
      }
      setSelectedFile(file)
    }
  }

  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault()
  }

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes'
    const k = 1024
    const sizes = ['Bytes', 'KB', 'MB', 'GB']
    const i = Math.floor(Math.log(bytes) / Math.log(k))
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i]
  }

  const resetDialog = () => {
    setSelectedFile(null)
    setUploadProgress(0)
    setIsUploading(false)
  }

  return (
    <Dialog open={open} onOpenChange={(open) => {
      onOpenChange(open)
      if (!open) resetDialog()
    }}>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Upload File</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          {!selectedFile ? (
            <div
              className="border-2 border-dashed border-muted-foreground/25 rounded-lg p-8 text-center cursor-pointer hover:border-primary/50 transition-colors"
              onDrop={handleDrop}
              onDragOver={handleDragOver}
              onClick={() => fileInputRef.current?.click()}
            >
              <Upload className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-sm text-muted-foreground mb-2">
                Drag and drop a file here, or click to select
              </p>
              <p className="text-xs text-muted-foreground">
                Maximum file size: 100MB
              </p>
              <input
                ref={fileInputRef}
                type="file"
                className="hidden"
                onChange={handleFileSelect}
                accept="*/*"
              />
            </div>
          ) : (
            <div className="space-y-4">
              <div className="flex items-center gap-3 p-3 bg-muted/30 rounded-lg">
                <File className="h-8 w-8 text-primary" />
                <div className="flex-1 min-w-0">
                  <p className="font-medium truncate">{selectedFile.name}</p>
                  <p className="text-sm text-muted-foreground">
                    {formatFileSize(selectedFile.size)}
                  </p>
                </div>
                {!isUploading && (
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => setSelectedFile(null)}
                  >
                    <X className="h-4 w-4" />
                  </Button>
                )}
              </div>

              {isUploading && (
                <div className="space-y-2">
                  <div className="flex items-center justify-between text-sm">
                    <span>Uploading...</span>
                    <span>{Math.round(uploadProgress)}%</span>
                  </div>
                  <Progress value={uploadProgress} className="h-2" />
                </div>
              )}

              <div className="flex justify-end gap-2">
                <Button
                  variant="outline"
                  onClick={() => onOpenChange(false)}
                  disabled={isUploading}
                >
                  Cancel
                </Button>
                <Button
                  onClick={uploadFile}
                  disabled={isUploading}
                  className="bg-gradient-primary hover:opacity-90"
                >
                  {isUploading ? (
                    'Uploading...'
                  ) : uploadProgress === 100 ? (
                    <>
                      <Check className="h-4 w-4 mr-2" />
                      Uploaded
                    </>
                  ) : (
                    <>
                      <Upload className="h-4 w-4 mr-2" />
                      Upload File
                    </>
                  )}
                </Button>
              </div>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  )
}

src/lib/supabase/server.ts.content:
import { createServerClient } from '@supabase/ssr'
import { cookies } from 'next/headers'
import { ENV } from '@/lib/config'

export async function createClient() {
  const cookieStore = await cookies()

  return createServerClient(
    ENV.SUPABASE_URL,
    ENV.SUPABASE_ANON_KEY,
    {
      cookies: {
        getAll() {
          return cookieStore.getAll()
        },
        setAll(cookiesToSet) {
          try {
            cookiesToSet.forEach(({ name, value, options }) =>
              cookieStore.set(name, value, options)
            )
          } catch {
            // The `setAll` method was called from a Server Component.
            // This can be ignored if you have middleware refreshing
            // user sessions.
          }
        },
      },
    }
  )
}

/**
 * Create a Supabase client with service role key for admin operations
 * WARNING: Only use this in server-side code. Never expose the service role key to the client.
 */
export function createServiceClient() {
  return createServerClient(
    ENV.SUPABASE_URL,
    ENV.SUPABASE_SERVICE_ROLE_KEY,
    {
      cookies: {},
    }
  )
}

src/app/api/lumenr/receipts/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { receipts } from '@/db/schema';
import { eq, desc, gte, lte, and } from 'drizzle-orm';
import { getAuthUser } from '@/lib/auth-api';
import { jsonOk, jsonError } from '@/lib/api-utils';
import { supabase } from '@/integrations/supabase/client';

function isValidDateFormat(dateString: string): boolean {
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(dateString)) {
    return false;
  }
  const date = new Date(dateString);
  return date instanceof Date && !isNaN(date.getTime());
}

async function getSignedUrlFromPath(imagePath: string | null): Promise<string | null> {
  if (!imagePath || !supabase) return null;

  // Extract the storage path from either a full URL or just the path
  let storagePath = imagePath;

  // If it's a full URL, extract just the path part
  if (imagePath.includes('/storage/v1/object/')) {
    const pathMatch = imagePath.match(/\/receipts\/(.+?)(?:\?|$)/);
    if (pathMatch) {
      storagePath = pathMatch[1];
    }
  } else if (imagePath.includes('/receipts/')) {
    const pathMatch = imagePath.match(/\/receipts\/(.+?)(?:\?|$)/);
    if (pathMatch) {
      storagePath = pathMatch[1];
    }
  }

  // Remove bucket name if it's included
  if (storagePath.startsWith('receipts/')) {
    storagePath = storagePath.substring('receipts/'.length);
  }

  try {
    const { data, error } = await supabase.storage
      .from('receipts')
      .createSignedUrl(storagePath, 31536000); // 1 year

    if (error) {
      console.error('[Receipts API] Error generating signed URL:', error);
      return null;
    }

    return data.signedUrl;
  } catch (error) {
    console.error('[Receipts API] Exception generating signed URL:', error);
    return null;
  }
}

export async function GET(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (id) {
      const receiptId = parseInt(id);
      if (isNaN(receiptId)) {
        return jsonError('Valid ID is required', 400);
      }

      const receipt = await db
        .select()
        .from(receipts)
        .where(and(eq(receipts.id, receiptId), eq(receipts.userId, userId)))
        .limit(1);

      if (receipt.length === 0) {
        return jsonError('Receipt not found', 404);
      }

      // Convert numeric strings to numbers and refresh signed URL
      const signedUrl = await getSignedUrlFromPath(receipt[0].imageUrl);
      const formattedReceipt = {
        ...receipt[0],
        amount: parseFloat(receipt[0].amount as any) || 0,
        imageUrl: signedUrl || receipt[0].imageUrl // Use fresh signed URL if available
      };

      return jsonOk(formattedReceipt);
    }

    const limit = Math.min(parseInt(searchParams.get('limit') ?? '100'), 100);
    const offset = parseInt(searchParams.get('offset') ?? '0');
    const category = searchParams.get('category');
    const type = searchParams.get('type'); // 'expense' or 'client'
    const clientId = searchParams.get('clientId');
    const startDate = searchParams.get('startDate');
    const endDate = searchParams.get('endDate');

    let conditions = [eq(receipts.userId, userId)];

    if (type && (type === 'expense' || type === 'client')) {
      conditions.push(eq(receipts.type, type));
    }

    if (clientId) {
      const clientIdInt = parseInt(clientId);
      if (!isNaN(clientIdInt)) {
        conditions.push(eq(receipts.clientId, clientIdInt));
      }
    }

    if (category) {
      conditions.push(eq(receipts.category, category));
    }

    if (startDate) {
      if (!isValidDateFormat(startDate)) {
        return jsonError('Invalid startDate format. Use YYYY-MM-DD', 400);
      }
      conditions.push(gte(receipts.date, startDate));
    }

    if (endDate) {
      if (!isValidDateFormat(endDate)) {
        return jsonError('Invalid endDate format. Use YYYY-MM-DD', 400);
      }
      conditions.push(lte(receipts.date, endDate));
    }

    const results = await db
      .select()
      .from(receipts)
      .where(and(...conditions))
      .orderBy(desc(receipts.date))
      .limit(limit)
      .offset(offset);

    // Convert numeric strings to numbers and refresh signed URLs for frontend
    const formattedResults = await Promise.all(results.map(async (receipt) => {
      const signedUrl = await getSignedUrlFromPath(receipt.imageUrl);
      return {
        ...receipt,
        amount: parseFloat(receipt.amount as any) || 0,
        imageUrl: signedUrl || receipt.imageUrl // Use fresh signed URL if available
      };
    }));

    return jsonOk(formattedResults);
  } catch (error) {
    console.error('GET error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const body = await request.json();
    const { vendor, amount, category, date, type, clientId, imageUrl, notes } = body;

    if (!vendor || !vendor.trim()) {
      return jsonError('Vendor is required', 400);
    }

    if (amount === undefined || amount === null) {
      return jsonError('Amount is required', 400);
    }

    const parsedAmount = parseFloat(amount);
    if (isNaN(parsedAmount) || parsedAmount <= 0) {
      return jsonError('Amount must be a positive number', 400);
    }

    if (!category || !category.trim()) {
      return jsonError('Category is required', 400);
    }

    if (!date || !date.trim()) {
      return jsonError('Date is required', 400);
    }

    if (!isValidDateFormat(date)) {
      return jsonError('Date must be in YYYY-MM-DD format', 400);
    }

    // Validate type if provided
    const receiptType = type?.trim() || 'expense';
    if (receiptType !== 'expense' && receiptType !== 'client') {
      return jsonError('Type must be "expense" or "client"', 400);
    }

    // Validate clientId if type is client
    let parsedClientId = null;
    if (receiptType === 'client') {
      if (!clientId) {
        return jsonError('Client ID is required for client receipts', 400);
      }
      parsedClientId = parseInt(clientId);
      if (isNaN(parsedClientId)) {
        return jsonError('Client ID must be a valid number', 400);
      }
    }

    const now = new Date().toISOString();

    const newReceipt = await db
      .insert(receipts)
      .values({
        vendor: vendor.trim(),
        amount: parsedAmount.toString(),
        category: category.trim(),
        date: date.trim(),
        type: receiptType,
        clientId: parsedClientId,
        imageUrl: imageUrl?.trim() || null,
        notes: notes?.trim() || null,
        userId: userId,
        createdAt: now,
        updatedAt: now,
      })
      .returning();

    // Convert numeric strings to numbers for frontend
    const formattedReceipt = {
      ...newReceipt[0],
      amount: parseFloat(newReceipt[0].amount as any) || 0
    };

    return jsonOk(formattedReceipt, 201);
  } catch (error) {
    console.error('POST error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function PUT(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id || isNaN(parseInt(id))) {
      return jsonError('Valid ID is required', 400);
    }

    const receiptId = parseInt(id);

    const existing = await db
      .select()
      .from(receipts)
      .where(and(eq(receipts.id, receiptId), eq(receipts.userId, userId)))
      .limit(1);

    if (existing.length === 0) {
      return jsonError('Receipt not found', 404);
    }

    const body = await request.json();
    const { vendor, amount, category, date, type, clientId, imageUrl, notes } = body;

    const updates: any = {
      updatedAt: new Date().toISOString(),
    };

    if (vendor !== undefined) {
      if (!vendor || !vendor.trim()) {
        return jsonError('Vendor cannot be empty', 400);
      }
      updates.vendor = vendor.trim();
    }

    if (amount !== undefined) {
      const parsedAmount = parseFloat(amount);
      if (isNaN(parsedAmount) || parsedAmount <= 0) {
        return jsonError('Amount must be a positive number', 400);
      }
      updates.amount = parsedAmount.toString();
    }

    if (category !== undefined) {
      if (!category || !category.trim()) {
        return jsonError('Category cannot be empty', 400);
      }
      updates.category = category.trim();
    }

    if (date !== undefined) {
      if (!date || !date.trim()) {
        return jsonError('Date cannot be empty', 400);
      }
      if (!isValidDateFormat(date)) {
        return jsonError('Date must be in YYYY-MM-DD format', 400);
      }
      updates.date = date.trim();
    }

    if (type !== undefined) {
      const receiptType = type.trim();
      if (receiptType !== 'expense' && receiptType !== 'client') {
        return jsonError('Type must be "expense" or "client"', 400);
      }
      updates.type = receiptType;

      // If changing to client type, clientId is required
      if (receiptType === 'client' && clientId === undefined && !existing[0].clientId) {
        return jsonError('Client ID is required for client receipts', 400);
      }
    }

    if (clientId !== undefined) {
      if (clientId === null) {
        updates.clientId = null;
      } else {
        const parsedClientId = parseInt(clientId);
        if (isNaN(parsedClientId)) {
          return jsonError('Client ID must be a valid number', 400);
        }
        updates.clientId = parsedClientId;
      }
    }

    if (imageUrl !== undefined) {
      updates.imageUrl = imageUrl?.trim() || null;
    }

    if (notes !== undefined) {
      updates.notes = notes?.trim() || null;
    }

    const updated = await db
      .update(receipts)
      .set(updates)
      .where(and(eq(receipts.id, receiptId), eq(receipts.userId, userId)))
      .returning();

    if (updated.length === 0) {
      return jsonError('Receipt not found', 404);
    }

    // Convert numeric strings to numbers for frontend
    const formattedReceipt = {
      ...updated[0],
      amount: parseFloat(updated[0].amount as any) || 0
    };

    return jsonOk(formattedReceipt);
  } catch (error) {
    console.error('PUT error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function DELETE(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id || isNaN(parseInt(id))) {
      return jsonError('Valid ID is required', 400);
    }

    const receiptId = parseInt(id);

    const existing = await db
      .select()
      .from(receipts)
      .where(and(eq(receipts.id, receiptId), eq(receipts.userId, userId)))
      .limit(1);

    if (existing.length === 0) {
      return jsonError('Receipt not found', 404);
    }

    const deleted = await db
      .delete(receipts)
      .where(and(eq(receipts.id, receiptId), eq(receipts.userId, userId)))
      .returning();

    if (deleted.length === 0) {
      return jsonError('Receipt not found', 404);
    }

    return jsonOk({
      message: 'Receipt deleted successfully',
      receipt: deleted[0],
    });
  } catch (error) {
    console.error('DELETE error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

