import { StatCard } from "./StatCard";
import { ChartCard } from "./ChartCard";
import { TrendingUp, Users, DollarSign, Activity, BarChart3, Calendar } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useEffect, useState } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/contexts/AuthContext";
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
} from "recharts";

export function ProfessionalDashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    revenue: 0,
    projects: 0,
    team: 0,
    tasks: 0,
  });
  const [revenueData, setRevenueData] = useState<any[]>([]);
  const [activityData, setActivityData] = useState<any[]>([]);
  const [recentActivity, setRecentActivity] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (user) {
      fetchDashboardData();
    }
  }, [user]);

  const fetchDashboardData = async () => {
    try {
      // Fetch projects
      const { data: projects } = await supabase
        .from('projects')
        .select('*')
        .eq('user_id', user?.id);

      // Fetch tasks
      const { data: tasks } = await supabase
        .from('tasks')
        .select('*')
        .eq('user_id', user?.id);

      // Fetch timesheets for revenue calculation
      const { data: timesheets } = await supabase
        .from('timesheets')
        .select('*')
        .eq('user_id', user?.id);

      // Calculate stats
      setStats({
        revenue: timesheets?.reduce((sum, t) => sum + (Number(t.hours) * 50), 0) || 0,
        projects: projects?.length || 0,
        team: 1, // Single user for now
        tasks: tasks?.filter(t => t.status === 'Completed').length || 0,
      });

      // Generate sample chart data for demonstration
      setRevenueData([
        { name: 'Jan', value: 4000 },
        { name: 'Feb', value: 3000 },
        { name: 'Mar', value: 5000 },
        { name: 'Apr', value: 4500 },
        { name: 'May', value: 6000 },
        { name: 'Jun', value: 5500 },
      ]);

      setActivityData([
        { name: 'Mon', tasks: 12, projects: 4 },
        { name: 'Tue', tasks: 19, projects: 3 },
        { name: 'Wed', tasks: 15, projects: 5 },
        { name: 'Thu', tasks: 22, projects: 4 },
        { name: 'Fri', tasks: 18, projects: 6 },
        { name: 'Sat', tasks: 8, projects: 2 },
        { name: 'Sun', tasks: 5, projects: 1 },
      ]);

      setRecentActivity([
        { user: 'You', action: 'completed a task', time: '2 minutes ago' },
        { user: 'Team', action: 'started a new project', time: '1 hour ago' },
        { user: 'You', action: 'logged 3 hours', time: '3 hours ago' },
      ]);
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="text-center py-12">Loading...</div>;
  }

  return (
    <div className="space-y-6">
      {/* Stats Grid */}
      <div className="grid gap-4 md:grid-cols-3">
        <StatCard
          title="Active Projects"
          value={stats.projects}
          icon={BarChart3}
          iconColor="text-blue-500"
        />
        <StatCard
          title="Team Members"
          value={stats.team}
          icon={Users}
          iconColor="text-purple-500"
        />
        <StatCard
          title="Tasks Completed"
          value={stats.tasks}
          icon={Activity}
          iconColor="text-orange-500"
        />
      </div>

      {/* Charts Row */}
      {(revenueData.length > 0 || activityData.length > 0) && (
        <div className="grid gap-6 md:grid-cols-2">
          {revenueData.length > 0 && (
            <ChartCard
              title="Revenue Overview"
              subtitle="Monthly performance"
              headerAction={
                <Select defaultValue="6m">
                  <SelectTrigger className="w-32">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="7d">Last 7 days</SelectItem>
                    <SelectItem value="30d">Last 30 days</SelectItem>
                    <SelectItem value="6m">Last 6 months</SelectItem>
                    <SelectItem value="1y">Last year</SelectItem>
                  </SelectContent>
                </Select>
              }
            >
              <ResponsiveContainer width="100%" height={280}>
                <AreaChart data={revenueData}>
                  <defs>
                    <linearGradient id="colorValue" x1="0" y1="0" x2="0" y2="1">
                      <stop offset="5%" stopColor="hsl(var(--primary))" stopOpacity={0.3} />
                      <stop offset="95%" stopColor="hsl(var(--primary))" stopOpacity={0} />
                    </linearGradient>
                  </defs>
                  <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" opacity={0.3} />
                  <XAxis
                    dataKey="name"
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={12}
                    tickLine={false}
                    axisLine={false}
                  />
                  <YAxis
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={12}
                    tickLine={false}
                    axisLine={false}
                    tickFormatter={(value) => `$${value}`}
                  />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: "hsl(var(--popover))",
                      border: "1px solid hsl(var(--border))",
                      borderRadius: "8px",
                    }}
                  />
                  <Area
                    type="monotone"
                    dataKey="value"
                    stroke="hsl(var(--primary))"
                    fillOpacity={1}
                    fill="url(#colorValue)"
                    strokeWidth={2}
                  />
                </AreaChart>
              </ResponsiveContainer>
            </ChartCard>
          )}

          {activityData.length > 0 && (
            <ChartCard
              title="Weekly Activity"
              subtitle="Tasks and projects"
              headerAction={
                <Tabs defaultValue="week" className="w-fit">
                  <TabsList>
                    <TabsTrigger value="week">Week</TabsTrigger>
                    <TabsTrigger value="month">Month</TabsTrigger>
                  </TabsList>
                </Tabs>
              }
            >
              <ResponsiveContainer width="100%" height={280}>
                <BarChart data={activityData}>
                  <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" opacity={0.3} />
                  <XAxis
                    dataKey="name"
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={12}
                    tickLine={false}
                    axisLine={false}
                  />
                  <YAxis
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={12}
                    tickLine={false}
                    axisLine={false}
                  />
                  <Tooltip
                    contentStyle={{
                      backgroundColor: "hsl(var(--popover))",
                      border: "1px solid hsl(var(--border))",
                      borderRadius: "8px",
                    }}
                  />
                  <Bar dataKey="tasks" fill="hsl(var(--primary))" radius={[8, 8, 0, 0]} />
                  <Bar dataKey="projects" fill="hsl(var(--chart-3))" radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </ChartCard>
          )}
        </div>
      )}

      {/* Recent Activity */}
      {recentActivity.length > 0 && (
        <ChartCard
          title="Recent Activity"
          subtitle="Your team's latest updates"
          headerAction={
            <Button variant="ghost" size="sm">
              View All
            </Button>
          }
        >
          <div className="space-y-4">
            {recentActivity.map((activity: any, i: number) => (
              <div key={i} className="flex items-center gap-4 p-3 rounded-lg hover:bg-accent/50 transition-colors">
                <div className="h-10 w-10 rounded-full bg-primary/10 flex items-center justify-center">
                  <span className="text-sm font-semibold">{activity.user?.charAt(0) || 'U'}</span>
                </div>
                <div className="flex-1 min-w-0">
                  <p className="text-sm">
                    <span className="font-medium">{activity.user}</span>
                    {" "}
                    <span className="text-muted-foreground">{activity.action}</span>
                  </p>
                  <p className="text-xs text-muted-foreground">{activity.time}</p>
                </div>
                <Button variant="ghost" size="sm">
                  View
                </Button>
              </div>
            ))}
          </div>
        </ChartCard>
      )}

      {/* Empty State */}
      {revenueData.length === 0 && activityData.length === 0 && recentActivity.length === 0 && (
        <div className="text-center py-12">
          <p className="text-muted-foreground mb-4">No data yet. Start tracking your time and creating projects!</p>
          <Button onClick={() => window.location.href = '/timesheets'}>
            Start Tracking
          </Button>
        </div>
      )}
    </div>
  );
}

src/app/(protected)/payments/page.tsx.content:
'use client';

import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import { Plus, Search, DollarSign, MoreHorizontal, Edit, Trash2, Eye, CreditCard } from 'lucide-react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Input } from '@/components/ui/input'
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog'
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from '@/components/ui/dropdown-menu'
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Label } from '@/components/ui/label'
import { Textarea } from '@/components/ui/textarea'
import { Badge } from '@/components/ui/badge'
import { toast } from 'sonner'
import { Tabs, TabsList, TabsTrigger } from '@/components/ui/tabs'

interface Payment {
  id: number
  invoiceId: number
  userId: string
  method: string
  amount: number
  currency: string
  transactionRef: string | null
  processedAt: string
  notes: string | null
  createdAt: string
  updatedAt: string
}

interface Invoice {
  id: number
  total: number
  status: string
}

const PAYMENT_METHODS = ['card', 'interac', 'bank', 'cash', 'other']

export default function PaymentsPage() {
  const [payments, setPayments] = useState<Payment[]>([])
  const [invoices, setInvoices] = useState<Invoice[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState('')
  const [methodFilter, setMethodFilter] = useState<string>('all')
  const [isDialogOpen, setIsDialogOpen] = useState(false)
  const [newPayment, setNewPayment] = useState({
    invoiceId: '',
    method: 'card',
    amount: '',
    currency: 'USD',
    transactionRef: '',
    processedAt: new Date().toISOString().split('T')[0],
    notes: ''
  })

  const fetchPayments = async () => {
    try {
      const token = localStorage.getItem('bearer_token')
      if (!token) {
        throw new Error('Authentication required')
      }

      const response = await fetch('/api/lumenr/payments', {
        headers: { 'Authorization': `Bearer ${token}` }
      })

      if (!response.ok) {
        throw new Error('Failed to fetch payments')
      }

      const result = await response.json()
      const data = result.success ? result.data : result
      setPayments(Array.isArray(data) ? data : [])
    } catch (error: any) {
      toast.error(error.message || 'Failed to fetch payments')
      setPayments([])
    } finally {
      setLoading(false)
    }
  }

  const fetchInvoices = async () => {
    try {
      const token = localStorage.getItem('bearer_token')
      const response = await fetch('/api/lumenr/invoices', {
        headers: { 'Authorization': `Bearer ${token}` }
      })

      if (response.ok) {
        const result = await response.json()
        const data = result.success ? result.data : result
        setInvoices(Array.isArray(data) ? data : [])
      }
    } catch (error: any) {
      console.error('Error fetching invoices:', error)
      setInvoices([])
    }
  }

  const createPayment = async (e: React.FormEvent) => {
    e.preventDefault()

    try {
      const token = localStorage.getItem('bearer_token')
      const response = await fetch('/api/lumenr/payments', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          invoiceId: parseInt(newPayment.invoiceId),
          method: newPayment.method,
          amount: parseFloat(newPayment.amount),
          currency: newPayment.currency,
          transactionRef: newPayment.transactionRef || null,
          processedAt: new Date(newPayment.processedAt).toISOString(),
          notes: newPayment.notes || null
        })
      })

      if (!response.ok) {
        throw new Error('Failed to record payment')
      }

      toast.success('Payment recorded successfully')

      setNewPayment({
        invoiceId: '',
        method: 'card',
        amount: '',
        currency: 'USD',
        transactionRef: '',
        processedAt: new Date().toISOString().split('T')[0],
        notes: ''
      })
      setIsDialogOpen(false)
      fetchPayments()
      fetchInvoices()
    } catch (error: any) {
      toast.error(error.message)
    }
  }

  useEffect(() => {
    fetchPayments()
    fetchInvoices()
  }, [])

  const getMethodColor = (method: string) => {
    switch (method) {
      case 'card': return 'bg-blue-500'
      case 'interac': return 'bg-purple-500'
      case 'bank': return 'bg-green-500'
      case 'cash': return 'bg-yellow-500'
      case 'other': return 'bg-gray-500'
      default: return 'bg-gray-500'
    }
  }

  const filteredPayments = payments.filter(payment => {
    const matchesSearch = 
      payment.transactionRef?.toLowerCase().includes(searchTerm.toLowerCase()) ||
      payment.id.toString().includes(searchTerm) ||
      payment.invoiceId.toString().includes(searchTerm)

    const matchesMethod = methodFilter === 'all' || payment.method === methodFilter

    return matchesSearch && matchesMethod
  })

  const totalRevenue = filteredPayments.reduce((sum, payment) => sum + payment.amount, 0)

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
      </div>
    )
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex flex-col sm:flex-row gap-4 sm:items-center justify-between">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground">Payments</h1>
          <p className="text-muted-foreground text-sm sm:text-base">Track and record payment transactions</p>
        </div>

        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button className="bg-gradient-primary hover:opacity-90 w-full sm:w-auto">
              <Plus className="h-4 w-4 mr-2" />
              Record Payment
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Record New Payment</DialogTitle>
            </DialogHeader>
            <form onSubmit={createPayment} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="invoice">Invoice *</Label>
                <Select value={newPayment.invoiceId} onValueChange={(value) => {
                  const invoice = invoices.find(inv => inv.id === parseInt(value))
                  setNewPayment({ 
                    ...newPayment, 
                    invoiceId: value,
                    amount: invoice?.total.toString() || ''
                  })
                }}>
                  <SelectTrigger>
                    <SelectValue placeholder="Select an invoice" />
                  </SelectTrigger>
                  <SelectContent>
                    {invoices.filter(inv => inv.status !== 'paid' && inv.status !== 'cancelled').map(invoice => (
                      <SelectItem key={invoice.id} value={invoice.id.toString()}>
                        Invoice #{invoice.id} - ${Number(invoice.total || 0).toFixed(2)} ({invoice.status})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="amount">Amount *</Label>
                  <Input
                    id="amount"
                    type="number"
                    step="0.01"
                    min="0"
                    value={newPayment.amount}
                    onChange={(e) => setNewPayment({ ...newPayment, amount: e.target.value })}
                    required
                    placeholder="0.00"
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="method">Payment Method *</Label>
                  <Select value={newPayment.method} onValueChange={(value) => setNewPayment({ ...newPayment, method: value })}>
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {PAYMENT_METHODS.map(method => (
                        <SelectItem key={method} value={method}>
                          {method.charAt(0).toUpperCase() + method.slice(1)}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="processedAt">Payment Date *</Label>
                  <Input
                    id="processedAt"
                    type="date"
                    value={newPayment.processedAt}
                    onChange={(e) => setNewPayment({ ...newPayment, processedAt: e.target.value })}
                    required
                  />
                </div>

                <div className="space-y-2">
                  <Label htmlFor="currency">Currency</Label>
                  <Input
                    id="currency"
                    value={newPayment.currency}
                    onChange={(e) => setNewPayment({ ...newPayment, currency: e.target.value })}
                    placeholder="USD"
                  />
                </div>
              </div>

              <div className="space-y-2">
                <Label htmlFor="transactionRef">Transaction Reference</Label>
                <Input
                  id="transactionRef"
                  value={newPayment.transactionRef}
                  onChange={(e) => setNewPayment({ ...newPayment, transactionRef: e.target.value })}
                  placeholder="e.g., TXN-123456"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="notes">Notes</Label>
                <Textarea
                  id="notes"
                  value={newPayment.notes}
                  onChange={(e) => setNewPayment({ ...newPayment, notes: e.target.value })}
                  rows={3}
                  placeholder="Add payment details..."
                />
              </div>

              <div className="flex justify-end space-x-2">
                <Button type="button" variant="outline" onClick={() => setIsDialogOpen(false)}>
                  Cancel
                </Button>
                <Button type="submit">Record Payment</Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="flex flex-col sm:flex-row items-start sm:items-center gap-4">
        <div className="relative flex-1 max-w-md">
          <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search payments..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>

        <Tabs value={methodFilter} onValueChange={setMethodFilter} className="w-full sm:w-auto">
          <TabsList>
            <TabsTrigger value="all">All</TabsTrigger>
            {PAYMENT_METHODS.map(method => (
              <TabsTrigger key={method} value={method}>
                {method.charAt(0).toUpperCase() + method.slice(1)}
              </TabsTrigger>
            ))}
          </TabsList>
        </Tabs>
      </div>

      <div className="bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-950 dark:to-blue-950 p-4 rounded-lg border">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm text-muted-foreground">Total Revenue {methodFilter !== 'all' && `(${methodFilter})`}</p>
            <p className="text-2xl font-bold">${totalRevenue.toFixed(2)}</p>
          </div>
          <div className="text-sm text-muted-foreground">
            {filteredPayments.length} payment{filteredPayments.length !== 1 ? 's' : ''}
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {filteredPayments.map((payment) => (
          <motion.div
            key={payment.id}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            whileHover={{ scale: 1.02 }}
            className="h-full"
          >
            <Card className="h-full hover:shadow-lg transition-all duration-300">
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <div className="flex items-center space-x-2 flex-1 min-w-0">
                  <CreditCard className="h-5 w-5 text-primary flex-shrink-0" />
                  <CardTitle className="text-lg font-semibold truncate">
                    Payment #{payment.id}
                  </CardTitle>
                </div>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center justify-between">
                  <Badge className={`${getMethodColor(payment.method)} text-white`}>
                    {payment.method}
                  </Badge>
                  <span className="text-lg font-bold flex items-center gap-1">
                    <DollarSign className="h-4 w-4" />
                    {Number(payment.amount || 0).toFixed(2)}
                  </span>
                </div>

                <div className="text-sm">
                  <span className="text-muted-foreground">Invoice: </span>
                  <span className="font-medium">#{payment.invoiceId}</span>
                </div>

                {payment.transactionRef && (
                  <div className="text-sm text-muted-foreground font-mono truncate">
                    Ref: {payment.transactionRef}
                  </div>
                )}

                <div className="flex justify-between items-center pt-4">
                  <div className="text-xs text-muted-foreground">
                    {new Date(payment.processedAt).toLocaleDateString()}
                  </div>
                </div>
              </CardContent>
            </Card>
          </motion.div>
        ))}
      </div>

      {filteredPayments.length === 0 && (
        <div className="text-center py-12">
          <CreditCard className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold mb-2">No payments found</h3>
          <p className="text-muted-foreground mb-4">
            {searchTerm ? 'No payments match your search.' : 'Record your first payment to get started.'}
          </p>
          {!searchTerm && (
            <Button onClick={() => setIsDialogOpen(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Record Payment
            </Button>
          )}
        </div>
      )}
    </div>
  )
}

src/components/Mode/ProfileSetupDialog.t.content:
import { useState } from 'react';
import { useAuth } from '@/contexts/AuthContext';
import { supabase } from '@/integrations/supabase/client';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
} from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { toast } from 'sonner';

interface ProfileSetupDialogProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  mode: 'student' | 'work';
  onComplete: () => void;
}

export function ProfileSetupDialog({
  open,
  onOpenChange,
  mode,
  onComplete,
}: ProfileSetupDialogProps) {
  const { user } = useAuth();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    // Student fields
    school_name: '',
    major: '',
    year: '',
    // Work fields
    company_name: '',
    job_title: '',
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!user) return;

    setLoading(true);

    try {
      if (mode === 'student') {
        const { error } = await supabase.from('student_profiles' as any).insert({
          user_id: user.id,
          school_name: formData.school_name,
          major: formData.major,
          year: formData.year,
        } as any);

        if (error) throw error;
        toast.success('Student profile created!');
      } else {
        const { error } = await supabase.from('work_profiles' as any).insert({
          user_id: user.id,
          company_name: formData.company_name,
          job_title: formData.job_title,
        } as any);

        if (error) throw error;
        toast.success('Work profile created!');
      }

      onComplete();
      onOpenChange(false);
    } catch (error) {
      console.error('Error creating profile:', error);
      toast.error('Failed to create profile');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent>
        <DialogHeader>
          <DialogTitle>
            Set up your {mode === 'student' ? 'Student' : 'Work'} Profile
          </DialogTitle>
          <DialogDescription>
            Complete your profile to start using {mode} mode
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4">
          {mode === 'student' ? (
            <>
              <div className="space-y-2">
                <Label htmlFor="school_name">School Name</Label>
                <Input
                  id="school_name"
                  value={formData.school_name}
                  onChange={(e) =>
                    setFormData({ ...formData, school_name: e.target.value })
                  }
                  placeholder="e.g., University of California"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="major">Major</Label>
                <Input
                  id="major"
                  value={formData.major}
                  onChange={(e) =>
                    setFormData({ ...formData, major: e.target.value })
                  }
                  placeholder="e.g., Computer Science"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="year">Year</Label>
                <Input
                  id="year"
                  value={formData.year}
                  onChange={(e) =>
                    setFormData({ ...formData, year: e.target.value })
                  }
                  placeholder="e.g., Junior"
                />
              </div>
            </>
          ) : (
            <>
              <div className="space-y-2">
                <Label htmlFor="company_name">Company Name</Label>
                <Input
                  id="company_name"
                  value={formData.company_name}
                  onChange={(e) =>
                    setFormData({ ...formData, company_name: e.target.value })
                  }
                  placeholder="e.g., Tech Corp"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="job_title">Job Title</Label>
                <Input
                  id="job_title"
                  value={formData.job_title}
                  onChange={(e) =>
                    setFormData({ ...formData, job_title: e.target.value })
                  }
                  placeholder="e.g., Software Engineer"
                />
              </div>
            </>
          )}

          <div className="flex gap-2 justify-end">
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={loading}>
              {loading ? 'Creating...' : 'Create Profile'}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

SUPABASE_FEATURES_GUIDE.md.content:
# ðŸš€ Supabase Features Integration Guide

Your LumenR application now has full Supabase integration with Auth, Storage, and Realtime capabilities!

## âœ… What's Configured

### Environment Variables
All required Supabase credentials are configured in your `.env` file:
- âœ… `NEXT_PUBLIC_SUPABASE_URL` - Your Supabase project URL
- âœ… `NEXT_PUBLIC_SUPABASE_ANON_KEY` - Client-side anon key
- âœ… `SUPABASE_SERVICE_ROLE_KEY` - Server-side admin key

### Packages Installed
- âœ… `@supabase/supabase-js` - Main Supabase client library
- âœ… `@supabase/ssr` - Server-side rendering support for Next.js

---

## ðŸ“¦ Supabase Storage

Upload, download, and manage files in Supabase Storage buckets.

### Setup Storage Buckets

Before using storage, create buckets in your Supabase Dashboard:

1. Go to **Storage** in your Supabase Dashboard
2. Click **New Bucket**
3. Enter bucket name (e.g., `documents`, `avatars`, `receipts`)
4. Configure privacy settings:
   - **Public**: Anyone can access files (good for images, public assets)
   - **Private**: Only authenticated users can access (good for documents)

### Usage Examples

#### Basic Upload
```typescript
import { uploadFile, getPublicUrl } from '@/lib/supabase/storage'

// Upload a file
const file = event.target.files[0]
const fileName = `${Date.now()}-${file.name}`
const data = await uploadFile('documents', fileName, file)
const url = getPublicUrl('documents', data.path)
```

#### Using the Hook
```typescript
'use client'
import { useSupabaseStorage } from '@/hooks/useSupabaseStorage'

export function FileUploader() {
  const { upload, uploading, error } = useSupabaseStorage('documents')

  const handleUpload = async (file: File) => {
    const { data, publicUrl } = await upload(`uploads/${file.name}`, file, {
      upsert: false,
      cacheControl: '3600'
    })
    console.log('Uploaded:', publicUrl)
  }

  return (
    <input type="file" onChange={(e) => handleUpload(e.target.files[0])} />
  )
}
```

#### Advanced Storage Operations
```typescript
import { 
  downloadFile, 
  deleteFile, 
  listFiles,
  createSignedUrl,
  moveFile,
  copyFile 
} from '@/lib/supabase/storage'

// Download a file
const blob = await downloadFile('documents', 'file.pdf')

// Delete files
await deleteFile('documents', 'file.pdf') // Single file
await deleteFile('documents', ['file1.pdf', 'file2.pdf']) // Multiple files

// List files in a folder
const files = await listFiles('documents', 'uploads/', {
  limit: 50,
  sortBy: { column: 'created_at', order: 'desc' }
})

// Create signed URL for private access (expires in 1 hour)
const { signedUrl } = await createSignedUrl('documents', 'private.pdf', 3600)

// Move file
await moveFile('documents', 'old/file.pdf', 'new/file.pdf')

// Copy file
await copyFile('documents', 'original.pdf', 'copy.pdf')
```

### Storage Component Example

Check out `src/components/examples/SupabaseStorageExample.tsx` for a complete working example.

---

## âš¡ Supabase Realtime

Subscribe to database changes in real-time and build collaborative features.

### Enable Realtime on Tables

Before using realtime, enable it in Supabase Dashboard:

1. Go to **Database** â†’ **Replication**
2. Enable replication for tables you want to track
3. Or run this SQL for a specific table:
```sql
ALTER TABLE clients REPLICA IDENTITY FULL;
ALTER PUBLICATION supabase_realtime ADD TABLE clients;
```

### Usage Examples

#### Subscribe to All Changes
```typescript
import { subscribeToTable, unsubscribe } from '@/lib/supabase/realtime'

const channel = subscribeToTable('clients', '*', (payload) => {
  console.log('Change detected:', payload)
  console.log('Event type:', payload.eventType) // INSERT, UPDATE, DELETE
  console.log('New data:', payload.new)
  console.log('Old data:', payload.old)
})

// Cleanup
await unsubscribe(channel)
```

#### Using the Hook
```typescript
'use client'
import { useSupabaseRealtime } from '@/hooks/useSupabaseRealtime'

export function ClientList() {
  const [clients, setClients] = useState([])

  useSupabaseRealtime('clients', 'INSERT', (payload) => {
    setClients(prev => [...prev, payload.new])
  })

  return <div>{/* Render clients */}</div>
}
```

#### Subscribe to Specific Events
```typescript
import { subscribeToTableChanges } from '@/lib/supabase/realtime'

const channel = subscribeToTableChanges('invoices', {
  onInsert: (payload) => {
    console.log('New invoice:', payload.new)
  },
  onUpdate: (payload) => {
    console.log('Invoice updated:', payload.new)
  },
  onDelete: (payload) => {
    console.log('Invoice deleted:', payload.old)
  }
}, 'status=eq.pending') // Optional filter
```

#### Presence (Online Users)
```typescript
import { subscribeToPresence } from '@/lib/supabase/realtime'

const channel = subscribeToPresence('room-123', {
  id: 'user-1',
  name: 'John Doe',
  avatar: 'https://...'
}, {
  onSync: () => {
    console.log('Presence synced')
  },
  onJoin: (key, currentPresence, newPresence) => {
    console.log('User joined:', newPresence)
  },
  onLeave: (key, currentPresence, leftPresence) => {
    console.log('User left:', leftPresence)
  }
})
```

#### Broadcast Messages
```typescript
import { broadcast, subscribeToBroadcast } from '@/lib/supabase/realtime'

// Send a message
await broadcast('chat-room', 'new-message', {
  text: 'Hello!',
  userId: 'user-1'
})

// Listen for messages
const channel = subscribeToBroadcast('chat-room', 'new-message', (payload) => {
  console.log('Received:', payload)
})
```

### Realtime Component Example

Check out `src/components/examples/SupabaseRealtimeExample.tsx` for a complete working example.

---

## ðŸ” Supabase Auth (Optional)

Your app currently uses a custom auth system. If you want to migrate to Supabase Auth:

### Setup
```typescript
import { createClient } from '@/lib/supabase/client'

const supabase = createClient()

// Sign up
const { data, error } = await supabase.auth.signUp({
  email: 'user@example.com',
  password: 'password'
})

// Sign in
const { data, error } = await supabase.auth.signInWithPassword({
  email: 'user@example.com',
  password: 'password'
})

// Sign out
await supabase.auth.signOut()

// Get current user
const { data: { user } } = await supabase.auth.getUser()
```

---

## ðŸŽ¯ Quick Start Examples

### Example 1: Receipt Upload with Storage
```typescript
'use client'
import { useState } from 'react'
import { useSupabaseStorage } from '@/hooks/useSupabaseStorage'

export function ReceiptUploader() {
  const { upload, uploading } = useSupabaseStorage('receipts')
  const [receipts, setReceipts] = useState([])

  const handleUpload = async (file: File) => {
    const fileName = `receipts/${Date.now()}-${file.name}`
    const { publicUrl } = await upload(fileName, file)

    // Save to database
    await fetch('/api/lumenr/receipts', {
      method: 'POST',
      body: JSON.stringify({
        fileName: file.name,
        fileUrl: publicUrl,
        uploadedAt: new Date().toISOString()
      })
    })

    setReceipts(prev => [...prev, { url: publicUrl }])
  }

  return (
    <div>
      <input 
        type="file" 
        onChange={(e) => handleUpload(e.target.files[0])}
        disabled={uploading}
      />
      {receipts.map((receipt, i) => (
        <img key={i} src={receipt.url} alt="Receipt" />
      ))}
    </div>
  )
}
```

### Example 2: Real-time Client Updates
```typescript
'use client'
import { useState, useEffect } from 'react'
import { useSupabaseTableChanges } from '@/hooks/useSupabaseRealtime'

export function ClientDashboard() {
  const [clients, setClients] = useState([])

  // Fetch initial data
  useEffect(() => {
    fetch('/api/lumenr/clients')
      .then(res => res.json())
      .then(data => setClients(data))
  }, [])

  // Listen to real-time updates
  useSupabaseTableChanges('clients', {
    onInsert: (payload) => {
      setClients(prev => [...prev, payload.new])
    },
    onUpdate: (payload) => {
      setClients(prev => prev.map(c => 
        c.id === payload.new.id ? payload.new : c
      ))
    },
    onDelete: (payload) => {
      setClients(prev => prev.filter(c => c.id !== payload.old.id))
    }
  })

  return (
    <div>
      {clients.map(client => (
        <div key={client.id}>{client.name}</div>
      ))}
    </div>
  )
}
```

---

## ðŸ“š Available Files

### Core Libraries
- `src/lib/supabase/client.ts` - Browser client creation
- `src/lib/supabase/server.ts` - Server client creation (with service role)
- `src/lib/supabase/storage.ts` - Storage utilities
- `src/lib/supabase/realtime.ts` - Realtime utilities

### React Hooks
- `src/hooks/useSupabaseStorage.ts` - Storage hook with loading states
- `src/hooks/useSupabaseRealtime.ts` - Realtime subscription hooks

### Examples
- `src/components/examples/SupabaseStorageExample.tsx` - Storage demo
- `src/components/examples/SupabaseRealtimeExample.tsx` - Realtime demo
- `src/app/supabase-features/page.tsx` - Demo page

---

## ðŸŽ¨ Live Demo

Visit `/supabase-features` in your app to see working examples of Storage and Realtime features.

---

## ðŸ“– Next Steps

1. **Create Storage Buckets** in Supabase Dashboard
2. **Enable Realtime** on tables you want to track
3. **Integrate into existing features**:
   - Add file uploads to receipts page
   - Add realtime updates to client dashboard
   - Add collaborative features with presence

## ðŸ”— Resources

- [Supabase Storage Docs](https://supabase.com/docs/guides/storage)
- [Supabase Realtime Docs](https://supabase.com/docs/guides/realtime)
- [Supabase Auth Docs](https://supabase.com/docs/guides/auth)

---

**Your Supabase integration is complete and ready to use!** ðŸŽ‰

