@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground font-sans;
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-feature-settings: 'cv02', 'cv03', 'cv04', 'cv11';
    line-height: 1.5;
  }

  h1 {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-weight: 800;
    letter-spacing: -0.04em;
    line-height: 1.1;
  }

  h2, h3 {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-weight: 700;
    letter-spacing: -0.03em;
    line-height: 1.2;
  }

  h4, h5, h6 {
    font-family: 'Inter', system-ui, -apple-system, sans-serif;
    font-weight: 600;
    letter-spacing: -0.02em;
    line-height: 1.3;
  }

  .text-gradient {
    @apply text-primary;
  }

  .text-gradient-secondary {
    @apply text-primary;
  }
}

@layer components {
  .card-hover {
    @apply transition-all duration-300 hover:shadow-premium hover:-translate-y-1;
  }

  .card-interactive {
    @apply cursor-pointer transition-all duration-200 hover:shadow-lg hover:-translate-y-0.5 hover:scale-[1.01];
  }

  .card-modern {
    @apply bg-card border-border/50 rounded-2xl p-6 shadow-lg backdrop-blur-sm;
  }

  .card-stat {
    @apply bg-card border-border/50 rounded-xl p-5 shadow-md hover:shadow-premium transition-all duration-300;
  }

  .button-premium {
    @apply bg-primary text-primary-foreground hover:shadow-premium transition-all duration-300 hover:scale-105 active:scale-95 rounded-xl;
  }

  .button-sleek {
    @apply bg-secondary text-secondary-foreground hover:shadow-glow transition-all duration-300 hover:scale-105 active:scale-95 rounded-xl;
  }

  .sidebar-item {
    @apply transition-all duration-200 hover:bg-accent/70 hover:translate-x-1 hover:scale-105;
  }

  .glass-effect {
    @apply bg-card/30 backdrop-blur-2xl border border-border/30 shadow-lg;
  }

  .liquid-card {
    @apply relative overflow-hidden rounded-3xl p-6;
    transition: transform 0.3s cubic-bezier(0.4, 0, 0.2, 1),
                box-shadow 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  }

  .liquid-card::before {
    content: '';
    @apply absolute inset-0 opacity-0 transition-opacity duration-500;
    background: radial-gradient(
      600px circle at var(--mouse-x, 50%) var(--mouse-y, 50%),
      rgba(255, 255, 255, 0.1),
      transparent 40%
    );
  }

  .liquid-card:hover::before {
    opacity: 1;
  }

  .liquid-card:hover {
    transform: translateY(-4px) scale(1.01);
  }

  .gradient-mesh {
    background: transparent;
  }

  .input-sleek {
    @apply border-2 border-transparent bg-muted/50 focus:border-primary focus:bg-background transition-all duration-200 rounded-xl;
  }

  .pulse-glow {
    @apply animate-glow-pulse bg-primary shadow-glow;
  }

  .hover-lift {
    @apply transition-transform duration-200 hover:-translate-y-1;
  }

  .text-balance {
    text-wrap: balance;
  }

  .stat-card-icon {
    @apply w-12 h-12 rounded-xl flex items-center justify-center bg-primary/10 text-primary;
  }

  /* Dense layout classes */
  .compact-spacing {
    @apply space-y-3;
  }

  .dense-grid {
    @apply gap-4;
  }

  /* Mobile optimizations */
  @media (max-width: 768px) {
    .mobile-dense {
      @apply py-3 px-4 space-y-2;
    }

    .mobile-compact {
      @apply text-sm leading-tight;
    }
  }
}

/* Hide Next.js error overlay portal */
nextjs-portal {
  display: none !important;
}

/* Performance-optimized animations */
@layer utilities {
  @keyframes fade-in {
    from {
      opacity: 0;
      transform: translateY(8px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .animate-fade-in {
    animation: fade-in 0.15s ease-out;
    will-change: transform, opacity;
  }

  @media (prefers-reduced-motion: reduce) {
    .animate-fade-in {
      animation: none;
    }
  }
}
/* Additional Performance Optimizations */
* {
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
}

html {
  text-rendering: optimizeLegibility;
}

/* Instant button/link feedback */
button, a, [role="button"] {
  cursor: pointer;
  user-select: none;
  -webkit-tap-highlight-color: transparent;
  touch-action: manipulation;
}

button:not(:disabled):active, 
a:active, 
[role="button"]:active {
  transform: scale(0.98);
  transition: transform 0.05s ease-out;
}

/* Optimize card hover animations */
.card, [class*="card-"] {
  transition: transform 0.15s ease-out, box-shadow 0.15s ease-out;
}

/* Reduce motion for accessibility */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Grid background pattern for landing page */
.bg-grid-pattern {
  background-image: 
    linear-gradient(to right, rgba(0, 0, 0, 0.05) 1px, transparent 1px),
    linear-gradient(to bottom, rgba(0, 0, 0, 0.05) 1px, transparent 1px);
  background-size: 50px 50px;
}

public/sw.js.content:
// LumenR Service Worker for PWA Support
const CACHE_NAME = 'lumenr-v1';
const OFFLINE_URL = '/offline.html';

const CACHE_URLS = [
  '/',
  '/offline.html',
  '/manifest.json',
  '/icon-192x192.png',
  '/icon-512x512.png'
];

// Install event - cache essential resources
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME).then((cache) => {
      console.log('[SW] Caching app shell');
      return cache.addAll(CACHE_URLS.map(url => new Request(url, { cache: 'reload' })));
    })
  );
  self.skipWaiting();
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys().then((cacheNames) => {
      return Promise.all(
        cacheNames.map((cacheName) => {
          if (cacheName !== CACHE_NAME) {
            console.log('[SW] Deleting old cache:', cacheName);
            return caches.delete(cacheName);
          }
        })
      );
    })
  );
  self.clients.claim();
});

// Fetch event - network first, fallback to cache
self.addEventListener('fetch', (event) => {
  // Skip non-GET requests
  if (event.request.method !== 'GET') return;

  // Skip chrome extensions and browser requests
  if (event.request.url.startsWith('chrome-extension://') || 
      event.request.url.includes('chrome://')) {
    return;
  }

  event.respondWith(
    fetch(event.request)
      .then((response) => {
        // Clone the response before caching
        const responseToCache = response.clone();

        // Cache successful responses
        if (response.status === 200) {
          caches.open(CACHE_NAME).then((cache) => {
            cache.put(event.request, responseToCache);
          });
        }

        return response;
      })
      .catch(() => {
        // Network failed, try cache
        return caches.match(event.request).then((response) => {
          if (response) {
            return response;
          }

          // Return offline page for navigation requests
          if (event.request.mode === 'navigate') {
            return caches.match(OFFLINE_URL);
          }

          // Return a generic offline response for other requests
          return new Response('Offline', {
            status: 503,
            statusText: 'Service Unavailable',
            headers: new Headers({
              'Content-Type': 'text/plain'
            })
          });
        });
      })
  );
});

// Background sync for offline task creation
self.addEventListener('sync', (event) => {
  if (event.tag === 'sync-tasks') {
    event.waitUntil(syncTasks());
  }
});

async function syncTasks() {
  // Get pending tasks from IndexedDB
  const db = await openDB();
  const tx = db.transaction('pending-tasks', 'readonly');
  const store = tx.objectStore('pending-tasks');
  const tasks = await store.getAll();

  // Sync each task
  for (const task of tasks) {
    try {
      const response = await fetch('/api/tasks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(task)
      });

      if (response.ok) {
        // Remove from pending
        const deleteTx = db.transaction('pending-tasks', 'readwrite');
        await deleteTx.objectStore('pending-tasks').delete(task.id);
      }
    } catch (error) {
      console.error('[SW] Failed to sync task:', error);
    }
  }
}

function openDB() {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open('lumenr-offline', 1);

    request.onerror = () => reject(request.error);
    request.onsuccess = () => resolve(request.result);

    request.onupgradeneeded = (event) => {
      const db = event.target.result;
      if (!db.objectStoreNames.contains('pending-tasks')) {
        db.createObjectStore('pending-tasks', { keyPath: 'id' });
      }
    };
  });
}

// Push notifications support
self.addEventListener('push', (event) => {
  const options = {
    body: event.data ? event.data.text() : 'New notification from LumenR',
    icon: '/icon-192x192.png',
    badge: '/icon-192x192.png',
    vibrate: [200, 100, 200],
    data: {
      dateOfArrival: Date.now(),
      primaryKey: 1
    }
  };

  event.waitUntil(
    self.registration.showNotification('LumenR', options)
  );
});

// Notification click handler
self.addEventListener('notificationclick', (event) => {
  event.notification.close();
  event.waitUntil(
    clients.openWindow('/')
  );
});

