'use client';

import * as Sentry from '@sentry/react';
import { useEffect } from 'react';
import { usePathname } from 'next/navigation';
import { AlertCircle, RefreshCw } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';

function FallbackComponent({ error, resetError }: { error: Error; resetError: () => void }) {
  const isDevelopment = process.env.NODE_ENV === 'development';

  return (
    <div className="min-h-screen flex items-center justify-center p-4 bg-background">
      <Card className="max-w-md w-full">
        <CardHeader>
          <div className="flex items-center gap-2 text-destructive mb-2">
            <AlertCircle className="h-6 w-6" />
            <CardTitle>Something went wrong</CardTitle>
          </div>
          <CardDescription>
            We're sorry, but an unexpected error occurred. Our team has been notified.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          {isDevelopment && (
            <div className="p-3 bg-muted rounded-md">
              <p className="text-xs font-mono text-muted-foreground break-all">
                {error.message}
              </p>
            </div>
          )}
          <div className="flex gap-2">
            <Button onClick={resetError} className="flex-1">
              <RefreshCw className="h-4 w-4 mr-2" />
              Try Again
            </Button>
            <Button variant="outline" onClick={() => window.location.href = '/'} className="flex-1">
              Go Home
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

export const ErrorBoundary = Sentry.withErrorBoundary(
  ({ children }: { children: React.ReactNode }) => {
    const pathname = usePathname();

    useEffect(() => {
      // Track page views
      Sentry.addBreadcrumb({
        category: 'navigation',
        message: `Navigated to ${pathname}`,
        level: 'info',
      });
    }, [pathname]);

    return <>{children}</>;
  },
  {
    fallback: FallbackComponent,
    showDialog: false,
  }
);

src/contexts/ModeContext.tsx.content:
import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useModeSettings } from '@/hooks/useModeSettings';
import { ProfileSetupDialog } from '@/components/Mode/ProfileSetupDialog';
import { useAuth } from '@/contexts/AuthContext';
import { supabase } from '@/integrations/supabase/client';

type AppMode = 'student' | 'work';

interface ModeContextType {
  mode: AppMode;
  toggleMode: () => void;
  setMode: (mode: AppMode) => void;
  loading: boolean;
}

const ModeContext = createContext<ModeContextType | undefined>(undefined);

export function ModeProvider({ children }: { children: ReactNode }) {
  const { user } = useAuth();
  const { settings, loading, updateSettings } = useModeSettings();
  const [showProfileSetup, setShowProfileSetup] = useState(false);
  const [pendingMode, setPendingMode] = useState<AppMode | null>(null);

  const checkProfileExists = async (mode: AppMode): Promise<boolean> => {
    if (!user) return false;

    const table = mode === 'student' ? 'student_profiles' : 'work_profiles';
    const { data, error } = await supabase
      .from(table as any)
      .select('id')
      .eq('user_id', user.id)
      .single();

    return !error && !!data;
  };

  const setMode = async (newMode: AppMode) => {
    if (!user || loading) return;

    const hasProfile = await checkProfileExists(newMode);

    if (!hasProfile) {
      setPendingMode(newMode);
      setShowProfileSetup(true);
      return;
    }

    await updateSettings({
      active_mode: newMode,
      [`${newMode}_mode_enabled`]: true,
    });
  };

  const toggleMode = async () => {
    const newMode = settings.active_mode === 'work' ? 'student' : 'work';
    await setMode(newMode);
  };

  const handleProfileSetupComplete = async () => {
    if (pendingMode) {
      await updateSettings({
        active_mode: pendingMode,
        [`${pendingMode}_mode_enabled`]: true,
      });
      setPendingMode(null);
    }
  };

  return (
    <ModeContext.Provider
      value={{
        mode: settings.active_mode,
        toggleMode,
        setMode,
        loading,
      }}
    >
      {children}
      {pendingMode && (
        <ProfileSetupDialog
          open={showProfileSetup}
          onOpenChange={setShowProfileSetup}
          mode={pendingMode}
          onComplete={handleProfileSetupComplete}
        />
      )}
    </ModeContext.Provider>
  );
}

export function useMode() {
  const context = useContext(ModeContext);
  if (context === undefined) {
    throw new Error('useMode must be used within a ModeProvider');
  }
  return context;
}

