'use client';

import { useState, useEffect, useMemo } from 'react';
import { motion } from 'framer-motion';
import { 
  Calendar as CalendarIcon, ChevronLeft, ChevronRight, Clock,
  Plus, Download, Settings, X, CheckCircle2, Users, MapPin, Loader2
} from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Tabs, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { useBookings, type Booking } from '@/lib/hooks/useBookings';
import { useToast } from '@/hooks/use-toast';

interface CalendarEvent {
  id: string;
  bookingId: number;
  title: string;
  description?: string;
  startTime: Date;
  endTime: Date;
  status: 'scheduled' | 'completed' | 'cancelled' | 'rescheduled';
  clientName?: string;
  clientEmail?: string;
  color: string;
}

interface Client {
  id: number;
  name: string;
  email: string;
}

const STATUS_COLORS: Record<string, string> = {
  scheduled: '#3b82f6',
  completed: '#10b981',
  cancelled: '#ef4444',
  rescheduled: '#f59e0b',
};

export function CalendarIntegration() {
  const [currentDate, setCurrentDate] = useState(new Date());
  const [viewMode, setViewMode] = useState<'day' | 'week' | 'month'>('week');
  const [showEventDialog, setShowEventDialog] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState<CalendarEvent | null>(null);
  const [clients, setClients] = useState<Client[]>([]);
  const [isLoadingClients, setIsLoadingClients] = useState(false);
  const [showImportDialog, setShowImportDialog] = useState(false);
  const [googleEvents, setGoogleEvents] = useState<any[]>([]);
  const [isLoadingGoogleEvents, setIsLoadingGoogleEvents] = useState(false);
  const { toast} = useToast();

  const [formData, setFormData] = useState({
    clientId: '',
    title: '',
    date: '',
    time: '',
    duration: '60',
    status: 'scheduled' as 'scheduled' | 'completed' | 'cancelled' | 'rescheduled',
    notes: '',
  });

  const startOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth(), 1);
  const endOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);

  const startDate = startOfMonth.toISOString().split('T')[0];
  const endDate = endOfMonth.toISOString().split('T')[0];

  const { bookings, isLoading, createBooking, updateBooking, deleteBooking } = useBookings({
    startDate,
    endDate,
  });

  useEffect(() => {
    fetchClients();
  }, []);

  const fetchClients = async () => {
    try {
      setIsLoadingClients(true);
      const response = await fetch('/api/lumenr/clients');
      if (response.ok) {
        const data = await response.json();
        setClients(data.data || []);
      }
    } catch (error) {
      console.error('Error fetching clients:', error);
    } finally {
      setIsLoadingClients(false);
    }
  };

  const events: CalendarEvent[] = useMemo(() => {
    return bookings.map((booking) => {
      const [hours, minutes] = booking.time.split(':').map(Number);
      const bookingDate = new Date(booking.date);
      const startTime = new Date(bookingDate);
      startTime.setHours(hours, minutes, 0, 0);

      const endTime = new Date(startTime);
      endTime.setMinutes(endTime.getMinutes() + booking.duration);

      return {
        id: booking.id.toString(),
        bookingId: booking.id,
        title: booking.title,
        description: booking.notes,
        startTime,
        endTime,
        status: booking.status,
        clientName: booking.clientName,
        clientEmail: booking.clientEmail,
        color: STATUS_COLORS[booking.status] || STATUS_COLORS.scheduled,
      };
    });
  }, [bookings]);

  const todayEvents = useMemo(() => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    return events.filter(event => {
      const eventDate = new Date(event.startTime);
      eventDate.setHours(0, 0, 0, 0);
      return eventDate.getTime() === today.getTime();
    });
  }, [events]);

  const hours = Array.from({ length: 24 }, (_, i) => i);

  const getEventStyle = (event: CalendarEvent) => {
    const start = event.startTime.getHours() * 60 + event.startTime.getMinutes();
    const end = event.endTime.getHours() * 60 + event.endTime.getMinutes();
    const duration = end - start;

    return {
      top: `${(start / 1440) * 100}%`,
      height: `${(duration / 1440) * 100}%`,
      backgroundColor: event.color,
    };
  };

  const navigateMonth = (direction: 'prev' | 'next') => {
    const newDate = new Date(currentDate);
    if (direction === 'prev') {
      newDate.setMonth(newDate.getMonth() - 1);
    } else {
      newDate.setMonth(newDate.getMonth() + 1);
    }
    setCurrentDate(newDate);
  };

  const handleEventClick = (event: CalendarEvent) => {
    setSelectedEvent(event);
  };

  const handleCreateClick = () => {
    const today = new Date();
    setFormData({
      clientId: '',
      title: '',
      date: today.toISOString().split('T')[0],
      time: '09:00',
      duration: '60',
      status: 'scheduled',
      notes: '',
    });
    setSelectedEvent(null);
    setShowEventDialog(true);
  };

  const handleEditClick = (event: CalendarEvent) => {
    const booking = bookings.find(b => b.id === event.bookingId);
    if (booking) {
      setFormData({
        clientId: booking.clientId.toString(),
        title: booking.title,
        date: booking.date,
        time: booking.time,
        duration: booking.duration.toString(),
        status: booking.status,
        notes: booking.notes || '',
      });
      setSelectedEvent(event);
      setShowEventDialog(true);
    }
  };

  const handleSubmit = async () => {
    try {
      if (!formData.clientId || !formData.title || !formData.date || !formData.time) {
        toast({
          title: 'Validation Error',
          description: 'Please fill in all required fields',
          variant: 'destructive',
        });
        return;
      }

      const bookingData = {
        clientId: parseInt(formData.clientId),
        title: formData.title,
        date: formData.date,
        time: formData.time,
        duration: parseInt(formData.duration),
        status: formData.status,
        notes: formData.notes,
      };

      if (selectedEvent) {
        await updateBooking(selectedEvent.bookingId, bookingData);
      } else {
        await createBooking(bookingData);
      }

      setShowEventDialog(false);
      setSelectedEvent(null);
    } catch (error) {
      console.error('Error saving booking:', error);
    }
  };

  const handleDelete = async () => {
    if (!selectedEvent) return;

    try {
      await deleteBooking(selectedEvent.bookingId);
      setShowEventDialog(false);
      setSelectedEvent(null);
    } catch (error) {
      console.error('Error deleting booking:', error);
    }
  };

  const exportCalendar = () => {
    toast({
      title: 'Export',
      description: 'Calendar export feature coming soon!',
    });
  };

  const handleSyncToGoogle = async () => {
    try {
      const token = localStorage.getItem('bearer_token');
      if (!token) {
        throw new Error('Authentication token not found. Please log in again.');
      }

      const today = new Date();
      const nextMonth = new Date();
      nextMonth.setMonth(nextMonth.getMonth() + 1);

      toast({
        title: 'Syncing to Google Calendar',
        description: 'This may take a moment...',
      });

      const response = await fetch('/api/lumenr/calendar/sync', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({
          action: 'sync-all',
          startDate: today.toISOString().split('T')[0],
          endDate: nextMonth.toISOString().split('T')[0],
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to sync to Google Calendar');
      }

      const result = await response.json();

      toast({
        title: 'Sync Complete',
        description: `Synced ${result.data.successful} bookings (${result.data.results.filter((r: any) => r.action === 'created').length} created, ${result.data.results.filter((r: any) => r.action === 'updated').length} updated)`,
      });
    } catch (error) {
      console.error('Error syncing to Google Calendar:', error);
      toast({
        title: 'Sync Failed',
        description: error instanceof Error ? error.message : 'Failed to sync to Google Calendar',
        variant: 'destructive',
      });
    }
  };

  const handleImportFromGoogle = async () => {
    try {
      const token = localStorage.getItem('bearer_token');
      if (!token) {
        throw new Error('Authentication token not found. Please log in again.');
      }

      setIsLoadingGoogleEvents(true);
      const today = new Date();
      const nextMonth = new Date();
      nextMonth.setMonth(nextMonth.getMonth() + 1);

      const response = await fetch(
        `/api/lumenr/calendar/sync?action=import&startDate=${today.toISOString().split('T')[0]}&endDate=${nextMonth.toISOString().split('T')[0]}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        }
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to import from Google Calendar');
      }

      const result = await response.json();
      setGoogleEvents(result.data.events || []);
      setShowImportDialog(true);
    } catch (error) {
      console.error('Error importing from Google Calendar:', error);
      toast({
        title: 'Import Failed',
        description: error instanceof Error ? error.message : 'Failed to import from Google Calendar',
        variant: 'destructive',
      });
    } finally {
      setIsLoadingGoogleEvents(false);
    }
  };

  return (
    <div className="space-y-4 sm:space-y-6 p-2 sm:p-4 md:p-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold mb-1 sm:mb-2">Calendar & Bookings</h1>
          <p className="text-sm text-muted-foreground">Manage your schedule and appointments</p>
        </div>

        <div className="flex items-center gap-2 flex-wrap">
          <Button variant="outline" size="sm" onClick={exportCalendar} className="flex-1 sm:flex-none">
            <Download className="h-4 w-4 sm:mr-2" />
            <span className="hidden sm:inline">Export</span>
          </Button>
          <Button variant="outline" size="sm" className="flex-1 sm:flex-none">
            <Settings className="h-4 w-4 sm:mr-2" />
            <span className="hidden sm:inline">Settings</span>
          </Button>
        </div>
      </div>

      {/* Calendar Controls */}
      <Card>
        <CardContent className="p-3 sm:p-4">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div className="flex items-center gap-2 sm:gap-4">
              <div className="flex items-center gap-2">
                <Button
                  variant="outline"
                  size="icon"
                  onClick={() => navigateMonth('prev')}
                  className="h-8 w-8 sm:h-10 sm:w-10"
                >
                  <ChevronLeft className="h-4 w-4" />
                </Button>
                <h2 className="text-base sm:text-xl font-semibold min-w-[140px] sm:min-w-[200px] text-center">
                  {currentDate.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}
                </h2>
                <Button
                  variant="outline"
                  size="icon"
                  onClick={() => navigateMonth('next')}
                  className="h-8 w-8 sm:h-10 sm:w-10"
                >
                  <ChevronRight className="h-4 w-4" />
                </Button>
              </div>

              <Button
                variant="outline"
                size="sm"
                onClick={() => setCurrentDate(new Date())}
              >
                Today
              </Button>
            </div>

            <div className="flex items-center gap-2 flex-wrap">
              <Tabs value={viewMode} onValueChange={(v) => setViewMode(v as any)} className="flex-1 sm:flex-none">
                <TabsList className="grid w-full grid-cols-3">
                  <TabsTrigger value="day" className="text-xs sm:text-sm">Day</TabsTrigger>
                  <TabsTrigger value="week" className="text-xs sm:text-sm">Week</TabsTrigger>
                  <TabsTrigger value="month" className="text-xs sm:text-sm">Month</TabsTrigger>
                </TabsList>
              </Tabs>

              <Button onClick={handleCreateClick} size="sm" className="flex-1 sm:flex-none">
                <Plus className="h-4 w-4 sm:mr-2" />
                <span className="hidden sm:inline">New Booking</span>
                <span className="sm:hidden">New</span>
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 lg:grid-cols-4 gap-4 sm:gap-6">
        {/* Calendar View */}
        <div className="lg:col-span-3">
          <Card className="h-[500px] sm:h-[600px] overflow-hidden">
            <CardContent className="p-0 h-full">
              {isLoading ? (
                <div className="h-full flex items-center justify-center">
                  <Loader2 className="h-8 w-8 animate-spin text-muted-foreground" />
                </div>
              ) : (
                <div className="h-full overflow-y-auto">
                  {/* Day/Week View */}
                  {(viewMode === 'day' || viewMode === 'week') && (
                    <>
                      {/* Time labels */}
                      <div className="flex border-b sticky top-0 bg-background z-10">
                        <div className="w-12 sm:w-16 flex-shrink-0 border-r p-2">
