/**
 * Standardized JSON response utilities for API routes
 * Ensures consistent response format across all endpoints
 */

export function jsonOk(data: any, status = 200) {
  return new Response(JSON.stringify({ success: true, data }), {
    status,
    headers: { 'Content-Type': 'application/json' },
  });
}

export function jsonError(error: string, status = 500) {
  return new Response(JSON.stringify({ success: false, error }), {
    status,
    headers: { 'Content-Type': 'application/json' },
  });
}

/**
 * Safe frontend fetch wrapper with JSON validation
 * Prevents "Unexpected end of JSON input" errors
 */
export async function safeFetch(url: string, options?: RequestInit) {
  const res = await fetch(url, options);
  const ct = res.headers.get('content-type') || '';

  if (!ct.includes('application/json')) {
    const body = await res.text();
    throw new Error(`Expected JSON, got ${ct}: ${body.slice(0, 180)}â€¦`);
  }

  const json = await res.json().catch(() => null);
  if (!res.ok) throw new Error(json?.error || `HTTP ${res.status}`);

  // Return data from wrapper if success is true, otherwise return json as-is
  return json?.success ? json.data : json;
}

export function createAuthHeaders(token?: string) {
  const headers = new Headers({ 'Content-Type': 'application/json' });
  if (token) headers.set('Authorization', `Bearer ${token}`);
  return headers;
}

src/db/index.ts.content:
import { drizzle } from 'drizzle-orm/postgres-js';
import postgres from 'postgres';
import * as schema from './schema';

// Get DATABASE_URL from environment
const databaseUrl = process.env.DATABASE_URL;

let db: ReturnType<typeof drizzle>;
let connectionError: string | null = null;

try {
  if (!databaseUrl) {
    connectionError = 'Missing required environment variable: DATABASE_URL';
    console.error('[Database Configuration Error]', connectionError);

    // Create a mock db object for when database is not configured
    db = {} as ReturnType<typeof drizzle>;
  } else {
    // Create PostgreSQL client for Supabase Transaction Pooler
    const client = postgres(databaseUrl, {
      max: 10,
      idle_timeout: 20,
      connect_timeout: 10,
      prepare: false, // Required for Supabase Transaction Pooler
    });

    // Initialize Drizzle with PostgreSQL
    db = drizzle(client, { schema });
    console.log('[Database] Successfully connected to Supabase PostgreSQL (Pooler)');
  }
} catch (error) {
  connectionError = `Failed to initialize database: ${error instanceof Error ? error.message : 'Unknown error'}`;
  console.error('[Database Connection Error]', connectionError);
  db = {} as ReturnType<typeof drizzle>;
}

// Helper function to check if database is properly configured
export function isDatabaseConfigured(): boolean {
  return connectionError === null;
}

// Helper function to get database error
export function getDatabaseError(): string | null {
  return connectionError;
}

export { db };

