import { ReactNode } from "react";
import { Card } from "@/components/ui/card";
import { LucideIcon } from "lucide-react";
import { cn } from "@/lib/utils";

interface StatCardProps {
  title: string;
  value: string | number;
  change?: string;
  changeType?: "positive" | "negative" | "neutral";
  icon?: LucideIcon;
  iconColor?: string;
  subtitle?: string;
  className?: string;
  children?: ReactNode;
}

export function StatCard({
  title,
  value,
  change,
  changeType = "neutral",
  icon: Icon,
  iconColor = "text-primary",
  subtitle,
  className,
  children,
}: StatCardProps) {
  const gradientClass = iconColor.includes('orange') 
    ? 'bg-gradient-orange' 
    : iconColor.includes('purple') 
    ? 'bg-gradient-purple' 
    : iconColor.includes('blue') 
    ? 'bg-gradient-blue' 
    : 'bg-gradient-primary';

  return (
    <Card className={cn(
      "liquid-card glass-effect border-border/30 backdrop-blur-xl",
      "hover:shadow-glow transition-all duration-500",
      className
    )}>
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <p className="text-sm text-muted-foreground/80 mb-2 font-medium">{title}</p>
          <h3 className="text-4xl font-bold tracking-tight bg-clip-text text-transparent bg-gradient-to-br from-foreground to-foreground/70">
            {value}
          </h3>
          {subtitle && (
            <p className="text-xs text-muted-foreground/70 mt-2">{subtitle}</p>
          )}
        </div>
        {Icon && (
          <div className={cn(
            "p-3 rounded-2xl backdrop-blur-sm",
            gradientClass
          )}>
            <Icon className="h-6 w-6 text-white drop-shadow-lg" />
          </div>
        )}
      </div>

      {change && (
        <div className="flex items-center gap-2 mt-4">
          <span
            className={cn(
              "text-sm font-semibold",
              changeType === "positive" && "text-green-400",
              changeType === "negative" && "text-red-400",
              changeType === "neutral" && "text-muted-foreground"
            )}
          >
            {change}
          </span>
          <span className="text-xs text-muted-foreground/60">vs last period</span>
        </div>
      )}

      {children && <div className="mt-4">{children}</div>}
    </Card>
  );
}

src/app/api/lumenr/quotes/[id]/convert-t.content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { quotes, invoices } from '@/db/schema';
import { eq, and } from 'drizzle-orm';
import { getAuthUser } from '@/lib/auth-api';
import { jsonOk, jsonError } from '@/lib/api-utils';

export async function POST(
  request: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const id = params.id;

    // Validate ID is a valid integer
    if (!id || isNaN(parseInt(id))) {
      return jsonError('Valid quote ID is required', 400);
    }

    // Fetch the quote by ID and verify it belongs to the user
    const quote = await db
      .select()
      .from(quotes)
      .where(and(eq(quotes.id, parseInt(id)), eq(quotes.userId, userId)))
      .limit(1);

    if (quote.length === 0) {
      return jsonError('Quote not found', 404);
    }

    const quoteData = quote[0];

    // Verify quote status is 'accepted'
    if (quoteData.status !== 'accepted') {
      return jsonError('Only accepted quotes can be converted to invoices', 400);
    }

    // Calculate due date (30 days from now)
    const dueDate = new Date();
    dueDate.setDate(dueDate.getDate() + 30);

    // Create new invoice with quote data
    const newInvoice = await db
      .insert(invoices)
      .values({
        quoteId: quoteData.id,
        clientId: quoteData.clientId,
        userId: quoteData.userId,
        items: quoteData.items,
        subtotal: quoteData.subtotal,
        tax: quoteData.tax,
        total: quoteData.total,
        status: 'unpaid',
        depositRequired: false,
        depositAmount: null,
        paidAt: null,
        pdfUrl: null,
        dueDate: dueDate.toISOString(),
        notes: quoteData.notes,
        createdAt: new Date().toISOString(),
        updatedAt: new Date().toISOString(),
      })
      .returning();

    return jsonOk(newInvoice[0], 201);
  } catch (error) {
    console.error('POST error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

src/contexts/AuthContext.tsx.content:
'use client';

import { createContext, useContext, useEffect, useState } from 'react'
import type { User } from '@supabase/supabase-js'
import { supabase } from '@/integrations/supabase/client'

interface AuthContextType {
  user: User | null
  userProfile: any | null
  loading: boolean
  signIn: (email: string, password: string, captchaToken?: string) => Promise<{ error?: any }>
  signUp: (email: string, password: string, firstName: string, lastName: string, businessName: string, captchaToken?: string) => Promise<{ error?: any }>
  signOut: () => Promise<void>
  updateUserStatus: (status: 'Available' | 'Away' | 'Busy') => Promise<void>
}

const AuthContext = createContext<AuthContextType | undefined>(undefined)

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null)
  const [userProfile, setUserProfile] = useState<any | null>(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    // Only run on client side
    if (typeof window === 'undefined') {
      return;
    }

    // Skip if no supabase client
    if (!supabase) {
      setLoading(false);
      return;
    }

    let mounted = true;

    const initAuth = async () => {
      try {
        const { data: { session } } = await supabase.auth.getSession();

        if (!mounted) return;

        setUser(session?.user ?? null)

        // Store bearer token in localStorage for API calls
        if (session?.access_token) {
          localStorage.setItem('bearer_token', session.access_token)
        } else {
          localStorage.removeItem('bearer_token')
        }

        if (session?.user) {
          await fetchUserProfile(session.user.id)
        }
      } catch (error) {
        console.error('[Auth] Init error:', error)
      } finally {
        if (mounted) {
          setLoading(false)
        }
      }
    }

    // Set up auth state listener
    const { data: { subscription } } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (!mounted) return;

      setUser(session?.user ?? null)

      // Update bearer token on auth state change
      if (session?.access_token) {
        localStorage.setItem('bearer_token', session.access_token)
      } else {
        localStorage.removeItem('bearer_token')
      }

      if (session?.user) {
        await fetchUserProfile(session.user.id)
      } else {
        setUserProfile(null)
      }
    })

    initAuth()

    return () => {
      mounted = false;
      subscription?.unsubscribe()
    }
  }, [])

  const fetchUserProfile = async (userId: string) => {
    if (!supabase) return;

    try {
      const { data: { user }, error } = await supabase.auth.getUser()

      if (error) {
        console.error('[Auth] Error fetching user profile:', error)
        setUserProfile(null)
      } else {
        setUserProfile({
          id: user?.id,
          email: user?.email,
          first_name: user?.user_metadata?.first_name || '',
          last_name: user?.user_metadata?.last_name || '',
          business_name: user?.user_metadata?.business_name || '',
          status: 'Available'
        })
      }
    } catch (error) {
      console.error('[Auth] Error fetching user profile:', error)
      setUserProfile(null)
    }
  }

  const signIn = async (email: string, password: string, captchaToken?: string) => {
    if (!supabase) return { error: { message: 'Auth not available' } };

    try {
      const { data, error } = await supabase.auth.signInWithPassword({ 
        email: email.trim().toLowerCase(), 
        password,
        options: { captchaToken }
      })

      return { error }
    } catch (error) {
      console.error('[Auth] Sign in exception:', error)
      return { error }
    }
  }

  const signUp = async (
    email: string, 
    password: string, 
    firstName: string, 
    lastName: string, 
    businessName: string, 
    captchaToken?: string
  ) => {
    if (!supabase) return { error: { message: 'Auth not available' } };

    try {
      const trimmedEmail = email.trim().toLowerCase()
      const redirectUrl = `${typeof window !== 'undefined' ? window.location.origin : ''}/`

      const { data: authData, error: authError } = await supabase.auth.signUp({ 
        email: trimmedEmail, 
        password,
        options: {
          emailRedirectTo: redirectUrl,
          captchaToken,
          data: {
            first_name: firstName.trim(),
            last_name: lastName.trim(),
            business_name: businessName.trim()
          }
        }
      })

      if (authError) return { error: authError }
      if (!authData.user) return { error: { message: 'No user created' } }

      if (typeof window !== 'undefined' && businessName.trim()) {
        localStorage.setItem('business_name', businessName.trim())
        localStorage.setItem('pending_business_name', businessName.trim())
      }

      return { error: null }
    } catch (error) {
      console.error('[Auth] Sign up exception:', error)
      return { error }
    }
  }

  const signOut = async () => {
    if (!supabase) return;

    try {
      await supabase.auth.signOut()
    } catch (error) {
      console.error('[Auth] Sign out failed:', error)
    }
  }

  const updateUserStatus = async (status: 'Available' | 'Away' | 'Busy') => {
    if (!user || !supabase) return

    try {
      setUserProfile((prev: any) => ({ ...prev, status }))
    } catch (error) {
      console.error('[Auth] Failed to update user status:', error)
    }
  }

  const value = {
    user,
    userProfile,
    loading,
    signIn,
    signUp,
    signOut,
    updateUserStatus,
  }

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>
}

export function useAuth() {
  const context = useContext(AuthContext)
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

src/app/api/core/invoices/route.ts.content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { invoices, clients } from '@/db/schema';
import { eq, and, desc } from 'drizzle-orm';
import { getAuthUser } from '@/lib/auth-api';
import { jsonOk, jsonError } from '@/lib/api-utils';
import { PerformanceMonitor } from '@/lib/performance';

const VALID_STATUSES = ['unpaid', 'partially_paid', 'paid', 'cancelled', 'overdue'];

export async function GET(request: NextRequest) {
  const perfMon = new PerformanceMonitor('GET /api/core/invoices');

  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      perfMon.end();
      return jsonError('Authentication required', 401);
    }

    perfMon.checkpoint('Auth completed');

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (id) {
      if (!id || isNaN(parseInt(id))) {
        perfMon.end();
        return jsonError('Valid ID is required', 400);
      }

      const invoice = await db
        .select()
        .from(invoices)
        .where(and(eq(invoices.id, parseInt(id)), eq(invoices.userId, userId)))
        .limit(1);

      if (invoice.length === 0) {
        perfMon.end();
        return jsonError('Invoice not found', 404);
      }

      perfMon.end();
      return jsonOk(invoice[0]);
    }

    const limit = Math.min(parseInt(searchParams.get('limit') ?? '100'), 100);
    const offset = parseInt(searchParams.get('offset') ?? '0');
    const status = searchParams.get('status');

    let query = db
      .select()
      .from(invoices)
      .where(eq(invoices.userId, userId))
      .orderBy(desc(invoices.createdAt))
      .limit(limit)
      .offset(offset);

    if (status) {
      if (!VALID_STATUSES.includes(status)) {
        perfMon.end();
        return jsonError('Invalid status value', 400);
      }

      query = db
        .select()
        .from(invoices)
        .where(and(eq(invoices.userId, userId), eq(invoices.status, status)))
        .orderBy(desc(invoices.createdAt))
        .limit(limit)
        .offset(offset);
    }

    const results = await query;

    perfMon.checkpoint('Query executed');

    const response = jsonOk(results);
    perfMon.end();
    return response;
  } catch (error) {
    perfMon.end();
    console.error('GET error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const body = await request.json();

    if (!body.clientId) {
      return jsonError('clientId is required', 400);
    }

    if (!body.items) {
      return jsonError('items is required', 400);
    }

    if (!Array.isArray(body.items)) {
      return jsonError('items must be a valid JSON array', 400);
    }

    if (body.subtotal === undefined || body.subtotal === null) {
      return jsonError('subtotal is required', 400);
    }

    if (body.tax === undefined || body.tax === null) {
      return jsonError('tax is required', 400);
    }

    if (body.total === undefined || body.total === null) {
      return jsonError('total is required', 400);
    }

    if (body.subtotal < 0) {
      return jsonError('subtotal must be a positive number', 400);
    }

    if (body.tax < 0) {
      return jsonError('tax must be a positive number', 400);
    }

    if (body.total < 0) {
      return jsonError('total must be a positive number', 400);
    }

    if (body.status && !VALID_STATUSES.includes(body.status)) {
      return jsonError('status must be one of: unpaid, partially_paid, paid, cancelled, overdue', 400);
    }

    if (body.depositRequired && !body.depositAmount) {
      return jsonError('depositAmount is required when depositRequired is true', 400);
    }

    if (body.depositAmount && body.depositAmount < 0) {
      return jsonError('depositAmount must be a positive number', 400);
    }

    const client = await db
      .select()
      .from(clients)
      .where(and(eq(clients.id, body.clientId), eq(clients.userId, userId)))
      .limit(1);

    if (client.length === 0) {
      return jsonError('Client not found or does not belong to user', 404);
    }

    const now = new Date().toISOString();

    const newInvoice = await db
      .insert(invoices)
      .values({
        quoteId: body.quoteId || null,
        clientId: body.clientId,
        userId: userId,
        items: body.items,
        subtotal: body.subtotal,
        tax: body.tax,
        total: body.total,
        depositRequired: body.depositRequired ?? false,
        depositAmount: body.depositAmount || null,
        status: body.status || 'unpaid',
        paidAt: body.paidAt || null,
        pdfUrl: body.pdfUrl || null,
        dueDate: body.dueDate || null,
        notes: body.notes || null,
        createdAt: now,
        updatedAt: now,
      })
      .returning();

    return jsonOk(newInvoice[0], 201);
  } catch (error) {
    console.error('POST error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function PUT(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id || isNaN(parseInt(id))) {
      return jsonError('Valid ID is required', 400);
    }

    const existing = await db
      .select()
      .from(invoices)
      .where(and(eq(invoices.id, parseInt(id)), eq(invoices.userId, userId)))
      .limit(1);

    if (existing.length === 0) {
      return jsonError('Invoice not found', 404);
    }

    const body = await request.json();

    if (body.items && !Array.isArray(body.items)) {
      return jsonError('items must be a valid JSON array', 400);
    }

    if (body.subtotal !== undefined && body.subtotal < 0) {
      return jsonError('subtotal must be a positive number', 400);
    }

    if (body.tax !== undefined && body.tax < 0) {
      return jsonError('tax must be a positive number', 400);
    }

    if (body.total !== undefined && body.total < 0) {
      return jsonError('total must be a positive number', 400);
    }

    if (body.status && !VALID_STATUSES.includes(body.status)) {
      return jsonError('status must be one of: unpaid, partially_paid, paid, cancelled, overdue', 400);
    }

    if (body.depositRequired && !body.depositAmount && !existing[0].depositAmount) {
      return jsonError('depositAmount is required when depositRequired is true', 400);
    }

    if (body.depositAmount !== undefined && body.depositAmount < 0) {
      return jsonError('depositAmount must be a positive number', 400);
    }

    if (body.clientId) {
      const client = await db
        .select()
        .from(clients)
        .where(and(eq(clients.id, body.clientId), eq(clients.userId, userId)))
        .limit(1);

      if (client.length === 0) {
        return jsonError('Client not found or does not belong to user', 404);
      }
    }

    const updateData: Record<string, any> = {
      updatedAt: new Date().toISOString(),
    };

    if (body.quoteId !== undefined) updateData.quoteId = body.quoteId;
    if (body.clientId !== undefined) updateData.clientId = body.clientId;
    if (body.items !== undefined) updateData.items = body.items;
    if (body.subtotal !== undefined) updateData.subtotal = body.subtotal;
    if (body.tax !== undefined) updateData.tax = body.tax;
    if (body.total !== undefined) updateData.total = body.total;
    if (body.depositRequired !== undefined) updateData.depositRequired = body.depositRequired;
    if (body.depositAmount !== undefined) updateData.depositAmount = body.depositAmount;
    if (body.status !== undefined) updateData.status = body.status;
    if (body.paidAt !== undefined) updateData.paidAt = body.paidAt;
    if (body.pdfUrl !== undefined) updateData.pdfUrl = body.pdfUrl;
    if (body.dueDate !== undefined) updateData.dueDate = body.dueDate;
    if (body.notes !== undefined) updateData.notes = body.notes;

    const updated = await db
      .update(invoices)
      .set(updateData)
      .where(and(eq(invoices.id, parseInt(id)), eq(invoices.userId, userId)))
      .returning();

    return jsonOk(updated[0]);
  } catch (error) {
    console.error('PUT error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function DELETE(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const id = searchParams.get('id');

    if (!id || isNaN(parseInt(id))) {
      return jsonError('Valid ID is required', 400);
    }

    const existing = await db
      .select()
      .from(invoices)
      .where(and(eq(invoices.id, parseInt(id)), eq(invoices.userId, userId)))
      .limit(1);

    if (existing.length === 0) {
      return jsonError('Invoice not found', 404);
    }

    const deleted = await db
      .delete(invoices)
      .where(and(eq(invoices.id, parseInt(id)), eq(invoices.userId, userId)))
      .returning();

    return jsonOk({
      message: 'Invoice deleted successfully',
      invoice: deleted[0],
    });
  } catch (error) {
    console.error('DELETE error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

