      })}
      <ToastViewport />
    </ToastProvider>
  );
}

src/hooks/useOnboarding.ts.content:
import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/contexts/AuthContext';

export function useOnboarding() {
  const { user } = useAuth();
  const [needsOnboarding, setNeedsOnboarding] = useState<boolean | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const checkOnboardingStatus = async () => {
      if (!user) {
        setNeedsOnboarding(false);
        setLoading(false);
        return;
      }

      // Check localStorage first for a simpler approach
      const localOnboardingComplete = localStorage.getItem('lumenr-onboarding-completed');

      if (localOnboardingComplete === 'true') {
        setNeedsOnboarding(false);
        setLoading(false);
        return;
      }

      try {
        const { data: settings, error } = await supabase
          .from('user_mode_settings' as any)
          .select('onboarding_completed')
          .eq('user_id', user.id)
          .maybeSingle();

        if (error) {
          console.error('Error checking onboarding status:', error);
          // If table doesn't exist (PGRST205), skip onboarding
          if (error.code === 'PGRST205') {
            setNeedsOnboarding(false);
            localStorage.setItem('lumenr-onboarding-completed', 'true');
          } else {
            setNeedsOnboarding(true);
          }
        } else {
          const isComplete = (settings as any)?.onboarding_completed || false;
          setNeedsOnboarding(!isComplete);
          if (isComplete) {
            localStorage.setItem('lumenr-onboarding-completed', 'true');
          }
        }
      } catch (error) {
        console.error('Error in onboarding check:', error);
        setNeedsOnboarding(true);
      } finally {
        setLoading(false);
      }
    };

    checkOnboardingStatus();
  }, [user]);

  return { needsOnboarding, loading };
}

export function useTrialStatus() {
  const { user } = useAuth();
  const [trialDaysRemaining, setTrialDaysRemaining] = useState<number | null>(null);
  const [isTrialActive, setIsTrialActive] = useState(false);
  const [planType, setPlanType] = useState<string>('trial');

  useEffect(() => {
    const checkTrialStatus = async () => {
      if (!user) return;

      try {
        const { data: settings } = await supabase
          .from('user_mode_settings' as any)
          .select('trial_end_date, plan_type')
          .eq('user_id', user.id)
          .maybeSingle();

        if (settings) {
          const settingsData = settings as any;
          setPlanType(settingsData.plan_type || 'trial');

          if (settingsData.trial_end_date && settingsData.plan_type === 'trial') {
            const endDate = new Date(settingsData.trial_end_date);
            const now = new Date();
            const daysLeft = Math.ceil((endDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

            setTrialDaysRemaining(Math.max(0, daysLeft));
            setIsTrialActive(daysLeft > 0);
          }
        }
      } catch (error) {
        console.error('Error checking trial status:', error);
      }
    };

    checkTrialStatus();
  }, [user]);

  return { trialDaysRemaining, isTrialActive, planType };
}

src/components/Mode/FocusMode.tsx.content:
import { useState, useEffect, useCallback } from 'react'
import { motion, AnimatePresence } from 'framer-motion'
import { Play, Pause, RotateCcw, X, Coffee, Target, CheckCircle2, Timer } from 'lucide-react'
import { Button } from '@/components/ui/button'
import { Card, CardContent } from '@/components/ui/card'
import { Progress } from '@/components/ui/progress'

interface FocusModeProps {
  tasks?: Array<{ id: string; title: string; completed: boolean }>
  onClose: () => void
  onTaskComplete?: (taskId: string) => void
}

type PomodoroPhase = 'focus' | 'short-break' | 'long-break'

const POMODORO_DURATIONS = {
  focus: 25 * 60, // 25 minutes
  'short-break': 5 * 60, // 5 minutes
  'long-break': 15 * 60 // 15 minutes
}

export function FocusMode({ tasks = [], onClose, onTaskComplete }: FocusModeProps) {
  const [phase, setPhase] = useState<PomodoroPhase>('focus')
  const [timeLeft, setTimeLeft] = useState(POMODORO_DURATIONS.focus)
  const [isRunning, setIsRunning] = useState(false)
  const [completedPomodoros, setCompletedPomodoros] = useState(0)
  const [selectedTaskId, setSelectedTaskId] = useState<string | null>(
    tasks.find(t => !t.completed)?.id || null
  )

  const totalDuration = POMODORO_DURATIONS[phase]
  const progress = ((totalDuration - timeLeft) / totalDuration) * 100

  // Timer logic
  useEffect(() => {
    if (!isRunning) return

    const interval = setInterval(() => {
      setTimeLeft(prev => {
        if (prev <= 1) {
          handlePhaseComplete()
          return 0
        }
        return prev - 1
      })
    }, 1000)

    return () => clearInterval(interval)
  }, [isRunning, phase])

  const handlePhaseComplete = useCallback(() => {
    setIsRunning(false)

    // Play notification sound (browser notification)
    if ('Notification' in window && Notification.permission === 'granted') {
      new Notification('Pomodoro Complete!', {
        body: phase === 'focus' 
          ? 'Time for a break!' 
          : 'Ready to focus again?',
        icon: '/favicon.ico'
      })
    }

    // Transition to next phase
    if (phase === 'focus') {
      setCompletedPomodoros(prev => prev + 1)
      const nextPhase = (completedPomodoros + 1) % 4 === 0 ? 'long-break' : 'short-break'
      setPhase(nextPhase)
      setTimeLeft(POMODORO_DURATIONS[nextPhase])
    } else {
      setPhase('focus')
      setTimeLeft(POMODORO_DURATIONS.focus)
    }
  }, [phase, completedPomodoros])

  const toggleTimer = () => {
    if (!isRunning && 'Notification' in window && Notification.permission === 'default') {
      Notification.requestPermission()
    }
    setIsRunning(!isRunning)
  }

  const resetTimer = () => {
    setIsRunning(false)
    setTimeLeft(POMODORO_DURATIONS[phase])
  }

  const skipPhase = () => {
    setIsRunning(false)
    handlePhaseComplete()
  }

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60)
    const secs = seconds % 60
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`
  }

  const getPhaseInfo = () => {
    switch (phase) {
      case 'focus':
        return {
          title: 'Focus Time',
          icon: Target,
          color: 'text-blue-500',
          bgColor: 'bg-blue-500/10'
        }
      case 'short-break':
        return {
          title: 'Short Break',
          icon: Coffee,
          color: 'text-green-500',
          bgColor: 'bg-green-500/10'
        }
      case 'long-break':
        return {
          title: 'Long Break',
          icon: Coffee,
          color: 'text-purple-500',
          bgColor: 'bg-purple-500/10'
        }
    }
  }

  const phaseInfo = getPhaseInfo()
  const PhaseIcon = phaseInfo.icon

  const activeTasks = tasks.filter(t => !t.completed)
  const selectedTask = tasks.find(t => t.id === selectedTaskId)

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="fixed inset-0 z-50 bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 flex items-center justify-center p-4"
    >
      {/* Background pattern */}
      <div className="absolute inset-0 opacity-5">
        <div className="absolute inset-0" style={{
          backgroundImage: 'radial-gradient(circle at 2px 2px, white 1px, transparent 0)',
          backgroundSize: '40px 40px'
        }} />
      </div>

      {/* Close button */}
      <Button
        onClick={onClose}
        variant="ghost"
        size="icon"
        className="absolute top-4 right-4 text-white/60 hover:text-white hover:bg-white/10"
      >
        <X className="h-6 w-6" />
      </Button>

      <div className="relative z-10 w-full max-w-2xl space-y-8">
        {/* Phase indicator */}
        <motion.div
          key={phase}
          initial={{ scale: 0.9, opacity: 0 }}
          animate={{ scale: 1, opacity: 1 }}
          className="text-center"
        >
          <div className={`inline-flex items-center gap-2 px-4 py-2 rounded-full ${phaseInfo.bgColor} ${phaseInfo.color} mb-4`}>
            <PhaseIcon className="h-5 w-5" />
            <span className="font-medium">{phaseInfo.title}</span>
          </div>

          {/* Pomodoro count */}
          <div className="flex items-center justify-center gap-2 mt-2">
            {Array.from({ length: 4 }).map((_, i) => (
              <div
                key={i}
                className={`h-2 w-2 rounded-full transition-colors ${
                  i < completedPomodoros % 4 ? 'bg-red-500' : 'bg-white/20'
                }`}
              />
            ))}
          </div>
        </motion.div>

        {/* Timer display */}
        <Card className="bg-white/5 backdrop-blur-xl border-white/10">
          <CardContent className="p-12">
            <div className="text-center space-y-8">
              {/* Time */}
              <motion.div
                key={timeLeft}
                initial={{ scale: 0.95 }}
                animate={{ scale: 1 }}
                className="text-8xl font-bold text-white font-mono tracking-tight"
              >
                {formatTime(timeLeft)}
              </motion.div>

              {/* Progress bar */}
              <div className="space-y-2">
                <Progress value={progress} className="h-2" />
                <div className="text-sm text-white/60">
                  {Math.round(progress)}% complete
                </div>
              </div>

              {/* Controls */}
              <div className="flex items-center justify-center gap-4">
                <Button
                  onClick={resetTimer}
                  variant="ghost"
                  size="lg"
                  className="text-white/60 hover:text-white hover:bg-white/10"
                >
                  <RotateCcw className="h-5 w-5" />
                </Button>

                <Button
                  onClick={toggleTimer}
                  size="lg"
                  className="h-16 w-16 rounded-full bg-white text-gray-900 hover:bg-white/90"
                >
                  {isRunning ? (
                    <Pause className="h-6 w-6" />
                  ) : (
                    <Play className="h-6 w-6 ml-1" />
                  )}
                </Button>

                <Button
                  onClick={skipPhase}
                  variant="ghost"
                  size="lg"
                  className="text-white/60 hover:text-white hover:bg-white/10"
                >
                  <Timer className="h-5 w-5" />
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Current task */}
        {selectedTask && (
          <Card className="bg-white/5 backdrop-blur-xl border-white/10">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-3 flex-1">
                  <Target className="h-5 w-5 text-blue-400" />
                  <div>
                    <div className="text-sm text-white/60">Current Task</div>
                    <div className="text-lg font-medium text-white">{selectedTask.title}</div>
                  </div>
                </div>
                {onTaskComplete && (
                  <Button
                    onClick={() => {
                      onTaskComplete(selectedTask.id)
                      const nextTask = activeTasks.find(t => t.id !== selectedTask.id)
                      setSelectedTaskId(nextTask?.id || null)
                    }}
                    variant="ghost"
                    size="sm"
                    className="text-green-400 hover:text-green-300 hover:bg-green-400/10"
                  >
                    <CheckCircle2 className="h-5 w-5" />
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Task list */}
        {activeTasks.length > 0 && (
          <Card className="bg-white/5 backdrop-blur-xl border-white/10">
            <CardContent className="p-6">
              <div className="text-sm text-white/60 mb-4">Today's Tasks ({activeTasks.length})</div>
              <div className="space-y-2 max-h-48 overflow-y-auto">
                {activeTasks.map(task => (
                  <motion.button
                    key={task.id}
                    onClick={() => setSelectedTaskId(task.id)}
                    className={`w-full text-left px-4 py-3 rounded-lg transition-colors ${
                      selectedTaskId === task.id
                        ? 'bg-blue-500/20 text-white border border-blue-500/30'
                        : 'bg-white/5 text-white/80 hover:bg-white/10'
                    }`}
                    whileHover={{ scale: 1.02 }}
                    whileTap={{ scale: 0.98 }}
                  >
                    {task.title}
                  </motion.button>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Stats */}
        <div className="grid grid-cols-3 gap-4">
          <Card className="bg-white/5 backdrop-blur-xl border-white/10">
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-white">{completedPomodoros}</div>
              <div className="text-xs text-white/60">Pomodoros Today</div>
            </CardContent>
          </Card>

          <Card className="bg-white/5 backdrop-blur-xl border-white/10">
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-white">{activeTasks.length}</div>
              <div className="text-xs text-white/60">Tasks Remaining</div>
            </CardContent>
          </Card>

          <Card className="bg-white/5 backdrop-blur-xl border-white/10">
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-white">
                {Math.round(completedPomodoros * 25 / 60)}h
              </div>
              <div className="text-xs text-white/60">Focus Time</div>
            </CardContent>
          </Card>
        </div>
      </div>
    </motion.div>
  )
}

src/app/api/supabase/setup/route.ts.content:
import { NextResponse } from 'next/server';
import { createClient } from '@supabase/supabase-js';

const SUPABASE_URL = process.env.NEXT_PUBLIC_SUPABASE_URL || '';
const SUPABASE_SERVICE_ROLE_KEY = process.env.SUPABASE_SERVICE_ROLE_KEY || '';

export async function POST() {
  try {
    if (!SUPABASE_URL || !SUPABASE_SERVICE_ROLE_KEY) {
      return NextResponse.json(
        { error: 'Missing Supabase credentials' },
        { status: 500 }
      );
    }

    const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY);

    // Create clients table if it doesn't exist
    const { error: createTableError } = await supabase.rpc('exec_sql', {
      sql: `
        CREATE TABLE IF NOT EXISTS clients (
          id SERIAL PRIMARY KEY,
          name TEXT NOT NULL,
          email TEXT NOT NULL,
          phone TEXT,
          company TEXT,
          created_at TIMESTAMP DEFAULT NOW(),
          updated_at TIMESTAMP DEFAULT NOW()
        );

        -- Enable Row Level Security
        ALTER TABLE clients ENABLE ROW LEVEL SECURITY;

        -- Create policy to allow all operations (for demo purposes)
        DROP POLICY IF EXISTS "Allow all operations" ON clients;
        CREATE POLICY "Allow all operations" ON clients
          FOR ALL
          USING (true)
          WITH CHECK (true);

        -- Enable Realtime
        ALTER PUBLICATION supabase_realtime ADD TABLE clients;
      `
    });

    if (createTableError) {
      // Try alternative approach - direct table creation
      const { error: altError } = await supabase.from('clients').select('id').limit(1);

      if (altError && altError.code === 'PGRST204') {
        return NextResponse.json({
          success: false,
          error: 'Table does not exist. Please run the SQL migration manually in Supabase Dashboard.',
          sql: `
CREATE TABLE IF NOT EXISTS clients (
  id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  email TEXT NOT NULL,
  phone TEXT,
  company TEXT,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

ALTER TABLE clients ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Allow all operations" ON clients
  FOR ALL
  USING (true)
  WITH CHECK (true);

ALTER PUBLICATION supabase_realtime ADD TABLE clients;
          `.trim()
        });
      }
    }

    // Verify table exists
    const { data, error: verifyError } = await supabase
      .from('clients')
      .select('id')
      .limit(1);

    if (verifyError) {
      return NextResponse.json({
        success: false,
        error: verifyError.message,
        hint: 'Run the SQL migration manually in your Supabase Dashboard'
      });
    }

    return NextResponse.json({
      success: true,
      message: 'Supabase clients table is ready!'
    });
  } catch (error: any) {
    return NextResponse.json(
      { error: error.message || 'Setup failed' },
      { status: 500 }
    );
  }
}

export async function GET() {
  try {
    if (!SUPABASE_URL || !SUPABASE_SERVICE_ROLE_KEY) {
      return NextResponse.json(
        { error: 'Missing Supabase credentials' },
        { status: 500 }
      );
    }

    const supabase = createClient(SUPABASE_URL, SUPABASE_SERVICE_ROLE_KEY);

    // Check if table exists
    const { data, error } = await supabase
      .from('clients')
      .select('id')
      .limit(1);

    if (error) {
      return NextResponse.json({
        exists: false,
        error: error.message,
        sql: `
CREATE TABLE IF NOT EXISTS clients (
  id SERIAL PRIMARY KEY,
  name TEXT NOT NULL,
  email TEXT NOT NULL,
  phone TEXT,
  company TEXT,
  created_at TIMESTAMP DEFAULT NOW(),
  updated_at TIMESTAMP DEFAULT NOW()
);

ALTER TABLE clients ENABLE ROW LEVEL SECURITY;

CREATE POLICY "Allow all operations" ON clients
  FOR ALL
  USING (true)
  WITH CHECK (true);

ALTER PUBLICATION supabase_realtime ADD TABLE clients;
        `.trim()
      });
    }

    return NextResponse.json({
      exists: true,
      message: 'Table exists and is ready'
    });
  } catch (error: any) {
    return NextResponse.json(
      { error: error.message },
      { status: 500 }
    );
  }
}

src/components/examples/SupabaseRealtime.content:
'use client'

import { useState } from 'react'
import { Card } from '@/components/ui/card'
import { Badge } from '@/components/ui/badge'
import { useSupabaseTableChanges } from '@/hooks/useSupabaseRealtime'
import { RealtimePostgresChangesPayload } from '@supabase/supabase-js'

interface RealtimeLog {
  id: string
  type: 'INSERT' | 'UPDATE' | 'DELETE'
  timestamp: string
  data: any
}

export function SupabaseRealtimeExample({ table }: { table: string }) {
  const [logs, setLogs] = useState<RealtimeLog[]>([])

  useSupabaseTableChanges(
    table,
    {
      onInsert: (payload: RealtimePostgresChangesPayload<any>) => {
        setLogs(prev => [
          {
            id: crypto.randomUUID(),
            type: 'INSERT',
            timestamp: new Date().toLocaleTimeString(),
            data: payload.new,
          },
          ...prev.slice(0, 9), // Keep last 10 logs
        ])
      },
      onUpdate: (payload: RealtimePostgresChangesPayload<any>) => {
        setLogs(prev => [
          {
            id: crypto.randomUUID(),
            type: 'UPDATE',
            timestamp: new Date().toLocaleTimeString(),
            data: payload.new,
          },
          ...prev.slice(0, 9),
        ])
      },
      onDelete: (payload: RealtimePostgresChangesPayload<any>) => {
        setLogs(prev => [
          {
            id: crypto.randomUUID(),
            type: 'DELETE',
            timestamp: new Date().toLocaleTimeString(),
            data: payload.old,
          },
          ...prev.slice(0, 9),
        ])
      },
    }
  )

  return (
    <Card className="p-6 space-y-4">
      <div>
        <h3 className="text-lg font-semibold mb-2">Supabase Realtime Example</h3>
        <p className="text-sm text-muted-foreground">
          Listening to changes on the <code className="bg-muted px-1 py-0.5 rounded">{table}</code> table
        </p>
      </div>

      <div className="space-y-2">
        {logs.length === 0 ? (
          <p className="text-sm text-muted-foreground text-center py-8">
            No changes detected yet. Make changes to the {table} table to see realtime updates.
          </p>
        ) : (
          logs.map(log => (
            <div
              key={log.id}
              className="flex items-start gap-3 p-3 bg-muted rounded-lg"
            >
              <Badge
                variant={
                  log.type === 'INSERT'
                    ? 'default'
                    : log.type === 'UPDATE'
                    ? 'secondary'
                    : 'destructive'
                }
                className="shrink-0"
              >
                {log.type}
              </Badge>
              <div className="flex-1 min-w-0">
                <p className="text-xs text-muted-foreground mb-1">{log.timestamp}</p>
                <pre className="text-xs overflow-x-auto">
                  {JSON.stringify(log.data, null, 2)}
                </pre>
              </div>
            </div>
          ))
        )}
      </div>
    </Card>
  )
}

