import { db } from '@/db';
import { sql } from 'drizzle-orm';

export async function POST(request: Request) {
  try {
    const authHeader = request.headers.get('Authorization');
    const userId = authHeader?.replace('Bearer ', '');

    if (!userId) {
      return new Response(JSON.stringify({ error: 'Unauthorized' }), {
        status: 401,
        headers: { 'Content-Type': 'application/json' }
      });
    }

    const body = await request.json();
    const { data_type, rows } = body;

    if (!data_type || !rows || !Array.isArray(rows)) {
      return new Response(JSON.stringify({ error: 'Invalid request data' }), {
        status: 400,
        headers: { 'Content-Type': 'application/json' }
      });
    }

    const results = {
      imported: 0,
      failed: 0,
      errors: [] as string[]
    };

    const now = new Date().toISOString();

    for (const row of rows) {
      try {
        if (data_type === 'clients') {
          await db.run(
            sql`INSERT INTO clients (
              name, email, phone, company, address, country, user_id, created_at, updated_at
            ) VALUES (
              ${row.name}, ${row.email}, ${row.phone || null}, ${row.company || null},
              ${row.address || null}, ${row.country || null}, ${userId}, ${now}, ${now}
            )`
          );
        } else if (data_type === 'products') {
          await db.run(
            sql`INSERT INTO products (
              name, description, price, category, user_id, created_at, updated_at
            ) VALUES (
              ${row.name}, ${row.description || null}, ${row.price}, ${row.category || null},
              ${userId}, ${now}, ${now}
            )`
          );
        } else if (data_type === 'services') {
          await db.run(
            sql`INSERT INTO services (
              name, description, unit_price, currency, user_id, created_at, updated_at
            ) VALUES (
              ${row.name}, ${row.description || null}, ${row.unit_price},
              ${row.currency || 'USD'}, ${userId}, ${now}, ${now}
            )`
          );
        } else if (data_type === 'receipts') {
          await db.run(
            sql`INSERT INTO receipts (
              vendor, amount, category, date, notes, user_id, created_at, updated_at
            ) VALUES (
              ${row.vendor}, ${row.amount}, ${row.category}, ${row.date},
              ${row.notes || null}, ${userId}, ${now}, ${now}
            )`
          );
        }
        results.imported++;
      } catch (error: any) {
        results.failed++;
        results.errors.push(`Row ${results.imported + results.failed}: ${error.message}`);
      }
    }

    return new Response(JSON.stringify(results), {
      status: 200,
      headers: { 'Content-Type': 'application/json' }
    });
  } catch (error: any) {
    return new Response(JSON.stringify({ error: error.message }), {
      status: 500,
      headers: { 'Content-Type': 'application/json' }
    });
  }
}

src/supabase/functions/generate-document.content:
import "https://deno.land/x/xhr@0.1.0/mod.ts";
import { serve } from "https://deno.land/std@0.168.0/http/server.ts";

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

serve(async (req) => {
  if (req.method === 'OPTIONS') {
    return new Response(JSON.stringify({ ok: true }), { 
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      status: 200
    });
  }

  try {
    const { type, prompt, data } = await req.json();

    if (!type || !prompt) {
      throw new Error('Document type and prompt are required');
    }

    console.log(`Generating ${type} document with prompt:`, prompt);

    let systemPrompt = '';
    let documentFormat = '';

    switch (type) {
      case 'excel':
      case 'spreadsheet':
        systemPrompt = 'You are an expert at creating structured data tables. Generate data in CSV format that can be converted to Excel. Include clear headers and properly formatted data.';
        documentFormat = 'CSV';
        break;
      case 'essay':
      case 'document':
        systemPrompt = 'You are an expert writer. Create well-structured, professional documents with clear sections, proper formatting, and compelling content.';
        documentFormat = 'Markdown';
        break;
      case 'report':
        systemPrompt = 'You are an expert at creating professional reports. Include executive summary, detailed sections, data analysis, and conclusions.';
        documentFormat = 'Markdown';
        break;
      default:
        systemPrompt = 'You are a document generation assistant. Create clear, well-structured content based on the user\'s requirements.';
        documentFormat = 'Markdown';
    }

    const fullPrompt = `${systemPrompt}\n\nUser Request: ${prompt}\n\n${data ? `Additional Data: ${JSON.stringify(data)}` : ''}\n\nGenerate a ${type} in ${documentFormat} format. Be thorough and professional.`;

    const response = await fetch(`https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash-exp:generateContent?key=${googleApiKey}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        contents: [
          { role: 'user', parts: [{ text: fullPrompt }] }
        ],
        generationConfig: {
          temperature: 0.7,
          maxOutputTokens: 4000,
        }
      }),
    });

    if (!response.ok) {
      const errorText = await response.text();
      console.error('Gemini API error:', response.status, errorText);
      throw new Error('Failed to generate document');
    }

    const result = await response.json();
    const generatedText = result.candidates?.[0]?.content?.parts?.find((p: any) => p.text)?.text || '';

    if (!generatedText) {
      throw new Error('No content generated');
    }

    console.log('Document generated successfully, length:', generatedText.length);

    // Convert to appropriate format
    let content = generatedText;
    let mimeType = 'text/markdown';
    let extension = 'md';

    if (type === 'excel' || type === 'spreadsheet') {
      mimeType = 'text/csv';
      extension = 'csv';
    }

    // Convert to base64 for download
    const blob = new TextEncoder().encode(content);
    const base64 = btoa(String.fromCharCode(...blob));

    return new Response(
      JSON.stringify({
        success: true,
        content: content,
        download: {
          data: `data:${mimeType};base64,${base64}`,
          filename: `document_${Date.now()}.${extension}`,
          mimeType: mimeType
        }
      }),
      { headers: { ...corsHeaders, 'Content-Type': 'application/json' } }
    );

  } catch (error: any) {
    const errorId = crypto.randomUUID();
    console.error(`[${errorId}] Error generating document:`, error);
    return new Response(
      JSON.stringify({ error: 'Failed to generate document' }),
      {
        status: 500,
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
      }
    );
  }
});

