import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { clients, invoices, quotes, receipts, payments } from '@/db/schema';
import { eq, and, gte, sql, desc } from 'drizzle-orm';

export async function GET(request: NextRequest) {
  try {
    const authHeader = request.headers.get('authorization');
    if (!authHeader || !authHeader.startsWith('Bearer ')) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const token = authHeader.substring(7);
    const userId = token;

    // Get total clients count
    const totalClients = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(clients)
      .where(eq(clients.userId, userId));

    // Get new clients this month
    const firstDayOfMonth = new Date();
    firstDayOfMonth.setDate(1);
    firstDayOfMonth.setHours(0, 0, 0, 0);

    const newClientsThisMonth = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(clients)
      .where(
        and(
          eq(clients.userId, userId),
          gte(clients.createdAt, firstDayOfMonth.toISOString())
        )
      );

    // Get all invoices
    const allInvoices = await db
      .select()
      .from(invoices)
      .where(eq(invoices.userId, userId));

    // Calculate revenue metrics
    const totalRevenue = allInvoices.reduce((sum, inv) => sum + parseFloat(inv.total || '0'), 0);
    const paidInvoices = allInvoices.filter(inv => inv.status === 'paid');
    const paidRevenue = paidInvoices.reduce((sum, inv) => sum + parseFloat(inv.total || '0'), 0);
    const unpaidRevenue = totalRevenue - paidRevenue;

    // Get revenue for this month
    const monthlyRevenue = allInvoices
      .filter(inv => {
        const invDate = new Date(inv.createdAt);
        return invDate >= firstDayOfMonth;
      })
      .reduce((sum, inv) => sum + parseFloat(inv.total || '0'), 0);

    // Get weekly data (last 7 days)
    const weeklyData = [];
    const today = new Date();
    for (let i = 6; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      date.setHours(0, 0, 0, 0);

      const nextDate = new Date(date);
      nextDate.setDate(nextDate.getDate() + 1);

      const dayInvoices = allInvoices.filter(inv => {
        const invDate = new Date(inv.createdAt);
        return invDate >= date && invDate < nextDate;
      });

      const dayClients = await db
        .select({ count: sql<number>`count(*)::int` })
        .from(clients)
        .where(
          and(
            eq(clients.userId, userId),
            gte(clients.createdAt, date.toISOString()),
            sql`${clients.createdAt} < ${nextDate.toISOString()}`
          )
        );

      weeklyData.push({
        day: date.toLocaleDateString('en-US', { weekday: 'short' }),
        revenue: dayInvoices.reduce((sum, inv) => sum + parseFloat(inv.total || '0'), 0),
        invoices: dayInvoices.length,
        clients: dayClients[0]?.count || 0
      });
    }

    // Get all quotes
    const allQuotes = await db
      .select()
      .from(quotes)
      .where(eq(quotes.userId, userId));

    const pendingQuotes = allQuotes.filter(q => q.status === 'pending' || q.status === 'sent');
    const acceptedQuotes = allQuotes.filter(q => q.status === 'accepted');

    // Get all receipts
    const allReceipts = await db
      .select()
      .from(receipts)
      .where(eq(receipts.userId, userId));

    const totalExpenses = allReceipts.reduce((sum, r) => sum + parseFloat(r.amount || '0'), 0);

    // Get weekly expenses (last 7 days)
    const weeklyExpenses = [];
    for (let i = 6; i >= 0; i--) {
      const date = new Date(today);
      date.setDate(date.getDate() - i);
      date.setHours(0, 0, 0, 0);

      const nextDate = new Date(date);
      nextDate.setDate(nextDate.getDate() + 1);

      const dayReceipts = allReceipts.filter(r => {
        const receiptDate = new Date(r.createdAt);
        return receiptDate >= date && receiptDate < nextDate;
      });

      weeklyExpenses.push({
        day: date.toLocaleDateString('en-US', { weekday: 'short' }),
        amount: dayReceipts.reduce((sum, r) => sum + parseFloat(r.amount || '0'), 0)
      });
    }

    // Get quote status distribution
    const quoteStatusData = [
      { status: 'Pending', count: allQuotes.filter(q => q.status === 'pending').length },
      { status: 'Sent', count: allQuotes.filter(q => q.status === 'sent').length },
      { status: 'Accepted', count: allQuotes.filter(q => q.status === 'accepted').length },
      { status: 'Rejected', count: allQuotes.filter(q => q.status === 'rejected').length },
    ].filter(item => item.count > 0);

    // Get recent activity (last 10 items)
    const recentInvoices = await db
      .select()
      .from(invoices)
      .where(eq(invoices.userId, userId))
      .orderBy(desc(invoices.createdAt))
      .limit(5);

    const recentClients = await db
      .select()
      .from(clients)
      .where(eq(clients.userId, userId))
      .orderBy(desc(clients.createdAt))
      .limit(5);

    const activity = [
      ...recentInvoices.map(inv => ({
        type: 'invoice',
        id: inv.id,
        title: `Invoice #${inv.id}`,
        description: `${inv.status} - $${inv.total}`,
        timestamp: inv.createdAt,
        status: inv.status
      })),
      ...recentClients.map(client => ({
        type: 'client',
        id: client.id,
        title: client.name,
        description: `New client added`,
        timestamp: client.createdAt,
        status: 'active'
      }))
    ]
    .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime())
    .slice(0, 10);

    // Generate insights
    const insights = [];

    // Revenue insight
    if (monthlyRevenue > 0) {
      insights.push({
        type: 'positive',
        title: 'Revenue This Month',
        description: `You've earned $${monthlyRevenue.toFixed(2)} this month`,
        action: 'View invoices'
      });
    }

    // Unpaid invoices insight
    const unpaidInvoices = allInvoices.filter(inv => inv.status !== 'paid' && inv.status !== 'cancelled');
    if (unpaidInvoices.length > 0) {
      insights.push({
        type: 'warning',
        title: 'Pending Invoices',
        description: `${unpaidInvoices.length} invoices totaling $${unpaidRevenue.toFixed(2)} are pending payment`,
        action: 'Review invoices'
      });
    }

    // New clients insight
    if (newClientsThisMonth[0]?.count > 0) {
      insights.push({
        type: 'info',
        title: 'New Clients',
        description: `You added ${newClientsThisMonth[0].count} new client${newClientsThisMonth[0].count > 1 ? 's' : ''} this month`,
        action: 'View clients'
      });
    }

    // Pending quotes insight
    if (pendingQuotes.length > 0) {
      insights.push({
        type: 'info',
        title: 'Pending Quotes',
        description: `${pendingQuotes.length} quote${pendingQuotes.length > 1 ? 's' : ''} waiting for client response`,
        action: 'View quotes'
      });
    }

    return NextResponse.json({
      success: true,
      data: {
        metrics: {
          monthlyRevenue: monthlyRevenue,
          activeClients: totalClients[0]?.count || 0,
          invoicesPaid: paidInvoices.length,
          totalInvoices: allInvoices.length,
          totalQuotes: allQuotes.length,
          acceptedQuotes: acceptedQuotes.length,
          totalExpenses: totalExpenses,
          unpaidRevenue: unpaidRevenue
        },
        weeklyData,
        weeklyExpenses,
        quoteStatusData,
        activity,
        insights
      }
    });

  } catch (error) {
    console.error('Analytics error:', error);
    return NextResponse.json(
      { success: false, error: 'Failed to fetch analytics' },
      { status: 500 }
    );
  }
}

src/app/api/lumenr/receipts/bulk-import/.content:
import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { receipts } from '@/db/schema';
import { getAuthUser } from '@/lib/auth-api';
import { jsonOk, jsonError } from '@/lib/api-utils';
import { ReceiptImportRow } from '@/lib/utils/receipt-excel-import';

export async function POST(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const body = await request.json();
    const { receipts: receiptData } = body;

    if (!receiptData || !Array.isArray(receiptData)) {
      return jsonError('Receipts array is required', 400);
    }

    if (receiptData.length === 0) {
      return jsonError('At least one receipt is required', 400);
    }

    if (receiptData.length > 1000) {
      return jsonError('Maximum 1000 receipts allowed per import', 400);
    }

    const now = new Date().toISOString();
    const successfulReceipts: any[] = [];
    const failedReceipts: Array<{ row: number; error: string }> = [];

    for (let i = 0; i < receiptData.length; i++) {
      const receipt: ReceiptImportRow = receiptData[i];
      const rowNumber = i + 2;

      try {
        if (!receipt.vendor || !receipt.vendor.trim()) {
          failedReceipts.push({
            row: rowNumber,
            error: 'Vendor is required'
          });
          continue;
        }

        if (!receipt.amount || isNaN(receipt.amount) || receipt.amount <= 0) {
          failedReceipts.push({
            row: rowNumber,
            error: 'Valid positive amount is required'
          });
          continue;
        }

        if (!receipt.category || !receipt.category.trim()) {
          failedReceipts.push({
            row: rowNumber,
            error: 'Category is required'
          });
          continue;
        }

        if (!receipt.date || !receipt.date.trim()) {
          failedReceipts.push({
            row: rowNumber,
            error: 'Date is required'
          });
          continue;
        }

        const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
        if (!dateRegex.test(receipt.date)) {
          failedReceipts.push({
            row: rowNumber,
            error: 'Date must be in YYYY-MM-DD format'
          });
          continue;
        }

        const newReceipt = await db
          .insert(receipts)
          .values({
            vendor: receipt.vendor.trim(),
            amount: receipt.amount.toString(),
            category: receipt.category.trim(),
            date: receipt.date.trim(),
            notes: receipt.notes?.trim() || null,
            fileUrl: null,
            userId: userId,
            createdAt: now,
            updatedAt: now,
          })
          .returning();

        successfulReceipts.push(newReceipt[0]);
      } catch (dbError) {
        failedReceipts.push({
          row: rowNumber,
          error: `Database error: ${(dbError as Error).message}`
        });
      }
    }

    return jsonOk({
      message: 'Bulk import completed',
      successful: successfulReceipts.length,
      failed: failedReceipts.length,
      totalProcessed: receiptData.length,
      receipts: successfulReceipts,
      errors: failedReceipts
    }, 201);

  } catch (error) {
    console.error('Bulk import error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

