import { NextRequest, NextResponse } from 'next/server';
import { db } from '@/db';
import { bookings, clients } from '@/db/schema';
import { eq, and, gte, lte } from 'drizzle-orm';
import { getAuthUser } from '@/lib/auth-api';
import { jsonOk, jsonError } from '@/lib/api-utils';
import {
  listGoogleCalendarEvents,
  createGoogleCalendarEvent,
  updateGoogleCalendarEvent,
  bookingToGoogleEvent,
} from '@/lib/integrations/google-calendar';

export async function GET(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const searchParams = request.nextUrl.searchParams;
    const action = searchParams.get('action');

    if (action === 'import') {
      const startDate = searchParams.get('startDate');
      const endDate = searchParams.get('endDate');

      const timeMin = startDate ? new Date(startDate) : new Date();
      const timeMax = endDate ? new Date(endDate) : new Date(Date.now() + 30 * 24 * 60 * 60 * 1000);

      try {
        const events = await listGoogleCalendarEvents('primary', timeMin, timeMax);

        return jsonOk({
          message: 'Google Calendar events retrieved successfully',
          events: events.map(event => ({
            id: event.id,
            summary: event.summary,
            description: event.description,
            start: event.start?.dateTime || event.start?.date,
            end: event.end?.dateTime || event.end?.date,
            location: event.location,
            attendees: event.attendees,
          })),
        });
      } catch (gcError: any) {
        console.error('Google Calendar API Error:', gcError);
        return jsonError('Failed to connect to Google Calendar: ' + gcError.message, 500);
      }
    }

    return jsonError('Invalid action. Use ?action=import', 400);
  } catch (error) {
    console.error('GET /api/lumenr/calendar/sync error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

export async function POST(request: NextRequest) {
  try {
    const { userId, error } = await getAuthUser(request);
    if (error || !userId) {
      return jsonError('Authentication required', 401);
    }

    const body = await request.json();
    const { action, bookingIds, startDate, endDate } = body;

    if (action === 'export') {
      if (!bookingIds || !Array.isArray(bookingIds)) {
        return jsonError('bookingIds array is required', 400);
      }

      const userBookings = await db
        .select({
          id: bookings.id,
          clientId: bookings.clientId,
          userId: bookings.userId,
          title: bookings.title,
          date: bookings.date,
          time: bookings.time,
          duration: bookings.duration,
          status: bookings.status,
          notes: bookings.notes,
          googleEventId: bookings.googleEventId,
          clientEmail: clients.email,
        })
        .from(bookings)
        .leftJoin(clients, eq(bookings.clientId, clients.id))
        .where(eq(bookings.userId, userId));

      const selectedBookings = userBookings.filter(booking => 
        bookingIds.includes(booking.id)
      );

      if (selectedBookings.length === 0) {
        return jsonError('No valid bookings found', 404);
      }

      const exportResults = [];

      for (const booking of selectedBookings) {
        try {
          const googleEvent = bookingToGoogleEvent(booking, booking.clientEmail || undefined);
          let createdEvent;

          if (booking.googleEventId) {
            try {
              createdEvent = await updateGoogleCalendarEvent(booking.googleEventId, googleEvent);
              await db.update(bookings)
                .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
                .where(eq(bookings.id, booking.id));
              exportResults.push({
                bookingId: booking.id,
                success: true,
                googleEventId: createdEvent.id,
                action: 'updated',
              });
            } catch (updateError: any) {
              if (updateError.code === 404) {
                createdEvent = await createGoogleCalendarEvent(googleEvent);
                await db.update(bookings)
                  .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
                  .where(eq(bookings.id, booking.id));
                exportResults.push({
                  bookingId: booking.id,
                  success: true,
                  googleEventId: createdEvent.id,
                  action: 'created',
                });
              } else {
                throw updateError;
              }
            }
          } else {
            createdEvent = await createGoogleCalendarEvent(googleEvent);
            await db.update(bookings)
              .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
              .where(eq(bookings.id, booking.id));
            exportResults.push({
              bookingId: booking.id,
              success: true,
              googleEventId: createdEvent.id,
              action: 'created',
            });
          }
        } catch (exportError: any) {
          console.error(`Error exporting booking ${booking.id}:`, exportError);
          exportResults.push({
            bookingId: booking.id,
            success: false,
            error: exportError.message,
          });
        }
      }

      return jsonOk({
        message: 'Export completed',
        results: exportResults,
        successful: exportResults.filter(r => r.success).length,
        failed: exportResults.filter(r => !r.success).length,
      });
    }

    if (action === 'sync-all') {
      const timeMin = startDate ? new Date(startDate) : new Date();
      const timeMax = endDate ? new Date(endDate) : new Date(Date.now() + 30 * 24 * 60 * 60 * 1000);

      const userBookings = await db
        .select({
          id: bookings.id,
          clientId: bookings.clientId,
          userId: bookings.userId,
          title: bookings.title,
          date: bookings.date,
          time: bookings.time,
          duration: bookings.duration,
          status: bookings.status,
          notes: bookings.notes,
          googleEventId: bookings.googleEventId,
          clientEmail: clients.email,
        })
        .from(bookings)
        .leftJoin(clients, eq(bookings.clientId, clients.id))
        .where(
          and(
            eq(bookings.userId, userId),
            gte(bookings.date, timeMin.toISOString().split('T')[0]),
            lte(bookings.date, timeMax.toISOString().split('T')[0])
          )
        );

      const exportResults = [];

      for (const booking of userBookings) {
        try {
          const googleEvent = bookingToGoogleEvent(booking, booking.clientEmail || undefined);
          let createdEvent;

          if (booking.googleEventId) {
            try {
              createdEvent = await updateGoogleCalendarEvent(booking.googleEventId, googleEvent);
              await db.update(bookings)
                .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
                .where(eq(bookings.id, booking.id));
              exportResults.push({
                bookingId: booking.id,
                success: true,
                googleEventId: createdEvent.id,
                action: 'updated',
              });
            } catch (updateError: any) {
              if (updateError.code === 404) {
                createdEvent = await createGoogleCalendarEvent(googleEvent);
                await db.update(bookings)
                  .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
                  .where(eq(bookings.id, booking.id));
                exportResults.push({
                  bookingId: booking.id,
                  success: true,
                  googleEventId: createdEvent.id,
                  action: 'created',
                });
              } else {
                throw updateError;
              }
            }
          } else {
            createdEvent = await createGoogleCalendarEvent(googleEvent);
            await db.update(bookings)
              .set({ googleEventId: createdEvent.id, updatedAt: new Date().toISOString() })
              .where(eq(bookings.id, booking.id));
            exportResults.push({
              bookingId: booking.id,
              success: true,
              googleEventId: createdEvent.id,
              action: 'created',
            });
          }
        } catch (exportError: any) {
          console.error(`Error syncing booking ${booking.id}:`, exportError);
          exportResults.push({
            bookingId: booking.id,
            success: false,
            error: exportError.message,
          });
        }
      }

      return jsonOk({
        message: 'Sync completed',
        results: exportResults,
        successful: exportResults.filter(r => r.success).length,
        failed: exportResults.filter(r => !r.success).length,
      });
    }

    return jsonError('Invalid action. Use action=export or action=sync-all', 400);
  } catch (error) {
    console.error('POST /api/lumenr/calendar/sync error:', error);
    return jsonError('Internal server error: ' + (error as Error).message, 500);
  }
}

src/lib/utils/receipt-excel-import.ts.content:
import * as XLSX from 'xlsx';

export interface ReceiptImportRow {
  vendor: string;
  amount: number;
  category: string;
  date: string;
  notes?: string;
}

export interface ReceiptImportResult {
  success: boolean;
  data?: ReceiptImportRow[];
  errors?: string[];
  rowCount?: number;
}

const VALID_CATEGORIES = [
  'Office Supplies',
  'Travel',
  'Meals',
  'Software',
  'Marketing',
  'Utilities',
  'Other'
];

function isValidDateFormat(dateString: string): boolean {
  const dateRegex = /^\d{4}-\d{2}-\d{2}$/;
  if (!dateRegex.test(dateString)) {
    return false;
  }
  const date = new Date(dateString);
  return date instanceof Date && !isNaN(date.getTime());
}

export function generateReceiptTemplate(): void {
  const template = [
    {
      'Vendor *': 'Starbucks',
      'Amount *': '15.50',
      'Category *': 'Meals',
      'Date * (YYYY-MM-DD)': '2025-10-15',
      'Notes': 'Client meeting coffee'
    },
    {
      'Vendor *': 'Amazon Web Services',
      'Amount *': '89.99',
      'Category *': 'Software',
      'Date * (YYYY-MM-DD)': '2025-10-01',
      'Notes': 'Monthly hosting fees'
    },
    {
      'Vendor *': 'Delta Airlines',
      'Amount *': '450.00',
      'Category *': 'Travel',
      'Date * (YYYY-MM-DD)': '2025-09-28',
      'Notes': 'Flight to client site'
    },
    {
      'Vendor *': 'Google Ads',
      'Amount *': '75.25',
      'Category *': 'Marketing',
      'Date * (YYYY-MM-DD)': '2025-10-10',
      'Notes': 'Monthly advertising campaign'
    }
  ];

  const worksheet = XLSX.utils.json_to_sheet(template);

  const colWidths = [
    { wch: 25 },
    { wch: 15 },
    { wch: 20 },
    { wch: 22 },
    { wch: 35 }
  ];
  worksheet['!cols'] = colWidths;

  const workbook = XLSX.utils.book_new();
  XLSX.utils.book_append_sheet(workbook, worksheet, 'Receipts Template');

  XLSX.writeFile(workbook, 'receipts_import_template.xlsx');
}

export function parseReceiptExcel(file: File): Promise<ReceiptImportResult> {
  return new Promise((resolve) => {
    const reader = new FileReader();

    reader.onload = (e) => {
      try {
        const data = e.target?.result;
        const workbook = XLSX.read(data, { type: 'binary' });

        const firstSheetName = workbook.SheetNames[0];
        const worksheet = workbook.Sheets[firstSheetName];

        const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' });

        const errors: string[] = [];
        const receipts: ReceiptImportRow[] = [];

        jsonData.forEach((row: any, index: number) => {
          const rowNumber = index + 2;

          const vendor = row['Vendor *'] || row['Vendor'] || '';
          const amountStr = row['Amount *'] || row['Amount'] || '';
          const category = row['Category *'] || row['Category'] || '';
          const date = row['Date * (YYYY-MM-DD)'] || row['Date'] || '';

          if (!vendor || !vendor.toString().trim()) {
            errors.push(`Row ${rowNumber}: Vendor is required`);
            return;
          }

          if (!amountStr || amountStr.toString().trim() === '') {
            errors.push(`Row ${rowNumber}: Amount is required`);
            return;
          }

          const amount = parseFloat(amountStr.toString().replace(/[,$]/g, ''));
          if (isNaN(amount) || amount <= 0) {
            errors.push(`Row ${rowNumber}: Amount must be a positive number`);
            return;
          }

          if (!category || !category.toString().trim()) {
            errors.push(`Row ${rowNumber}: Category is required`);
            return;
          }

          const categoryStr = category.toString().trim();
          if (!VALID_CATEGORIES.includes(categoryStr)) {
            errors.push(`Row ${rowNumber}: Category must be one of: ${VALID_CATEGORIES.join(', ')}`);
            return;
          }

          if (!date || !date.toString().trim()) {
            errors.push(`Row ${rowNumber}: Date is required`);
            return;
          }

          const dateStr = date.toString().trim();
          if (!isValidDateFormat(dateStr)) {
            errors.push(`Row ${rowNumber}: Date must be in YYYY-MM-DD format (e.g., 2025-10-15)`);
            return;
          }

          receipts.push({
            vendor: vendor.toString().trim(),
            amount: amount,
            category: categoryStr,
            date: dateStr,
            notes: row['Notes'] ? row['Notes'].toString().trim() : undefined
          });
        });

        if (errors.length > 0) {
          resolve({
            success: false,
            errors,
            rowCount: jsonData.length
          });
          return;
        }

        if (receipts.length === 0) {
          resolve({
            success: false,
            errors: ['No valid receipt data found in the file'],
            rowCount: 0
          });
          return;
        }

        resolve({
          success: true,
          data: receipts,
          rowCount: receipts.length
        });

      } catch (error) {
        resolve({
          success: false,
          errors: [`Failed to parse Excel file: ${(error as Error).message}`],
          rowCount: 0
        });
      }
    };

    reader.onerror = () => {
      resolve({
        success: false,
        errors: ['Failed to read file'],
        rowCount: 0
      });
    };

    reader.readAsBinaryString(file);
  });
}

src/components/Receipts/ReceiptExcelImpo.content:
'use client'

import { useState } from 'react'
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog'
import { Button } from '@/components/ui/button'
import { generateReceiptTemplate, parseReceiptExcel, ReceiptImportRow } from '@/lib/utils/receipt-excel-import'
import { Upload, Download, FileSpreadsheet, AlertCircle, CheckCircle2 } from 'lucide-react'
import { Alert, AlertDescription } from '@/components/ui/alert'
import { toast } from 'sonner'

interface ReceiptExcelImportProps {
  open: boolean
  onOpenChange: (open: boolean) => void
  onImportSuccess: () => void
}

export function ReceiptExcelImport({ open, onOpenChange, onImportSuccess }: ReceiptExcelImportProps) {
  const [importFile, setImportFile] = useState<File | null>(null)
  const [importing, setImporting] = useState(false)
  const [importResult, setImportResult] = useState<any>(null)

  const handleDownloadTemplate = () => {
    generateReceiptTemplate()
    toast.success('Template downloaded successfully')
  }

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0]
    if (file) {
      if (!file.name.endsWith('.xlsx') && !file.name.endsWith('.xls')) {
        toast.error('Please upload an Excel file (.xlsx or .xls)')
        return
      }
      setImportFile(file)
      setImportResult(null)
    }
  }

  const handleImport = async () => {
    if (!importFile) {
      toast.error('Please select a file to import')
      return
    }

    setImporting(true)
    setImportResult(null)

    try {
      const result = await parseReceiptExcel(importFile)

      if (!result.success) {
        const errorList = result.errors?.map(err => {
          const match = err.match(/^Row (\d+): (.+)$/);
          if (match) {
            return { row: parseInt(match[1]), error: match[2] };
          }
          return { row: 0, error: err };
        }) || []

        setImportResult({
          success: false,
          successful: 0,
          failed: result.errors?.length || 0,
          errors: errorList,
          total: result.rowCount || 0
        })
        toast.error(`Found ${result.errors?.length || 0} validation errors in the Excel file`)
        return
      }

      const token = localStorage.getItem('bearer_token')
      const response = await fetch('/api/lumenr/receipts/bulk-import', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ receipts: result.data })
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || 'Failed to import receipts')
      }

      const data = await response.json()

      setImportResult({
        success: true,
        successful: data.data.successful || 0,
        failed: data.data.failed || 0,
        errors: data.data.errors || [],
        total: data.data.totalProcessed || 0
      })

      if (data.data.successful > 0) {
        toast.success(`Successfully imported ${data.data.successful} receipts`)
        onImportSuccess()

        if (data.data.failed === 0) {
          setTimeout(() => {
            handleClose()
          }, 1500)
        }
      }

      if (data.data.failed > 0) {
        toast.error(`Failed to import ${data.data.failed} receipts`)
      }

    } catch (error: any) {
      console.error('Import error:', error)
      setImportResult({
        success: false,
        successful: 0,
        failed: 0,
        errors: [{ row: 0, error: error.message || 'An unexpected error occurred' }],
        total: 0
      })
      toast.error(error.message || 'Failed to import receipts')
    } finally {
      setImporting(false)
    }
  }

  const handleClose = () => {
    onOpenChange(false)
    setImportFile(null)
    setImportResult(null)
  }

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Import Receipts from Excel</DialogTitle>
        </DialogHeader>

        <div className="space-y-4">
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
            <div className="flex items-start space-x-3">
              <FileSpreadsheet className="h-5 w-5 text-blue-600 mt-0.5" />
              <div className="flex-1">
                <h4 className="text-sm font-medium text-blue-900 mb-1">Step 1: Download Template</h4>
                <p className="text-xs text-blue-700 mb-3">
                  Download the Excel template with sample data and proper column headers.
                </p>
                <Button 
                  size="sm" 
                  variant="outline" 
                  onClick={handleDownloadTemplate}
                  className="border-blue-300 hover:bg-blue-100"
                >
                  <Download className="h-4 w-4 mr-2" />
                  Download Template
                </Button>
              </div>
            </div>
          </div>

          <div className="bg-green-50 border border-green-200 rounded-lg p-4">
            <div className="flex items-start space-x-3">
              <Upload className="h-5 w-5 text-green-600 mt-0.5" />
              <div className="flex-1">
                <h4 className="text-sm font-medium text-green-900 mb-1">Step 2: Upload Your File</h4>
                <p className="text-xs text-green-700 mb-3">
                  Upload your completed Excel file with receipt data.
                </p>
                <div className="space-y-3">
                  <input
                    type="file"
                    accept=".xlsx,.xls"
                    onChange={handleFileSelect}
                    className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-md file:border-0 file:text-sm file:font-medium file:bg-green-100 file:text-green-700 hover:file:bg-green-200 cursor-pointer"
                    disabled={importing}
                  />
                  {importFile && (
                    <p className="text-xs text-gray-600">
                      Selected: <span className="font-medium">{importFile.name}</span>
                    </p>
                  )}
                </div>
              </div>
            </div>
          </div>

          {importResult && (
            <div className="space-y-2">
              {importResult.successful > 0 && (
                <Alert className="border-green-200 bg-green-50">
                  <CheckCircle2 className="h-4 w-4 text-green-600" />
                  <AlertDescription className="text-green-800">
                    Successfully imported <strong>{importResult.successful}</strong> out of {importResult.total} receipts!
                  </AlertDescription>
                </Alert>
              )}

              {importResult.failed > 0 && importResult.errors && (
                <Alert className="border-red-200 bg-red-50">
                  <AlertCircle className="h-4 w-4 text-red-600" />
                  <AlertDescription className="text-red-800">
                    <p className="font-medium mb-2">Failed to import {importResult.failed} receipts:</p>
                    <ul className="text-xs space-y-1 max-h-40 overflow-y-auto">
                      {importResult.errors.map((error: any, index: number) => (
                        <li key={index}>Row {error.row}: {error.error}</li>
                      ))}
                    </ul>
                  </AlertDescription>
                </Alert>
              )}

              {!importResult.success && importResult.errors && !importResult.total && (
                <Alert className="border-red-200 bg-red-50">
                  <AlertCircle className="h-4 w-4 text-red-600" />
                  <AlertDescription className="text-red-800">
                    <ul className="text-xs space-y-1">
                      {importResult.errors.map((error: any, index: number) => (
                        <li key={index}>{error.error || error}</li>
                      ))}
                    </ul>
                  </AlertDescription>
                </Alert>
              )}
            </div>
          )}

          <div className="flex justify-end space-x-2">
            <Button 
              type="button" 
              variant="outline" 
              onClick={handleClose}
              disabled={importing}
            >
              Cancel
            </Button>
            <Button 
              onClick={handleImport} 
              disabled={!importFile || importing}
            >
              {importing ? 'Importing...' : 'Import Receipts'}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  )
}

src/components/Receipts/OCRReceiptUpload.content:
'use client';

import { useState, useRef, useEffect } from 'react';
import { Upload, FileImage, Loader2, CheckCircle2, Camera, X } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { toast } from 'sonner';
import { processReceiptImage, ExtractedReceiptData } from '@/lib/utils/ocr-processor';
import { uploadReceiptImage } from '@/lib/receipt-storage';

interface Client {
  id: number;
  name: string;
}

interface OCRReceiptUploadProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
  categories: string[];
  receiptType?: 'expense' | 'client';
  clients?: Client[];
}

type InputMode = 'upload' | 'camera';

export default function OCRReceiptUpload({ isOpen, onClose, onSuccess, categories, receiptType = 'expense', clients = [] }: OCRReceiptUploadProps) {
  const [inputMode, setInputMode] = useState<InputMode>('upload');
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [ocrProgress, setOcrProgress] = useState<number>(0);
  const [extractedData, setExtractedData] = useState<ExtractedReceiptData | null>(null);
  const [editableData, setEditableData] = useState({
    vendor: '',
    amount: '',
    date: '',
    category: 'Other',
    currency: 'USD',
    notes: '',
    clientId: ''
  });
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [cameraError, setCameraError] = useState<string | null>(null);

  const fileInputRef = useRef<HTMLInputElement>(null);
  const videoRef = useRef<HTMLVideoElement>(null);
  const streamRef = useRef<MediaStream | null>(null);

  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, []);

  const startCamera = async () => {
    try {
      setCameraError(null);
      const stream = await navigator.mediaDevices.getUserMedia({
        video: { facingMode: 'environment' }
      });

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        streamRef.current = stream;
        setIsCameraActive(true);
      }
    } catch (error: any) {
      console.error('Camera error:', error);
      setCameraError('Unable to access camera. Please check permissions.');
      toast.error('Camera access denied. Please enable camera permissions.');
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    setIsCameraActive(false);
  };

  const capturePhoto = () => {
    if (!videoRef.current) return;

    const canvas = document.createElement('canvas');
    canvas.width = videoRef.current.videoWidth;
    canvas.height = videoRef.current.videoHeight;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    ctx.drawImage(videoRef.current, 0, 0);

    canvas.toBlob(async (blob) => {
      if (!blob) return;

      const file = new File([blob], `receipt-${Date.now()}.jpg`, { type: 'image/jpeg' });
      setSelectedFile(file);
      setPreviewUrl(URL.createObjectURL(file));

      stopCamera();

      setIsProcessing(true);
      setOcrProgress(0);
      try {
        const extracted = await processReceiptImage(file, (progress) => {
          setOcrProgress(Math.round(progress * 100));
        });
        setExtractedData(extracted);

        setEditableData({
          vendor: extracted.vendor,
          amount: extracted.amount?.toString() || '',
          date: extracted.date || new Date().toISOString().split('T')[0],
          category: extracted.category,
          currency: 'USD',
          notes: ''
        });

        toast.success('Receipt processed successfully!');
      } catch (error: any) {
        toast.error(error.message || 'Failed to process receipt');
        console.error('OCR Error:', error);
      } finally {
        setIsProcessing(false);
        setOcrProgress(0);
      }
    }, 'image/jpeg', 0.95);
  };

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (!file.type.startsWith('image/')) {
      toast.error('Please select an image file');
      return;
    }

    if (file.size > 10 * 1024 * 1024) {
      toast.error('Image must be less than 10MB');
      return;
    }

    setSelectedFile(file);
    setPreviewUrl(URL.createObjectURL(file));

    setIsProcessing(true);
    setOcrProgress(0);
    try {
      const extracted = await processReceiptImage(file, (progress) => {
        setOcrProgress(Math.round(progress * 100));
      });
      setExtractedData(extracted);

      setEditableData({
        vendor: extracted.vendor,
        amount: extracted.amount?.toString() || '',
        date: extracted.date || new Date().toISOString().split('T')[0],
        category: extracted.category,
        currency: 'USD',
        notes: ''
      });

      toast.success('Receipt processed successfully!');
    } catch (error: any) {
      toast.error(error.message || 'Failed to process receipt');
      console.error('OCR Error:', error);
    } finally {
      setIsProcessing(false);
      setOcrProgress(0);
    }
  };

  const handleDrop = (e: React.DragEvent<HTMLDivElement>) => {
    e.preventDefault();
    const file = e.dataTransfer.files?.[0];
    if (file && fileInputRef.current) {
      const dataTransfer = new DataTransfer();
      dataTransfer.items.add(file);
      fileInputRef.current.files = dataTransfer.files;
      handleFileSelect({ target: { files: dataTransfer.files } } as any);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!editableData.vendor || !editableData.amount || !editableData.date) {
      toast.error('Please fill in all required fields');
      return;
    }

    if (receiptType === 'client' && !editableData.clientId) {
      toast.error('Please select a client');
      return;
    }

    if (!selectedFile) {
      toast.error('No receipt image selected');
      return;
    }

    setIsSaving(true);

    try {
      const token = localStorage.getItem('bearer_token');
      const userId = localStorage.getItem('user_id');

      if (!userId) {
        toast.error('User not authenticated');
        return;
      }

      const amount = parseFloat(editableData.amount);

      if (isNaN(amount) || amount <= 0) {
        toast.error('Please enter a valid amount');
        return;
      }

      let imageUrl = null;

      toast.loading('Uploading receipt image...');
      const uploadResult = await uploadReceiptImage({
        file: selectedFile,
        userId: userId,
        fileName: selectedFile.name
      });

      if (!uploadResult.success) {
        toast.dismiss();
        toast.error(uploadResult.error || 'Failed to upload image');
        return;
      }

      imageUrl = uploadResult.publicUrl || null;
      toast.dismiss();

      const response = await fetch('/api/lumenr/receipts', {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          vendor: editableData.vendor,
          category: editableData.category,
          amount,
          currency: editableData.currency,
          date: editableData.date,
          type: receiptType,
          clientId: receiptType === 'client' ? parseInt(editableData.clientId) : null,
          imageUrl: imageUrl,
          notes: editableData.notes || null
        })
      });

      if (!response.ok) {
        throw new Error('Failed to create receipt');
      }

      toast.success('Receipt saved successfully!');
      resetForm();
      onSuccess();
      onClose();
    } catch (error: any) {
      toast.error(error.message || 'Failed to save receipt');
    } finally {
      setIsSaving(false);
    }
  };

  const resetForm = () => {
    setSelectedFile(null);
    setPreviewUrl(null);
    setExtractedData(null);
    setOcrProgress(0);
    setInputMode('upload');
    stopCamera();
    setEditableData({
      vendor: '',
      amount: '',
      date: '',
      category: 'Other',
      currency: 'USD',
      notes: '',
      clientId: ''
    });
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const handleClose = () => {
    resetForm();
    onClose();
  };

  const handleModeChange = (mode: InputMode) => {
    if (mode === 'camera') {
      setInputMode('camera');
      startCamera();
    } else {
      stopCamera();
      setInputMode('upload');
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => !open && handleClose()}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Upload className="w-5 h-5" />
            Scan Receipt with OCR
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {!selectedFile ? (
            <>
              <div className="flex gap-2 justify-center">
                <Button
                  type="button"
                  variant={inputMode === 'upload' ? 'default' : 'outline'}
                  onClick={() => handleModeChange('upload')}
                  className="flex-1"
                >
                  <Upload className="w-4 h-4 mr-2" />
                  Upload File
                </Button>
                <Button
                  type="button"
                  variant={inputMode === 'camera' ? 'default' : 'outline'}
                  onClick={() => handleModeChange('camera')}
                  className="flex-1"
                >
                  <Camera className="w-4 h-4 mr-2" />
                  Use Camera
                </Button>
              </div>

              {inputMode === 'upload' && (
                <div
                  onDrop={handleDrop}
                  onDragOver={(e) => e.preventDefault()}
                  onClick={() => fileInputRef.current?.click()}
                  className="border-2 border-dashed border-border rounded-lg p-12 text-center cursor-pointer hover:border-primary transition-colors"
                >
                  <FileImage className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                  <p className="text-lg font-medium mb-2">Drop receipt image here or click to browse</p>
                  <p className="text-sm text-muted-foreground">
                    Supports JPG, PNG, WEBP (max 10MB)
                  </p>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    className="hidden"
                  />
                </div>
              )}

              {inputMode === 'camera' && (
                <div className="space-y-4">
                  {cameraError ? (
                    <div className="border-2 border-dashed border-destructive rounded-lg p-12 text-center">
                      <Camera className="w-16 h-16 mx-auto mb-4 text-destructive" />
                      <p className="text-lg font-medium mb-2 text-destructive">{cameraError}</p>
                      <p className="text-sm text-muted-foreground mb-4">
                        Please enable camera access in your browser settings
                      </p>
                      <Button onClick={() => handleModeChange('upload')} variant="outline">
                        Switch to File Upload
                      </Button>
                    </div>
                  ) : isCameraActive ? (
                    <div className="relative rounded-lg overflow-hidden bg-black">
                      <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        className="w-full max-h-[400px] object-contain"
                      />
                      <div className="absolute bottom-4 left-0 right-0 flex justify-center gap-4">
                        <Button
                          type="button"
                          size="lg"
                          onClick={capturePhoto}
                          className="bg-white hover:bg-gray-100 text-black"
                        >
                          <Camera className="w-5 h-5 mr-2" />
                          Capture Photo
                        </Button>
                        <Button
                          type="button"
                          size="lg"
                          variant="destructive"
                          onClick={() => handleModeChange('upload')}
                        >
                          <X className="w-5 h-5 mr-2" />
                          Cancel
                        </Button>
                      </div>
                    </div>
                  ) : (
                    <div className="border-2 border-dashed border-border rounded-lg p-12 text-center">
                      <Camera className="w-16 h-16 mx-auto mb-4 text-muted-foreground animate-pulse" />
                      <p className="text-lg font-medium mb-2">Starting camera...</p>
                    </div>
                  )}
                </div>
              )}
            </>
          ) : (
            <div className="space-y-6">
              <div className="flex items-start gap-4">
                {previewUrl && (
                  <div className="w-48 h-48 flex-shrink-0 rounded-lg overflow-hidden border border-border">
                    <img src={previewUrl} alt="Receipt preview" className="w-full h-full object-cover" />
                  </div>
                )}

                <div className="flex-1 space-y-2">
                  <div className="flex items-center gap-2">
                    <FileImage className="w-5 h-5 text-muted-foreground" />
                    <span className="text-sm font-medium truncate">{selectedFile.name}</span>
                  </div>

                  {isProcessing && (
                    <div className="space-y-2">
                      <div className="flex items-center gap-2 text-blue-600">
                        <Loader2 className="w-4 h-4 animate-spin" />
                        <span className="text-sm">Processing with OCR... {ocrProgress}%</span>
                      </div>
                      <div className="w-full bg-muted rounded-full h-2">
                        <div 
                          className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${ocrProgress}%` }}
                        />
                      </div>
                    </div>
                  )}

                  {extractedData && !isProcessing && (
                    <div className="flex items-center gap-2 text-green-600">
                      <CheckCircle2 className="w-4 h-4" />
                      <span className="text-sm">Data extracted successfully!</span>
                    </div>
                  )}

                  <Button
                    type="button"
                    variant="outline"
                    size="sm"
                    onClick={() => {
                      resetForm();
                    }}
                  >
                    Choose Different Image
                  </Button>
                </div>
              </div>

              {extractedData && !isProcessing && (
                <form onSubmit={handleSubmit} className="space-y-4">
                  <div className="bg-muted/50 p-4 rounded-lg border border-border">
                    <p className="text-sm font-medium mb-2 flex items-center gap-2">
                      <CheckCircle2 className="w-4 h-4 text-green-600" />
                      Review and edit the extracted data:
                    </p>
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="vendor">Vendor *</Label>
                      <Input
                        id="vendor"
                        value={editableData.vendor}
                        onChange={(e) => setEditableData(prev => ({ ...prev, vendor: e.target.value }))}
                        placeholder="Enter vendor name"
                        required
                      />
                    </div>

                    <div>
                      <Label htmlFor="date">Date *</Label>
                      <Input
                        id="date"
                        type="date"
                        value={editableData.date}
                        onChange={(e) => setEditableData(prev => ({ ...prev, date: e.target.value }))}
                        required
                      />
                    </div>

                    <div>
                      <Label htmlFor="amount">Amount *</Label>
                      <Input
                        id="amount"
                        type="number"
                        step="0.01"
                        value={editableData.amount}
                        onChange={(e) => setEditableData(prev => ({ ...prev, amount: e.target.value }))}
                        placeholder="0.00"
                        required
                      />
                    </div>

                    <div>
                      <Label htmlFor="currency">Currency *</Label>
                      <Select
                        value={editableData.currency}
                        onValueChange={(value) => setEditableData(prev => ({ ...prev, currency: value }))}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="USD">USD ($)</SelectItem>
                          <SelectItem value="CAD">CAD ($)</SelectItem>
                          <SelectItem value="EUR">EUR (â‚¬)</SelectItem>
                          <SelectItem value="GBP">GBP (Â£)</SelectItem>
                          <SelectItem value="AUD">AUD ($)</SelectItem>
                          <SelectItem value="NZD">NZD ($)</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="md:col-span-2">
                      <Label htmlFor="category">Category *</Label>
                      <Select
                        value={editableData.category}
                        onValueChange={(value) => setEditableData(prev => ({ ...prev, category: value }))}
                      >
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {categories.map(cat => (
                            <SelectItem key={cat} value={cat}>{cat}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {receiptType === 'client' && (
                      <div className="md:col-span-2">
                        <Label htmlFor="client">Client *</Label>
                        <Select
                          value={editableData.clientId}
                          onValueChange={(value) => setEditableData(prev => ({ ...prev, clientId: value }))}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select a client" />
                          </SelectTrigger>
                          <SelectContent>
                            {clients.map(client => (
                              <SelectItem key={client.id} value={client.id.toString()}>
                                {client.name}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}
                  </div>

                  <div>
                    <Label htmlFor="notes">Notes (Optional)</Label>
                    <Textarea
                      id="notes"
                      value={editableData.notes}
                      onChange={(e) => setEditableData(prev => ({ ...prev, notes: e.target.value }))}
                      placeholder="Add any additional notes..."
                      rows={3}
                    />
                  </div>

                  <div className="flex justify-end gap-3 pt-4">
                    <Button type="button" variant="outline" onClick={handleClose} disabled={isSaving}>
                      Cancel
                    </Button>
                    <Button type="submit" disabled={isSaving}>
                      {isSaving ? (
                        <>
                          <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                          Saving...
                        </>
                      ) : (
                        'Save Receipt'
                      )}
                    </Button>
                  </div>
                </form>
              )}
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

src/lib/hooks/useBookings.ts.content:
import { useState, useEffect, useCallback } from 'react';
import { useToast } from '@/hooks/use-toast';

export interface Booking {
  id: number;
  clientId: number;
  userId: string;
  title: string;
  date: string;
  time: string;
  duration: number;
  status: 'scheduled' | 'completed' | 'cancelled' | 'rescheduled';
  notes?: string;
  createdAt: string;
  updatedAt: string;
  clientName?: string;
  clientEmail?: string;
}

interface UseBookingsOptions {
  startDate?: string;
  endDate?: string;
  status?: string;
  clientId?: number;
}

export function useBookings(options: UseBookingsOptions = {}) {
  const [bookings, setBookings] = useState<Booking[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const fetchBookings = useCallback(async () => {
    try {
      setIsLoading(true);
      setError(null);

      const params = new URLSearchParams();
      if (options.startDate) params.append('startDate', options.startDate);
      if (options.endDate) params.append('endDate', options.endDate);
      if (options.status) params.append('status', options.status);
      if (options.clientId) params.append('clientId', options.clientId.toString());

      const response = await fetch(`/api/lumenr/bookings?${params.toString()}`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch bookings');
      }

      const data = await response.json();
      setBookings(data.data || []);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch bookings';
      setError(errorMessage);
      toast({
        title: 'Error',
        description: errorMessage,
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  }, [options.startDate, options.endDate, options.status, options.clientId, toast]);

  useEffect(() => {
    fetchBookings();
  }, [fetchBookings]);

  const createBooking = async (data: Omit<Booking, 'id' | 'userId' | 'createdAt' | 'updatedAt' | 'clientName' | 'clientEmail'>) => {
    try {
      const response = await fetch('/api/lumenr/bookings', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create booking');
      }

      const result = await response.json();

      toast({
        title: 'Success',
        description: 'Booking created successfully',
      });

      await fetchBookings();
      return result.data;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to create booking';
      toast({
        title: 'Error',
        description: errorMessage,
        variant: 'destructive',
      });
      throw err;
    }
  };

  const updateBooking = async (id: number, data: Partial<Omit<Booking, 'id' | 'userId' | 'createdAt' | 'updatedAt' | 'clientName' | 'clientEmail'>>) => {
    try {
      const response = await fetch(`/api/lumenr/bookings?id=${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to update booking');
      }

      const result = await response.json();

      toast({
        title: 'Success',
        description: 'Booking updated successfully',
      });

      await fetchBookings();
      return result.data;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to update booking';
      toast({
        title: 'Error',
        description: errorMessage,
        variant: 'destructive',
      });
      throw err;
    }
  };

  const deleteBooking = async (id: number) => {
    try {
      const response = await fetch(`/api/lumenr/bookings?id=${id}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to delete booking');
      }

      toast({
        title: 'Success',
        description: 'Booking deleted successfully',
      });

      await fetchBookings();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to delete booking';
      toast({
        title: 'Error',
        description: errorMessage,
        variant: 'destructive',
      });
      throw err;
    }
  };

  return {
    bookings,
    isLoading,
    error,
    refetch: fetchBookings,
    createBooking,
    updateBooking,
    deleteBooking,
  };
}

