'use client';

import { useState, useEffect, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Badge } from '@/components/ui/badge';
import { Switch } from '@/components/ui/switch';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Plus, Edit, Trash2, Clock, DollarSign, Upload, Download, FileSpreadsheet } from 'lucide-react';
import { toast } from 'sonner';
import { motion } from 'framer-motion';
import { 
  generateServiceTemplate, 
  parseServiceExcel, 
  exportServicesToExcel 
} from '@/lib/utils/service-excel-import';

interface Service {
  id: number;
  name: string;
  description: string | null;
  unitPrice: string;
  currency: string;
  category: string | null;
  duration: number | null;
  active: boolean;
  userId: string;
  createdAt: string;
  updatedAt: string;
}

const SERVICE_CATEGORIES = [
  'Consulting',
  'Design',
  'Development',
  'Marketing',
  'Writing',
  'Photography',
  'Video Production',
  'Training',
  'Support',
  'Other',
];

export default function ServicesPage() {
  const [services, setServices] = useState<Service[]>([]);
  const [filteredServices, setFilteredServices] = useState<Service[]>([]);
  const [loading, setLoading] = useState(true);
  const [isCreateDialogOpen, setIsCreateDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingService, setEditingService] = useState<Service | null>(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [categoryFilter, setCategoryFilter] = useState<string>('all');
  const [isImportDialogOpen, setIsImportDialogOpen] = useState(false);
  const [importFile, setImportFile] = useState<File | null>(null);
  const [importing, setImporting] = useState(false);
  const [importResult, setImportResult] = useState<any>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const [newService, setNewService] = useState({
    name: '',
    description: '',
    unitPrice: '',
    currency: 'USD',
    category: '',
    duration: '',
    active: true,
  });

  useEffect(() => {
    fetchServices();
  }, []);

  useEffect(() => {
    let filtered = services;

    if (searchQuery) {
      filtered = filtered.filter(
        (service) =>
          service.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
          service.description?.toLowerCase().includes(searchQuery.toLowerCase())
      );
    }

    if (categoryFilter !== 'all') {
      filtered = filtered.filter((service) => service.category === categoryFilter);
    }

    setFilteredServices(filtered);
  }, [services, searchQuery, categoryFilter]);

  const fetchServices = async () => {
    try {
      const token = localStorage.getItem('bearer_token');
      const response = await fetch('/api/lumenr/services', {
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch services');
      }

      const data = await response.json();
      const servicesList = Array.isArray(data) ? data : data.data || [];
      setServices(servicesList);
    } catch (error: any) {
      toast.error(error.message || 'Failed to fetch services');
      setServices([]);
    } finally {
      setLoading(false);
    }
  };

  const createService = async (e: React.FormEvent) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem('bearer_token');
      const response = await fetch('/api/lumenr/services', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({
          ...newService,
          unitPrice: parseFloat(newService.unitPrice),
          duration: newService.duration ? parseInt(newService.duration) : null,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to create service');
      }

      toast.success('Service created successfully');
      setIsCreateDialogOpen(false);
      setNewService({
        name: '',
        description: '',
        unitPrice: '',
        currency: 'USD',
        category: '',
        duration: '',
        active: true,
      });
      fetchServices();
    } catch (error: any) {
      toast.error(error.message || 'Failed to create service');
    }
  };

  const updateService = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingService) return;

    try {
      const token = localStorage.getItem('bearer_token');
      const response = await fetch(`/api/lumenr/services?id=${editingService.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(editingService),
      });

      if (!response.ok) {
        throw new Error('Failed to update service');
      }

      toast.success('Service updated successfully');
      setIsEditDialogOpen(false);
      setEditingService(null);
      fetchServices();
    } catch (error: any) {
      toast.error(error.message || 'Failed to update service');
    }
  };

  const deleteService = async (id: number) => {
    if (!confirm('Are you sure you want to delete this service?')) return;

    try {
      const token = localStorage.getItem('bearer_token');
      const response = await fetch(`/api/lumenr/services?id=${id}`, {
        method: 'DELETE',
        headers: { Authorization: `Bearer ${token}` },
      });

      if (!response.ok) {
        throw new Error('Failed to delete service');
      }

      toast.success('Service deleted successfully');
      fetchServices();
    } catch (error: any) {
      toast.error(error.message || 'Failed to delete service');
    }
  };

  const openEditDialog = (service: Service) => {
    setEditingService(service);
    setIsEditDialogOpen(true);
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      setImportFile(file);
      setImportResult(null);
    }
  };

  const handleImport = async () => {
    if (!importFile) {
      toast.error('Please select a file to import');
      return;
    }

    setImporting(true);
    setImportResult(null);

    try {
      const result = await parseServiceExcel(importFile);

      if (!result.success) {
        setImportResult({ success: false, errors: result.errors });
        toast.error('Import failed. Please check the errors below.');
        return;
      }

      const token = localStorage.getItem('bearer_token');
      const response = await fetch('/api/lumenr/services/bulk-import', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({ services: result.data })
      });

      if (!response.ok) throw new Error('Failed to import services');

      const importData = await response.json();
      setImportResult({ success: true, imported: importData.imported, errors: importData.errors });
      toast.success(`Successfully imported ${importData.imported} services`);
      fetchServices();
      setImportFile(null);
      if (fileInputRef.current) fileInputRef.current.value = '';
    } catch (error: any) {
      toast.error(error.message || 'Failed to import services');
      setImportResult({ success: false, errors: [error.message] });
    } finally {
      setImporting(false);
    }
  };

  const handleExport = () => {
    if (services.length === 0) {
      toast.error('No services to export');
      return;
    }
    exportServicesToExcel(services);
    toast.success('Services exported successfully');
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary" />
      </div>
    );
  }

  const activeServices = services.filter((s) => s.active).length;
  const avgPrice =
    services.length > 0
      ? services.reduce((sum, s) => sum + parseFloat(s.unitPrice), 0) / services.length
      : 0;

  return (
    <div className="p-6 space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground">Services</h1>
          <p className="text-muted-foreground text-sm sm:text-base">
            Manage your service catalog
          </p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={handleExport}>
            <Download className="h-4 w-4 mr-2" />
            Export
          </Button>
          <Dialog open={isImportDialogOpen} onOpenChange={setIsImportDialogOpen}>
            <DialogTrigger asChild>
              <Button variant="outline">
                <Upload className="h-4 w-4 mr-2" />
                Import
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>Import Services from Excel</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="space-y-2">
                  <p className="text-sm text-muted-foreground">
                    Download the template, fill in your service data, and upload it here.
                  </p>
                  <Button 
                    variant="outline" 
                    onClick={() => {
                      generateServiceTemplate();
                      toast.success('Template downloaded');
                    }}
                    className="w-full"
                  >
                    <FileSpreadsheet className="h-4 w-4 mr-2" />
                    Download Template
                  </Button>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="file-upload">Select Excel File</Label>
                  <Input
                    id="file-upload"
                    type="file"
                    accept=".xlsx,.xls"
                    ref={fileInputRef}
                    onChange={handleFileSelect}
                  />
                </div>
                {importResult && (
                  <div className={`p-4 rounded-md ${importResult.success ? 'bg-green-50 dark:bg-green-900/20 border border-green-200' : 'bg-red-50 dark:bg-red-900/20 border border-red-200'}`}>
                    <p className={`font-semibold ${importResult.success ? 'text-green-800 dark:text-green-200' : 'text-red-800 dark:text-red-200'}`}>
                      {importResult.success ? `Successfully imported ${importResult.imported} services!` : 'Import failed'}
                    </p>
                    {importResult.errors && importResult.errors.length > 0 && (
                      <ul className="mt-2 text-sm text-red-700 dark:text-red-300 list-disc list-inside max-h-32 overflow-y-auto">
                        {importResult.errors.map((error: any, index: number) => (
                          <li key={index}>{typeof error === 'string' ? error : `Row ${error.row}: ${error.error}`}</li>
                        ))}
                      </ul>
                    )}
                  </div>
                )}
                <div className="flex justify-end gap-2">
                  <Button variant="outline" onClick={() => {
                    setIsImportDialogOpen(false);
                    setImportFile(null);
                    setImportResult(null);
                  }}>
                    Cancel
                  </Button>
                  <Button onClick={handleImport} disabled={!importFile || importing}>
                    {importing ? 'Importing...' : 'Import Services'}
                  </Button>
                </div>
              </div>
            </DialogContent>
          </Dialog>
          <Button onClick={() => setIsCreateDialogOpen(true)}>
            <Plus className="h-4 w-4 mr-2" />
            Add Service
          </Button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Services</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{services.length}</div>
            <p className="text-xs text-muted-foreground">{activeServices} active</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Average Rate</CardTitle>
            <DollarSign className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">${avgPrice.toFixed(2)}</div>
            <p className="text-xs text-muted-foreground">Per hour/unit</p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Categories</CardTitle>
            <Clock className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {new Set(services.map((s) => s.category).filter(Boolean)).size}
            </div>
            <p className="text-xs text-muted-foreground">Unique categories</p>
          </CardContent>
        </Card>
      </div>

      <div className="flex flex-col sm:flex-row gap-4">
        <Input
          placeholder="Search services..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="sm:max-w-xs"
        />
        <Select value={categoryFilter} onValueChange={setCategoryFilter}>
          <SelectTrigger className="sm:max-w-xs">
            <SelectValue placeholder="Filter by category" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="all">All Categories</SelectItem>
            {SERVICE_CATEGORIES.map((category) => (
              <SelectItem key={category} value={category}>
                {category}
              </SelectItem>
            ))}
          </SelectContent>
        </Select>
      </div>

      {filteredServices.length === 0 ? (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <p className="text-muted-foreground mb-4">No services found</p>
            <Button onClick={() => setIsCreateDialogOpen(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Create Your First Service
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredServices.map((service, index) => (
            <motion.div
              key={service.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.05 }}
            >
              <Card className="h-full hover:shadow-lg transition-shadow">
                <CardHeader>
                  <div className="flex justify-between items-start">
                    <div className="flex-1">
                      <CardTitle className="text-lg">{service.name}</CardTitle>
                      {service.category && (
                        <Badge variant="outline" className="mt-2">
                          {service.category}
                        </Badge>
                      )}
                    </div>
                    <div className="flex gap-2">
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => openEditDialog(service)}
                      >
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button
                        variant="ghost"
                        size="icon"
                        onClick={() => deleteService(service.id)}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                </CardHeader>
                <CardContent className="space-y-3">
                  {service.description && (
                    <p className="text-sm text-muted-foreground line-clamp-2">
                      {service.description}
                    </p>
                  )}
                  <div className="flex justify-between items-center">
                    <div>
                      <p className="text-2xl font-bold">
                        ${Number(service.unitPrice || 0).toFixed(2)}
                      </p>
                      <p className="text-xs text-muted-foreground">{service.currency}</p>
                    </div>
                    {service.duration && (
                      <div className="flex items-center gap-1 text-muted-foreground">
                        <Clock className="h-4 w-4" />
                        <span className="text-sm">{service.duration} min</span>
                      </div>
                    )}
                  </div>
                  <div className="flex items-center justify-between">
                    <Badge variant={service.active ? 'default' : 'secondary'}>
                      {service.active ? 'Active' : 'Inactive'}
                    </Badge>
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>
      )}

      <Dialog open={isCreateDialogOpen} onOpenChange={setIsCreateDialogOpen}>
        <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Create New Service</DialogTitle>
          </DialogHeader>
          <form onSubmit={createService} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div className="space-y-2 col-span-2">
                <Label htmlFor="name">Service Name *</Label>
                <Input
                  id="name"
                  value={newService.name}
                  onChange={(e) => setNewService({ ...newService, name: e.target.value })}
                  required
                />
              </div>
              <div className="space-y-2 col-span-2">
                <Label htmlFor="description">Description</Label>
                <Textarea
                  id="description"
                  value={newService.description}
                  onChange={(e) =>
                    setNewService({ ...newService, description: e.target.value })
                  }
                  rows={3}
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="unitPrice">Hourly Rate *</Label>
                <Input
                  id="unitPrice"
                  type="number"
                  step="0.01"
                  min="0"
                  value={newService.unitPrice}
                  onChange={(e) =>
                    setNewService({ ...newService, unitPrice: e.target.value })
                  }
                  required
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="category">Category</Label>
                <Select
                  value={newService.category}
                  onValueChange={(value) => setNewService({ ...newService, category: value })}
                >
                  <SelectTrigger>
                    <SelectValue placeholder="Select category" />
                  </SelectTrigger>
                  <SelectContent>
                    {SERVICE_CATEGORIES.map((category) => (
                      <SelectItem key={category} value={category}>
                        {category}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
              <div className="space-y-2">
                <Label htmlFor="duration">Duration (minutes)</Label>
                <Input
                  id="duration"
                  type="number"
                  min="0"
                  value={newService.duration}
                  onChange={(e) =>
                    setNewService({ ...newService, duration: e.target.value })
                  }
                  placeholder="e.g., 60"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="currency">Currency</Label>
                <Select
                  value={newService.currency}
                  onValueChange={(value) => setNewService({ ...newService, currency: value })}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="USD">USD</SelectItem>
                    <SelectItem value="EUR">EUR</SelectItem>
                    <SelectItem value="GBP">GBP</SelectItem>
                    <SelectItem value="CAD">CAD</SelectItem>
                  </SelectContent>
                </Select>
              </div>
              <div className="flex items-center space-x-2 col-span-2">
                <Switch
                  checked={newService.active}
                  onCheckedChange={(checked) =>
                    setNewService({ ...newService, active: checked })
                  }
                />
                <Label>Active (visible to customers)</Label>
              </div>
            </div>

            <div className="flex justify-end gap-2">
              <Button
                type="button"
                variant="outline"
                onClick={() => setIsCreateDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button type="submit">Create Service</Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {editingService && (
        <Dialog open={isEditDialogOpen} onOpenChange={setIsEditDialogOpen}>
          <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
            <DialogHeader>
              <DialogTitle>Edit Service</DialogTitle>
            </DialogHeader>
            <form onSubmit={updateService} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2 col-span-2">
                  <Label htmlFor="edit-name">Service Name *</Label>
                  <Input
                    id="edit-name"
                    value={editingService.name}
                    onChange={(e) =>
                      setEditingService({ ...editingService, name: e.target.value })
                    }
                    required
                  />
                </div>
                <div className="space-y-2 col-span-2">
                  <Label htmlFor="edit-description">Description</Label>
                  <Textarea
                    id="edit-description"
                    value={editingService.description || ''}
                    onChange={(e) =>
                      setEditingService({ ...editingService, description: e.target.value })
                    }
                    rows={3}
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-unitPrice">Hourly Rate *</Label>
                  <Input
                    id="edit-unitPrice"
                    type="number"
                    step="0.01"
                    min="0"
                    value={editingService.unitPrice}
                    onChange={(e) =>
                      setEditingService({ ...editingService, unitPrice: e.target.value })
                    }
                    required
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-category">Category</Label>
                  <Select
                    value={editingService.category || ''}
                    onValueChange={(value) =>
                      setEditingService({ ...editingService, category: value })
                    }
                  >
                    <SelectTrigger>
                      <SelectValue placeholder="Select category" />
                    </SelectTrigger>
                    <SelectContent>
                      {SERVICE_CATEGORIES.map((category) => (
                        <SelectItem key={category} value={category}>
                          {category}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-duration">Duration (minutes)</Label>
                  <Input
                    id="edit-duration"
                    type="number"
                    min="0"
                    value={editingService.duration || ''}
                    onChange={(e) =>
                      setEditingService({
                        ...editingService,
                        duration: e.target.value ? parseInt(e.target.value) : null,
                      })
                    }
                    placeholder="e.g., 60"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="edit-currency">Currency</Label>
                  <Select
                    value={editingService.currency}
                    onValueChange={(value) =>
                      setEditingService({ ...editingService, currency: value })
                    }
                  >
                    <SelectTrigger>
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="USD">USD</SelectItem>
                      <SelectItem value="EUR">EUR</SelectItem>
                      <SelectItem value="GBP">GBP</SelectItem>
                      <SelectItem value="CAD">CAD</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex items-center space-x-2 col-span-2">
                  <Switch
                    checked={editingService.active}
                    onCheckedChange={(checked) =>
                      setEditingService({ ...editingService, active: checked })
                    }
                  />
                  <Label>Active (visible to customers)</Label>
                </div>
              </div>

              <div className="flex justify-end gap-2">
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => setIsEditDialogOpen(false)}
                >
                  Cancel
                </Button>
                <Button type="submit">Update Service</Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      )}
    </div>
  );
}

src/components/Receipts/ReceiptDetailMod.content:
'use client';

import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Badge } from '@/components/ui/badge';
import { Receipt, Calendar, DollarSign, User, FileText, Image as ImageIcon, Download, Upload, Loader2 } from 'lucide-react';
import { useState, useRef } from 'react';
import { Button } from '@/components/ui/button';
import { toast } from 'sonner';
import { uploadReceiptImage } from '@/lib/receipt-storage';
import { useAuth } from '@/contexts/AuthContext';

interface ReceiptDetailModalProps {
  receipt: {
    id: number;
    vendor: string;
    category: string;
    amount: number;
    currency: string;
    date: string;
    notes: string | null;
    type: 'expense' | 'client';
    clientId: number | null;
    imageUrl: string | null;
    createdAt: string;
    updatedAt: string;
  };
  clientName?: string | null;
  isOpen: boolean;
  onClose: () => void;
  onUpdate?: () => void;
}

export default function ReceiptDetailModal({ receipt, clientName, isOpen, onClose, onUpdate }: ReceiptDetailModalProps) {
  const { user } = useAuth();
  const [imageError, setImageError] = useState(false);
  const [imageLoading, setImageLoading] = useState(true);
  const [isUploading, setIsUploading] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const getCategoryColor = (category: string) => {
    const colors: { [key: string]: string } = {
      'Office Supplies': 'bg-blue-500',
      'Travel': 'bg-purple-500',
      'Meals': 'bg-orange-500',
      'Software': 'bg-cyan-500',
      'Marketing': 'bg-pink-500',
      'Utilities': 'bg-green-500',
      'Other': 'bg-gray-500'
    };
    return colors[category] || 'bg-gray-500';
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleDownload = async () => {
    if (!receipt.imageUrl) return;

    try {
      const response = await fetch(receipt.imageUrl);

      if (!response.ok) {
        throw new Error('Failed to fetch image');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = `receipt-${receipt.vendor}-${receipt.date}.jpg`;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
      toast.success('Receipt image downloaded');
    } catch (error) {
      toast.error('Failed to download image');
      console.error('Download error:', error);
    }
  };

  const handleFileSelect = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Validate file type
    if (!file.type.startsWith('image/')) {
      toast.error('Please select an image file');
      return;
    }

    // Validate file size
    if (file.size > 10 * 1024 * 1024) {
      toast.error('Image must be less than 10MB');
      return;
    }

    // Validate authentication
    const token = localStorage.getItem('bearer_token');
    if (!token) {
      toast.error('Authentication required. Please log in again.');
      return;
    }

    if (!user?.id) {
      toast.error('User not authenticated');
      return;
    }

    // All validations passed, start upload
    setIsUploading(true);
    const toastId = toast.loading('Uploading receipt image...');

    try {
      const userId = user.id;

      const uploadResult = await uploadReceiptImage({
        file: file,
        userId: userId,
        fileName: file.name
      });

      if (!uploadResult.success) {
        toast.dismiss(toastId);
        toast.error(uploadResult.error || 'Failed to upload image');
        return;
      }

      const imageUrl = uploadResult.publicUrl || null;

      const response = await fetch(`/api/lumenr/receipts?id=${receipt.id}`, {
        method: 'PUT',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({
          imageUrl: imageUrl
        })
      });

      if (!response.ok) {
        throw new Error('Failed to update receipt');
      }

      toast.dismiss(toastId);
      toast.success('Receipt image updated successfully!');
      setImageError(false);
      setImageLoading(true);

      if (onUpdate) {
        onUpdate();
      }

      onClose();

    } catch (error: any) {
      toast.dismiss(toastId);
      toast.error(error.message || 'Failed to update receipt image');
    } finally {
      setIsUploading(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2 text-xl">
            <Receipt className="w-6 h-6 text-primary" />
            Receipt Details
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {receipt.imageUrl && !imageError ? (
            <div className="space-y-3">
              <div className="relative rounded-lg overflow-hidden border border-border bg-muted">
                {imageLoading && (
                  <div className="absolute inset-0 flex items-center justify-center bg-muted">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                  </div>
                )}
                <img
                  src={receipt.imageUrl}
                  alt={`Receipt from ${receipt.vendor}`}
                  className="w-full max-h-[400px] object-contain"
                  onLoad={() => setImageLoading(false)}
                  onError={() => {
                    setImageError(true);
                    setImageLoading(false);
                  }}
                />
              </div>
              <div className="flex gap-2">
                <Button 
                  onClick={handleDownload} 
                  variant="outline" 
                  size="sm"
                  className="flex-1"
                >
                  <Download className="w-4 h-4 mr-2" />
                  Download Image
                </Button>
                <Button 
                  onClick={() => fileInputRef.current?.click()} 
                  variant="outline" 
                  size="sm"
                  className="flex-1"
                  disabled={isUploading}
                >
                  {isUploading ? (
                    <>
                      <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                      Uploading...
                    </>
                  ) : (
                    <>
                      <Upload className="w-4 h-4 mr-2" />
                      Update Image
                    </>
                  )}
                </Button>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept="image/*"
                  onChange={handleFileSelect}
                  className="hidden"
                />
              </div>
            </div>
          ) : (
            <div className="space-y-3">
              <div className="rounded-lg border border-dashed border-border p-12 text-center bg-muted/50">
                <ImageIcon className="w-12 h-12 mx-auto mb-4 text-muted-foreground" />
                <p className="text-sm text-muted-foreground mb-4">
                  {imageError ? 'Image could not be loaded' : 'No image attached'}
                </p>
              </div>
              <Button 
                onClick={() => fileInputRef.current?.click()} 
                variant="outline" 
                size="sm"
                className="w-full"
                disabled={isUploading}
              >
                {isUploading ? (
                  <>
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                    Uploading...
                  </>
                ) : (
                  <>
                    <Upload className="w-4 h-4 mr-2" />
                    Attach Image
                  </>
                )}
              </Button>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleFileSelect}
                className="hidden"
              />
            </div>
          )}

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Vendor</label>
                <div className="flex items-center gap-2">
                  <Receipt className="w-4 h-4 text-muted-foreground" />
                  <span className="text-lg font-semibold">{receipt.vendor}</span>
                </div>
              </div>

              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Amount</label>
                <div className="flex items-center gap-2">
                  <DollarSign className="w-4 h-4 text-muted-foreground" />
                  <span className="text-2xl font-bold">
                    {Number(receipt.amount || 0).toFixed(2)} {receipt.currency}
                  </span>
                </div>
              </div>

              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Date</label>
                <div className="flex items-center gap-2">
                  <Calendar className="w-4 h-4 text-muted-foreground" />
                  <span className="text-base">{formatDate(receipt.date)}</span>
                </div>
              </div>
            </div>

            <div className="space-y-4">
              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Category</label>
                <Badge className={`${getCategoryColor(receipt.category)} text-white`}>
                  {receipt.category}
                </Badge>
              </div>

              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Type</label>
                <Badge variant="outline">
                  {receipt.type === 'expense' ? 'My Expense' : 'Client Receipt'}
                </Badge>
              </div>

              {receipt.type === 'client' && clientName && (
                <div>
                  <label className="text-sm font-medium text-muted-foreground mb-1 block">Client</label>
                  <div className="flex items-center gap-2">
                    <User className="w-4 h-4 text-muted-foreground" />
                    <span className="text-base font-medium">{clientName}</span>
                  </div>
                </div>
              )}

              <div>
                <label className="text-sm font-medium text-muted-foreground mb-1 block">Currency</label>
                <span className="text-base">{receipt.currency}</span>
              </div>
            </div>
          </div>

          {receipt.notes && (
            <div className="pt-4 border-t">
              <label className="text-sm font-medium text-muted-foreground mb-2 flex items-center gap-2">
                <FileText className="w-4 h-4" />
                Notes
              </label>
              <p className="text-base text-foreground whitespace-pre-wrap bg-muted/50 p-4 rounded-lg">
                {receipt.notes}
              </p>
            </div>
          )}

          <div className="pt-4 border-t text-xs text-muted-foreground space-y-1">
            <p>Created: {formatDate(receipt.createdAt)}</p>
            <p>Last updated: {formatDate(receipt.updatedAt)}</p>
            <p>Receipt ID: #{receipt.id}</p>
          </div>

          <div className="flex justify-end gap-3 pt-4">
            <Button onClick={onClose} variant="outline">
              Close
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

src/lib/receipt-storage.ts.content:
import { supabase } from '@/integrations/supabase/client';

const RECEIPT_BUCKET = 'receipts';
const ALLOWED_IMAGE_TYPES = ['image/jpeg', 'image/jpg', 'image/png', 'image/webp', 'image/heic'];
const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB

export interface UploadReceiptImageOptions {
  file: File | Blob;
  userId: string;
  fileName?: string;
}

export interface UploadReceiptImageResult {
  success: boolean;
  path?: string;
  publicUrl?: string;
  error?: string;
}

export async function uploadReceiptImage({
  file,
  userId,
  fileName
}: UploadReceiptImageOptions): Promise<UploadReceiptImageResult> {
  try {
    console.log('[Receipt Storage] Starting upload...', {
      fileType: file.type,
      fileSize: file.size,
      fileName
    });

    if (!supabase) {
      console.error('[Receipt Storage] Supabase client not available');
      return {
        success: false,
        error: 'Supabase client not available'
      };
    }

    // Validate file type
    if (!ALLOWED_IMAGE_TYPES.includes(file.type)) {
      console.error('[Receipt Storage] Invalid file type:', file.type);
      return {
        success: false,
        error: `Invalid file type. Allowed types: ${ALLOWED_IMAGE_TYPES.join(', ')}`
      };
    }

    // Validate file size
    if (file.size > MAX_FILE_SIZE) {
      console.error('[Receipt Storage] File too large:', file.size);
      return {
        success: false,
        error: `File too large. Maximum size: ${MAX_FILE_SIZE / 1024 / 1024}MB`
      };
    }

    // Create unique filename
    const timestamp = Date.now();
    const randomString = Math.random().toString(36).substring(2, 15);
    const fileExt = fileName?.split('.').pop() || 'jpg';
    const uniqueFileName = `${timestamp}-${randomString}.${fileExt}`;

    // Organize by user ID
    const filePath = `${userId}/${uniqueFileName}`;
    console.log('[Receipt Storage] Uploading to path:', filePath);

    // Upload to Supabase Storage (file is already a Blob/File, no need to convert)
    const { data, error } = await supabase.storage
      .from(RECEIPT_BUCKET)
      .upload(filePath, file, {
        contentType: file.type,
        cacheControl: '3600',
        upsert: false
      });

    if (error) {
      console.error('[Receipt Storage] Upload error:', error);
      return {
        success: false,
        error: error.message
      };
    }

    console.log('[Receipt Storage] Upload successful, generating signed URL...');

    // Generate a signed URL (valid for 1 year)
    const { data: signedUrlData, error: signedUrlError } = await supabase.storage
      .from(RECEIPT_BUCKET)
      .createSignedUrl(data.path, 31536000); // 1 year in seconds

    if (signedUrlError) {
      console.error('[Receipt Storage] Signed URL error:', signedUrlError);
      // Fall back to just returning the path
      return {
        success: true,
        path: data.path,
        publicUrl: null
      };
    }

    console.log('[Receipt Storage] Signed URL obtained');

    return {
      success: true,
      path: data.path,
      publicUrl: signedUrlData.signedUrl
    };

  } catch (error) {
    console.error('[Receipt Storage] Upload exception:', error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Failed to upload receipt image'
    };
  }
}

export async function deleteReceiptImage(path: string): Promise<boolean> {
  try {
    if (!supabase) {
      console.error('Supabase client not available');
      return false;
    }

    const { error } = await supabase.storage
      .from(RECEIPT_BUCKET)
      .remove([path]);

    if (error) {
      console.error('Receipt image delete error:', error);
      return false;
    }

    return true;
  } catch (error) {
    console.error('Receipt image delete exception:', error);
    return false;
  }
}

export async function getReceiptImageSignedUrl(
  path: string,
  expiresIn: number = 3600
): Promise<string | null> {
  try {
    if (!supabase) {
      console.error('Supabase client not available');
      return null;
    }

    const { data, error } = await supabase.storage
      .from(RECEIPT_BUCKET)
      .createSignedUrl(path, expiresIn);

    if (error) {
      console.error('Receipt image signed URL error:', error);
      return null;
    }

    return data.signedUrl;
  } catch (error) {
    console.error('Receipt image signed URL exception:', error);
    return null;
  }
}

src/components/LineItems/LineItemsEditor.content:
'use client';

import { useState, useEffect } from 'react';
import { Plus, Trash2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';

interface LineItem {
  id: string;
  type: 'product' | 'service';
  itemId: number | null;
  name: string;
  description: string;
  quantity: number;
  price: number;
  total: number;
}

interface Product {
  id: number;
  name: string;
  description: string | null;
  price: string;
  active: boolean;
}

interface Service {
  id: number;
  name: string;
  description: string | null;
  unitPrice: string;
  active: boolean;
}

interface LineItemsEditorProps {
  items: LineItem[];
  onChange: (items: LineItem[]) => void;
  currency?: string;
}

export default function LineItemsEditor({ items, onChange, currency = 'USD' }: LineItemsEditorProps) {
  const [products, setProducts] = useState<Product[]>([]);
  const [services, setServices] = useState<Service[]>([]);

  useEffect(() => {
    fetchProductsAndServices();
  }, []);

  const fetchProductsAndServices = async () => {
    try {
      const token = localStorage.getItem('bearer_token');

      const [productsRes, servicesRes] = await Promise.all([
        fetch('/api/lumenr/products', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('/api/lumenr/services', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (productsRes.ok) {
        const result = await productsRes.json();
        const data = result.data || result;
        setProducts(Array.isArray(data) ? data.filter(p => p.active) : []);
      }

      if (servicesRes.ok) {
        const result = await servicesRes.json();
        const data = result.data || result;
        setServices(Array.isArray(data) ? data.filter(s => s.active) : []);
      }
    } catch (error) {
      console.error('Failed to fetch products/services:', error);
    }
  };

  const addLineItem = () => {
    const newItem: LineItem = {
      id: `item-${Date.now()}`,
      type: 'product',
      itemId: null,
      name: '',
      description: '',
      quantity: 1,
      price: 0,
      total: 0
    };
    onChange([...items, newItem]);
  };

  const removeLineItem = (id: string) => {
    onChange(items.filter(item => item.id !== id));
  };

  const updateLineItem = (id: string, field: keyof LineItem, value: any) => {
    const updatedItems = items.map(item => {
      if (item.id !== id) return item;

      const updated = { ...item, [field]: value };

      if (field === 'type') {
        updated.itemId = null;
        updated.name = '';
        updated.description = '';
        updated.price = 0;
        updated.total = 0;
      }

      if (field === 'itemId') {
        if (updated.type === 'product') {
          const product = products.find(p => p.id === value);
          if (product) {
            updated.name = product.name;
            updated.description = product.description || '';
            updated.price = parseFloat(product.price) || 0;
          }
        } else if (updated.type === 'service') {
          const service = services.find(s => s.id === value);
          if (service) {
            updated.name = service.name;
            updated.description = service.description || '';
            updated.price = parseFloat(service.unitPrice) || 0;
          }
        }
      }

      if (field === 'quantity' || field === 'price' || field === 'itemId') {
        updated.total = updated.quantity * updated.price;
      }

      return updated;
    });

    onChange(updatedItems);
  };

  const getCurrencySymbol = (curr: string) => {
    const symbols: Record<string, string> = {
      USD: '$',
      EUR: 'â‚¬',
      GBP: 'Â£',
      CAD: 'C$',
      AUD: 'A$'
    };
    return symbols[curr] || curr;
  };

  const symbol = getCurrencySymbol(currency);

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span>Line Items</span>
          <Button type="button" onClick={addLineItem} size="sm">
            <Plus className="h-4 w-4 mr-1" />
            Add Item
          </Button>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-4">
        {items.length === 0 && (
          <p className="text-muted-foreground text-sm text-center py-4">
            No items added yet. Click "Add Item" to get started.
          </p>
        )}

        {items.map((item, index) => (
          <div key={item.id} className="p-4 border rounded-lg space-y-3">
            <div className="flex items-center justify-between">
              <span className="text-sm font-medium">Item {index + 1}</span>
              <Button
                type="button"
                variant="ghost"
                size="sm"
                onClick={() => removeLineItem(item.id)}
              >
                <Trash2 className="h-4 w-4 text-destructive" />
              </Button>
            </div>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              <div>
                <Label>Type</Label>
                <Select
                  value={item.type}
                  onValueChange={(value) => updateLineItem(item.id, 'type', value as 'product' | 'service')}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="product">Product</SelectItem>
                    <SelectItem value="service">Service</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>{item.type === 'product' ? 'Product' : 'Service'}</Label>
                <Select
                  value={item.itemId?.toString() || ''}
                  onValueChange={(value) => updateLineItem(item.id, 'itemId', parseInt(value))}
                >
                  <SelectTrigger>
                    <SelectValue placeholder={`Select ${item.type}`} />
                  </SelectTrigger>
                  <SelectContent>
                    {item.type === 'product' ? (
                      products.length > 0 ? (
                        products.map(product => (
                          <SelectItem key={product.id} value={product.id.toString()}>
                            {product.name} ({symbol}{Number(product.price).toFixed(2)})
                          </SelectItem>
                        ))
                      ) : (
                        <SelectItem value="none" disabled>No active products</SelectItem>
                      )
                    ) : (
                      services.length > 0 ? (
                        services.map(service => (
                          <SelectItem key={service.id} value={service.id.toString()}>
                            {service.name} ({symbol}{Number(service.unitPrice).toFixed(2)})
                          </SelectItem>
                        ))
                      ) : (
                        <SelectItem value="none" disabled>No active services</SelectItem>
                      )
                    )}
                  </SelectContent>
                </Select>
              </div>
            </div>

            {item.name && (
              <div>
                <Label>Name</Label>
                <Input
                  value={item.name}
                  onChange={(e) => updateLineItem(item.id, 'name', e.target.value)}
                />
              </div>
            )}

            {item.description && (
              <div>
                <Label>Description</Label>
                <Input
                  value={item.description}
                  onChange={(e) => updateLineItem(item.id, 'description', e.target.value)}
                />
              </div>
            )}

            <div className="grid grid-cols-3 gap-3">
              <div>
                <Label>Quantity</Label>
                <Input
                  type="number"
                  min="1"
                  step="1"
                  value={item.quantity}
                  onChange={(e) => updateLineItem(item.id, 'quantity', parseInt(e.target.value) || 1)}
                />
              </div>

              <div>
                <Label>Price ({symbol})</Label>
                <Input
                  type="number"
                  min="0"
                  step="0.01"
                  value={item.price}
                  onChange={(e) => updateLineItem(item.id, 'price', parseFloat(e.target.value) || 0)}
                />
              </div>

              <div>
                <Label>Total ({symbol})</Label>
                <Input
                  type="text"
                  value={item.total.toFixed(2)}
                  disabled
                  className="bg-muted"
                />
              </div>
            </div>
          </div>
        ))}
      </CardContent>
    </Card>
  );
}

