    position: 'absolute',
    bottom: 30,
    left: 40,
    right: 40,
    textAlign: 'center',
    color: '#999',
    fontSize: 9,
    borderTopWidth: 1,
    borderTopColor: '#e5e7eb',
    paddingTop: 10,
  },
});

interface LineItem {
  name: string;
  description: string;
  quantity: number;
  price: number;
  total: number;
}

interface InvoiceData {
  invoiceNumber: string;
  date: string;
  dueDate?: string;
  status: string;
  clientName: string;
  clientEmail: string;
  clientCompany?: string;
  clientAddress?: string;
  businessName: string;
  businessAddress?: string;
  businessPhone?: string;
  businessEmail?: string;
  logoUrl?: string;
  items: LineItem[];
  subtotal: number;
  tax: number;
  total: number;
  notes?: string;
  paymentInstructions?: string;
  footer?: string;
}

const getStatusColor = (status: string) => {
  switch (status.toLowerCase()) {
    case 'paid': return { bg: '#dcfce7', text: '#166534' };
    case 'unpaid': return { bg: '#fef3c7', text: '#92400e' };
    case 'overdue': return { bg: '#fee2e2', text: '#991b1b' };
    case 'partially_paid': return { bg: '#dbeafe', text: '#1e40af' };
    default: return { bg: '#f3f4f6', text: '#374151' };
  }
};

export const InvoicePDF = ({ data }: { data: InvoiceData }) => {
  const statusColors = getStatusColor(data.status);

  return (
    <Document>
      <Page size="A4" style={styles.page}>
        {/* Header */}
        <View style={styles.header}>
          <View>
            {data.logoUrl && <Image src={data.logoUrl} style={styles.logo} />}
            <Text style={{ fontSize: 16, fontWeight: 'bold', marginTop: 10 }}>{data.businessName}</Text>
            {data.businessAddress && <Text style={{ fontSize: 9, color: '#666', marginTop: 5 }}>{data.businessAddress}</Text>}
            {data.businessPhone && <Text style={{ fontSize: 9, color: '#666' }}>{data.businessPhone}</Text>}
            {data.businessEmail && <Text style={{ fontSize: 9, color: '#666' }}>{data.businessEmail}</Text>}
          </View>
          <View>
            <Text style={styles.title}>INVOICE</Text>
            <Text style={styles.subtitle}>#{data.invoiceNumber}</Text>
            <View style={[styles.statusBadge, { backgroundColor: statusColors.bg }]}>
              <Text style={[styles.statusText, { color: statusColors.text }]}>{data.status}</Text>
            </View>
          </View>
        </View>

        {/* Invoice Info */}
        <View style={styles.section}>
          <View style={styles.row}>
            <Text style={styles.label}>Invoice Date:</Text>
            <Text style={styles.value}>{data.date}</Text>
          </View>
          {data.dueDate && (
            <View style={styles.row}>
              <Text style={styles.label}>Due Date:</Text>
              <Text style={[styles.value, { fontWeight: 'bold' }]}>{data.dueDate}</Text>
            </View>
          )}
        </View>

        {/* Client Info */}
        <View style={styles.section}>
          <Text style={styles.sectionTitle}>Bill To:</Text>
          <Text style={{ fontSize: 12, fontWeight: 'bold', marginBottom: 3 }}>{data.clientName}</Text>
          {data.clientCompany && <Text style={{ fontSize: 10, color: '#666' }}>{data.clientCompany}</Text>}
          <Text style={{ fontSize: 10, color: '#666' }}>{data.clientEmail}</Text>
          {data.clientAddress && <Text style={{ fontSize: 10, color: '#666', marginTop: 3 }}>{data.clientAddress}</Text>}
        </View>

        {/* Items Table */}
        <View style={styles.table}>
          <View style={styles.tableHeader}>
            <Text style={[styles.tableCol, styles.tableColDesc]}>Description</Text>
            <Text style={[styles.tableCol, styles.tableColQty]}>Qty</Text>
            <Text style={[styles.tableCol, styles.tableColPrice]}>Price</Text>
            <Text style={[styles.tableCol, styles.tableColTotal]}>Total</Text>
          </View>
          {data.items.map((item, index) => (
            <View key={index} style={styles.tableRow}>
              <View style={styles.tableColDesc}>
                <Text style={[styles.tableCol, { fontWeight: 'bold' }]}>{item.name}</Text>
                {item.description && <Text style={[styles.tableCol, { color: '#666', marginTop: 2 }]}>{item.description}</Text>}
              </View>
              <Text style={[styles.tableCol, styles.tableColQty]}>{item.quantity}</Text>
              <Text style={[styles.tableCol, styles.tableColPrice]}>${item.price.toFixed(2)}</Text>
              <Text style={[styles.tableCol, styles.tableColTotal]}>${item.total.toFixed(2)}</Text>
            </View>
          ))}
        </View>

        {/* Totals */}
        <View style={styles.totalsSection}>
          <View style={styles.totalRow}>
            <Text style={styles.totalLabel}>Subtotal:</Text>
            <Text style={styles.totalValue}>${data.subtotal.toFixed(2)}</Text>
          </View>
          {data.tax > 0 && (
            <View style={styles.totalRow}>
              <Text style={styles.totalLabel}>Tax:</Text>
              <Text style={styles.totalValue}>${data.tax.toFixed(2)}</Text>
            </View>
          )}
          <View style={styles.grandTotalRow}>
            <Text style={styles.grandTotalLabel}>Amount Due:</Text>
            <Text style={styles.grandTotalValue}>${data.total.toFixed(2)}</Text>
          </View>
        </View>

        {/* Payment Instructions */}
        {data.paymentInstructions && (
          <View style={styles.paymentInfo}>
            <Text style={styles.paymentTitle}>Payment Instructions:</Text>
            <Text style={styles.paymentText}>{data.paymentInstructions}</Text>
          </View>
        )}

        {/* Notes */}
        {data.notes && (
          <View style={styles.notes}>
            <Text style={styles.notesTitle}>Notes:</Text>
            <Text style={styles.notesText}>{data.notes}</Text>
          </View>
        )}

        {/* Footer */}
        <Text style={styles.footer}>
          {data.footer || 'Thank you for your business!'}
        </Text>
      </Page>
    </Document>
  );
};

src/components/Onboarding/OnboardingWiza.content:
'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useRouter } from 'next/navigation';
import { useAuth } from '@/contexts/AuthContext';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Textarea } from '@/components/ui/textarea';
import { Progress } from '@/components/ui/progress';
import { ArrowRight, ArrowLeft, MapPin, Building2, Settings, CheckCircle, Upload } from 'lucide-react';
import { toast } from 'sonner';
import { supabase } from '@/integrations/supabase/client';

const CURRENCIES = [
  { value: 'USD', label: 'US Dollar (USD)' },
  { value: 'EUR', label: 'Euro (EUR)' },
  { value: 'GBP', label: 'British Pound (GBP)' },
  { value: 'CAD', label: 'Canadian Dollar (CAD)' },
  { value: 'AUD', label: 'Australian Dollar (AUD)' },
  { value: 'JPY', label: 'Japanese Yen (JPY)' },
  { value: 'INR', label: 'Indian Rupee (INR)' }
];

const COUNTRIES = [
  'United States', 'Canada', 'United Kingdom', 'Australia', 'Germany', 
  'France', 'Spain', 'Italy', 'Netherlands', 'Belgium', 'Switzerland',
  'Austria', 'Sweden', 'Norway', 'Denmark', 'Finland', 'Ireland',
  'Portugal', 'Greece', 'Poland', 'Czech Republic', 'Japan', 'China',
  'India', 'Brazil', 'Mexico', 'Argentina', 'South Africa', 'Other'
];

interface OnboardingData {
  country: string;
  province: string;
  city: string;
  businessName: string;
  businessAddress: string;
  businessPhone: string;
  taxId: string;
  currency: string;
  taxRegion: string;
  paymentInstructions: string;
  invoiceFooter: string;
}

export function OnboardingWizard() {
  const router = useRouter();
  const { user } = useAuth();
  const [currentStep, setCurrentStep] = useState(1);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const [formData, setFormData] = useState<OnboardingData>({
    country: '',
    province: '',
    city: '',
    businessName: '',
    businessAddress: '',
    businessPhone: '',
    taxId: '',
    currency: 'USD',
    taxRegion: '',
    paymentInstructions: '',
    invoiceFooter: 'Thank you for your business!'
  });

  // Load saved progress from localStorage
  useEffect(() => {
    // Load pending business name
    const pendingBusinessName = localStorage.getItem('pending_business_name');
    if (pendingBusinessName) {
      setFormData(prev => ({ ...prev, businessName: pendingBusinessName }));
    }

    // Load saved progress
    const savedData = localStorage.getItem('lumenr-onboarding-progress');
    if (savedData) {
      try {
        const parsed = JSON.parse(savedData);
        setFormData(prev => ({ ...prev, ...parsed }));
      } catch (error) {
        console.error('Error loading onboarding progress:', error);
      }
    }
  }, []);

  // Save progress to localStorage whenever formData changes
  useEffect(() => {
    localStorage.setItem('lumenr-onboarding-progress', JSON.stringify(formData));
  }, [formData]);

  const updateField = (field: keyof OnboardingData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const progress = (currentStep / 3) * 100;

  const handleNext = () => {
    if (currentStep === 1 && (!formData.country || !formData.city)) {
      toast.error('Please fill in all location fields');
      return;
    }
    if (currentStep === 2 && !formData.businessName) {
      toast.error('Business name is required');
      return;
    }
    setCurrentStep(prev => Math.min(prev + 1, 3));
  };

  const handleBack = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
  };

  const handleComplete = async () => {
    if (!user) {
      toast.error('You must be logged in to complete onboarding');
      router.push('/login');
      return;
    }

    setIsSubmitting(true);

    try {
      // Create/update business profile
      const { error: profileError } = await supabase
        .from('business_profiles' as any)
        .upsert({
          user_id: user.id,
          business_name: formData.businessName,
          address: formData.businessAddress,
          phone: formData.businessPhone,
          tax_id: formData.taxId,
          currency: formData.currency,
          tax_region: formData.taxRegion,
          default_payment_instructions: formData.paymentInstructions,
          invoice_footer: formData.invoiceFooter,
          updated_at: new Date().toISOString()
        });

      if (profileError) {
        console.error('Error saving business profile:', profileError);
      }

      // Mark onboarding as complete
      localStorage.setItem('lumenr-onboarding-completed', 'true');
      localStorage.removeItem('lumenr-onboarding-progress');
      localStorage.removeItem('pending_business_name');

      try {
        await supabase
          .from('user_mode_settings' as any)
          .upsert({
            user_id: user.id,
            onboarding_completed: true,
            updated_at: new Date().toISOString()
          });
      } catch (error) {
        console.error('Error completing onboarding:', error);
      }

      toast.success('Welcome to LumenR!', {
        description: 'Your business profile has been set up successfully.'
      });

      router.push('/dashboard');
    } catch (error) {
      console.error('Error completing onboarding:', error);
      toast.error('Failed to save your information. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const steps = [
    {
      id: 1,
      title: 'Location',
      icon: MapPin,
      description: 'Where is your business located?'
    },
    {
      id: 2,
      title: 'Business Info',
      icon: Building2,
      description: 'Tell us about your business'
    },
    {
      id: 3,
      title: 'Preferences',
      icon: Settings,
      description: 'Configure your settings'
    }
  ];

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-50 via-blue-50 to-pink-50 dark:from-gray-900 dark:via-gray-800 dark:to-gray-900 p-4">
      <Card className="max-w-3xl w-full shadow-2xl">
        <CardHeader className="space-y-4">
          <div className="space-y-2">
            <h1 className="text-3xl font-bold text-center">Welcome to LumenR!</h1>
            <p className="text-muted-foreground text-center">Let's set up your business profile in 3 easy steps</p>
          </div>

          <div className="space-y-3">
            <Progress value={progress} className="h-2" />
            <div className="flex justify-between text-sm">
              {steps.map((step) => {
                const Icon = step.icon;
                return (
                  <div
                    key={step.id}
                    className={`flex items-center gap-2 ${
                      currentStep >= step.id ? 'text-purple-600 dark:text-purple-400' : 'text-muted-foreground'
                    }`}
                  >
                    {currentStep > step.id ? (
                      <CheckCircle className="h-5 w-5" />
                    ) : (
                      <Icon className="h-5 w-5" />
                    )}
                    <span className="hidden sm:inline">{step.title}</span>
                  </div>
                );
              })}
            </div>
          </div>
        </CardHeader>

        <CardContent className="pt-6">
          <AnimatePresence mode="wait">
            {currentStep === 1 && (
              <motion.div
                key="step1"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                className="space-y-6"
              >
                <div>
                  <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
                    <MapPin className="h-5 w-5 text-purple-600" />
                    Business Location
                  </h2>
                  <p className="text-sm text-muted-foreground mb-6">
                    This helps us calculate correct taxes and comply with local regulations.
                  </p>
                </div>

                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="country">Country *</Label>
                    <Select value={formData.country} onValueChange={(value) => updateField('country', value)}>
                      <SelectTrigger id="country">
                        <SelectValue placeholder="Select your country" />
                      </SelectTrigger>
                      <SelectContent>
                        {COUNTRIES.map(country => (
                          <SelectItem key={country} value={country}>{country}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="province">State / Province</Label>
                      <Input
                        id="province"
                        placeholder="e.g., California"
                        value={formData.province}
                        onChange={(e) => updateField('province', e.target.value)}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="city">City *</Label>
                      <Input
                        id="city"
                        placeholder="e.g., San Francisco"
                        value={formData.city}
                        onChange={(e) => updateField('city', e.target.value)}
                      />
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {currentStep === 2 && (
              <motion.div
                key="step2"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                className="space-y-6"
              >
                <div>
                  <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
                    <Building2 className="h-5 w-5 text-purple-600" />
                    Business Information
                  </h2>
                  <p className="text-sm text-muted-foreground mb-6">
                    This information will appear on your invoices and quotes.
                  </p>
                </div>

                <div className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="businessName">Business Name *</Label>
                    <Input
                      id="businessName"
                      placeholder="Acme Inc."
                      value={formData.businessName}
                      onChange={(e) => updateField('businessName', e.target.value)}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="businessAddress">Business Address</Label>
                    <Textarea
                      id="businessAddress"
                      placeholder="123 Main St, Suite 100"
                      value={formData.businessAddress}
                      onChange={(e) => updateField('businessAddress', e.target.value)}
                      rows={3}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="businessPhone">Phone Number</Label>
                      <Input
                        id="businessPhone"
                        type="tel"
                        placeholder="+1 (555) 123-4567"
                        value={formData.businessPhone}
                        onChange={(e) => updateField('businessPhone', e.target.value)}
                      />
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="taxId">Tax ID / EIN</Label>
                      <Input
                        id="taxId"
                        placeholder="12-3456789"
                        value={formData.taxId}
                        onChange={(e) => updateField('taxId', e.target.value)}
                      />
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {currentStep === 3 && (
              <motion.div
                key="step3"
                initial={{ opacity: 0, x: 20 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -20 }}
                className="space-y-6"
              >
                <div>
                  <h2 className="text-xl font-semibold mb-4 flex items-center gap-2">
                    <Settings className="h-5 w-5 text-purple-600" />
                    Business Preferences
                  </h2>
                  <p className="text-sm text-muted-foreground mb-6">
                    Customize how you manage invoicing and payments.
                  </p>
                </div>

                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div className="space-y-2">
                      <Label htmlFor="currency">Default Currency</Label>
                      <Select value={formData.currency} onValueChange={(value) => updateField('currency', value)}>
                        <SelectTrigger id="currency">
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          {CURRENCIES.map(curr => (
                            <SelectItem key={curr.value} value={curr.value}>{curr.label}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    <div className="space-y-2">
                      <Label htmlFor="taxRegion">Tax Region</Label>
                      <Input
                        id="taxRegion"
                        placeholder="e.g., US-CA for California"
                        value={formData.taxRegion}
                        onChange={(e) => updateField('taxRegion', e.target.value)}
                      />
                    </div>
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="paymentInstructions">Default Payment Instructions</Label>
                    <Textarea
                      id="paymentInstructions"
                      placeholder="Payment due within 30 days. Please pay via bank transfer to..."
                      value={formData.paymentInstructions}
                      onChange={(e) => updateField('paymentInstructions', e.target.value)}
                      rows={3}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="invoiceFooter">Invoice Footer</Label>
                    <Textarea
                      id="invoiceFooter"
                      placeholder="Thank you for your business!"
                      value={formData.invoiceFooter}
                      onChange={(e) => updateField('invoiceFooter', e.target.value)}
                      rows={2}
                    />
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>

          <div className="flex justify-between mt-8 pt-6 border-t">
            <Button
              variant="outline"
              onClick={handleBack}
              disabled={currentStep === 1 || isSubmitting}
              className="gap-2"
            >
              <ArrowLeft className="h-4 w-4" />
              Back
            </Button>

            {currentStep < 3 ? (
              <Button onClick={handleNext} className="gap-2 bg-purple-600 hover:bg-purple-700">
                Next
                <ArrowRight className="h-4 w-4" />
              </Button>
            ) : (
              <Button 
                onClick={handleComplete} 
                disabled={isSubmitting}
                className="gap-2 bg-purple-600 hover:bg-purple-700"
              >
                {isSubmitting ? 'Setting up...' : 'Complete Setup'}
                <CheckCircle className="h-4 w-4" />
              </Button>
            )}
          </div>

          <div className="mt-4 text-center">
            <button
              onClick={() => {
                localStorage.setItem('lumenr-onboarding-completed', 'true');
                localStorage.removeItem('lumenr-onboarding-progress');
                router.push('/dashboard');
              }}
              className="text-sm text-muted-foreground hover:text-foreground transition-colors"
              disabled={isSubmitting}
            >
              Skip for now
            </button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

src/components/ui/feature-lock.tsx.content:
import { ReactNode } from 'react'
import { useSubscription } from '@/hooks/useSubscription'
import { Button } from '@/components/ui/button'
import { Lock, Crown } from 'lucide-react'
import { toast } from 'sonner'
import { useRouter } from 'next/navigation'
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from '@/components/ui/tooltip'

interface FeatureLockProps {
  children: ReactNode
  feature?: string
  showUpgradeToast?: boolean
}

export function FeatureLock({ children, feature, showUpgradeToast = true }: FeatureLockProps) {
  const { hasAccess, needsUpgrade, isTrialing, daysRemaining, loading } = useSubscription()
  const router = useRouter()

  if (loading) {
    return <>{children}</>
  }

  const handleUpgradeClick = (e: React.MouseEvent) => {
    e.preventDefault()
    e.stopPropagation()

    if (showUpgradeToast) {
      if (isTrialing) {
        toast.info(`Trial Period`, {
          description: `You have ${daysRemaining} days remaining in your trial. Upgrade to Pro for continued access.`,
          action: {
            label: 'Upgrade',
            onClick: () => router.push('/billing')
          }
        })
      } else {
        toast.error('Subscription Required', {
          description: 'Your trial has expired. Upgrade to continue using this feature.',
          action: {
            label: 'Upgrade Now',
            onClick: () => router.push('/billing')
          }
        })
      }
    }
  }

  if (!hasAccess) {
    return (
      <TooltipProvider>
        <Tooltip>
          <TooltipTrigger asChild>
            <div className="relative inline-block" onClick={handleUpgradeClick}>
              <div className="opacity-50 pointer-events-none">
                {children}
              </div>
              <div className="absolute inset-0 flex items-center justify-center pointer-events-none">
                <Lock className="h-4 w-4 text-muted-foreground" />
              </div>
            </div>
          </TooltipTrigger>
          <TooltipContent>
            <p>Upgrade required - {feature || 'This feature'} is locked</p>
          </TooltipContent>
        </Tooltip>
      </TooltipProvider>
    )
  }

  return <>{children}</>
}

interface LockedButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  children: ReactNode
  variant?: 'default' | 'destructive' | 'outline' | 'secondary' | 'ghost' | 'link'
  size?: 'default' | 'sm' | 'lg' | 'icon'
  className?: string
  feature?: string
}

export function LockedButton({ children, feature, className, variant, size, ...props }: LockedButtonProps) {
  const { hasAccess, needsUpgrade } = useSubscription()
  const router = useRouter()

  const handleClick = (e: React.MouseEvent<HTMLButtonElement>) => {
    if (!hasAccess) {
      e.preventDefault()
      e.stopPropagation()

      toast.error('Upgrade Required', {
        description: 'Your trial has expired. Upgrade to continue.',
        action: {
          label: 'Upgrade Now',
          onClick: () => router.push('/billing')
        }
      })
      return
    }

    props.onClick?.(e)
  }

  return (
    <TooltipProvider>
      <Tooltip>
        <TooltipTrigger asChild>
          <Button
            {...props}
            variant={variant}
            size={size}
            className={`${className} ${!hasAccess ? 'opacity-50 cursor-not-allowed' : ''}`}
            disabled={props.disabled}
            onClick={handleClick}
          >
            {!hasAccess && <Lock className="h-4 w-4 mr-2" />}
            {children}
          </Button>
        </TooltipTrigger>
        {!hasAccess && (
          <TooltipContent>
            <p>Upgrade required - {feature || 'this feature'} is locked</p>
          </TooltipContent>
        )}
      </Tooltip>
    </TooltipProvider>
  )
}

export function TrialBanner() {
  const { isTrialing, daysRemaining, hasAccess } = useSubscription()
  const router = useRouter()

  if (!isTrialing || !hasAccess) return null

  return (
    <div className="bg-gradient-to-r from-purple-500/10 to-pink-500/10 border border-purple-500/20 rounded-lg p-4 flex items-center justify-between">
      <div className="flex items-center gap-3">
        <Crown className="h-5 w-5 text-purple-500" />
        <div>
          <p className="font-medium text-sm">
            Trial Period - {daysRemaining} days remaining
          </p>
          <p className="text-xs text-muted-foreground">
            Upgrade to Pro for unlimited access to all features
          </p>
        </div>
      </div>
      <Button 
        onClick={() => router.push('/billing')}
        className="bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
        size="sm"
      >
        <Crown className="h-4 w-4 mr-2" />
        Upgrade Now
      </Button>
    </div>
  )
}

src/app/(protected)/documents/page.tsx.content:
'use client';

import { useState, useEffect } from 'react'
import { motion } from 'framer-motion'
import { FileText, Download, Calendar, Filter, FolderOpen, Receipt as ReceiptIcon, FileSignature, DollarSign } from 'lucide-react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select'
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs'
import { toast } from 'sonner'

interface Document {
  id: number
  type: 'quote' | 'invoice' | 'receipt' | 'contract'
  title: string
  clientName?: string
  amount?: number
  date: string
  pdfUrl?: string
  status?: string
}

interface TaxSummary {
  month: string
  totalRevenue: number
  totalExpenses: number
  totalTax: number
  documentCount: number
}

export default function DocumentsPage() {
  const [documents, setDocuments] = useState<Document[]>([])
  const [filteredDocuments, setFilteredDocuments] = useState<Document[]>([])
  const [taxSummaries, setTaxSummaries] = useState<TaxSummary[]>([])
  const [selectedYear, setSelectedYear] = useState(new Date().getFullYear().toString())
  const [selectedMonth, setSelectedMonth] = useState('all')
  const [selectedType, setSelectedType] = useState('all')
  const [isLoading, setIsLoading] = useState(true)

  useEffect(() => {
    loadDocuments()
  }, [])

  useEffect(() => {
    filterDocuments()
    calculateTaxSummaries()
  }, [documents, selectedYear, selectedMonth, selectedType])

  const loadDocuments = async () => {
    setIsLoading(true)
    try {
      const token = localStorage.getItem('bearer_token')
      if (!token) return

      // Load all document types in parallel
      const [quotesRes, invoicesRes, receiptsRes, contractsRes] = await Promise.all([
        fetch('/api/lumenr/quotes', { headers: { 'Authorization': `Bearer ${token}` } }),
        fetch('/api/lumenr/invoices', { headers: { 'Authorization': `Bearer ${token}` } }),
        fetch('/api/lumenr/receipts', { headers: { 'Authorization': `Bearer ${token}` } }),
        fetch('/api/lumenr/contracts', { headers: { 'Authorization': `Bearer ${token}` } })
      ])

      const [quotesData, invoicesData, receiptsData, contractsData] = await Promise.all([
        quotesRes.ok ? quotesRes.json() : { data: [] },
        invoicesRes.ok ? invoicesRes.json() : { data: [] },
        receiptsRes.ok ? receiptsRes.json() : { data: [] },
        contractsRes.ok ? contractsRes.json() : { data: [] }
      ])

      // Transform and combine all documents
      const allDocs: Document[] = [
        ...(quotesData.data || []).filter((q: any) => q.pdfUrl).map((q: any) => ({
          id: q.id,
          type: 'quote' as const,
          title: `Quote #${q.id}`,
          clientName: q.client?.name,
          amount: parseFloat(q.total || 0),
          date: q.createdAt,
          pdfUrl: q.pdfUrl,
          status: q.status
        })),
        ...(invoicesData.data || []).filter((i: any) => i.pdfUrl).map((i: any) => ({
          id: i.id,
          type: 'invoice' as const,
          title: `Invoice #${i.id}`,
          clientName: i.client?.name,
          amount: parseFloat(i.total || 0),
          date: i.createdAt,
          pdfUrl: i.pdfUrl,
          status: i.status
        })),
        ...(receiptsData.data || []).filter((r: any) => r.imageUrl).map((r: any) => ({
          id: r.id,
          type: 'receipt' as const,
          title: `Receipt - ${r.vendor}`,
          amount: parseFloat(r.amount || 0),
          date: r.date,
          pdfUrl: r.imageUrl,
          status: 'processed'
        })),
        ...(contractsData.data || []).filter((c: any) => c.pdfUrl).map((c: any) => ({
          id: c.id,
          type: 'contract' as const,
          title: c.title,
          clientName: c.client?.name,
          amount: parseFloat(c.value || 0),
          date: c.createdAt,
          pdfUrl: c.pdfUrl,
          status: c.status
        }))
      ]

      // Sort by date descending
      allDocs.sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime())

      setDocuments(allDocs)
    } catch (error) {
      console.error('Failed to load documents:', error)
      toast.error('Failed to load documents')
    } finally {
      setIsLoading(false)
    }
  }

  const filterDocuments = () => {
    let filtered = documents

    // Filter by year
    filtered = filtered.filter(doc => {
      const docYear = new Date(doc.date).getFullYear().toString()
      return docYear === selectedYear
    })

    // Filter by month
    if (selectedMonth !== 'all') {
      filtered = filtered.filter(doc => {
        const docMonth = new Date(doc.date).getMonth()
        return docMonth === parseInt(selectedMonth)
      })
    }

    // Filter by type
    if (selectedType !== 'all') {
      filtered = filtered.filter(doc => doc.type === selectedType)
    }

    setFilteredDocuments(filtered)
  }

  const calculateTaxSummaries = () => {
    const summaries: Map<string, TaxSummary> = new Map()

    documents.filter(doc => new Date(doc.date).getFullYear().toString() === selectedYear).forEach(doc => {
      const date = new Date(doc.date)
      const monthKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`
      const monthName = date.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })

      if (!summaries.has(monthKey)) {
        summaries.set(monthKey, {
          month: monthName,
          totalRevenue: 0,
          totalExpenses: 0,
          totalTax: 0,
          documentCount: 0
        })
      }

      const summary = summaries.get(monthKey)!
      summary.documentCount++

      if (doc.type === 'invoice' && doc.amount) {
        summary.totalRevenue += doc.amount
        // Estimate tax at 13% (can be made configurable)
        summary.totalTax += doc.amount * 0.13
      } else if (doc.type === 'receipt' && doc.amount) {
        summary.totalExpenses += doc.amount
      }
    })

    const sortedSummaries = Array.from(summaries.values()).sort((a, b) => {
      return b.month.localeCompare(a.month)
    })

    setTaxSummaries(sortedSummaries)
  }

  const handleDownload = async (doc: Document) => {
    if (!doc.pdfUrl) {
      toast.error('Document URL not available')
      return
    }

    try {
      // Open in new tab for now - can be enhanced with actual download
      window.open(doc.pdfUrl, '_blank')
      toast.success('Document opened in new tab')
    } catch (error) {
      toast.error('Failed to open document')
    }
  }

  const handleExportAll = () => {
    toast.info('Export functionality coming soon!')
  }

  const getDocumentIcon = (type: string) => {
    switch (type) {
      case 'quote': return FileSignature
      case 'invoice': return FileText
      case 'receipt': return ReceiptIcon
      case 'contract': return FileText
      default: return FileText
    }
  }

  const years = Array.from(new Set(documents.map(d => new Date(d.date).getFullYear().toString())))
  const currentYearDocs = years.includes(new Date().getFullYear().toString()) ? years : [new Date().getFullYear().toString(), ...years]

  return (
    <div className="p-3 sm:p-4 lg:p-6 space-y-4 sm:space-y-6 max-w-7xl mx-auto">
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        className="flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4"
      >
        <div>
          <h1 className="text-2xl sm:text-3xl font-bold text-foreground flex items-center gap-2">
            <FolderOpen className="h-6 w-6 sm:h-8 sm:w-8" />
            Tax & Documents
          </h1>
          <p className="text-muted-foreground text-sm sm:text-base mt-1">
            Centralized document management and tax summaries
          </p>
        </div>
        <Button onClick={handleExportAll} className="gap-2">
          <Download className="h-4 w-4" />
          Export All
        </Button>
      </motion.div>

      {/* Filters */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
      >
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2 text-lg">
              <Filter className="h-5 w-5" />
              Filters
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
              <div className="space-y-2">
                <label className="text-sm font-medium">Year</label>
                <Select value={selectedYear} onValueChange={setSelectedYear}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {currentYearDocs.map(year => (
                      <SelectItem key={year} value={year}>{year}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Month</label>
                <Select value={selectedMonth} onValueChange={setSelectedMonth}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Months</SelectItem>
                    {Array.from({ length: 12 }, (_, i) => (
                      <SelectItem key={i} value={i.toString()}>
                        {new Date(2025, i).toLocaleDateString('en-US', { month: 'long' })}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <label className="text-sm font-medium">Document Type</label>
                <Select value={selectedType} onValueChange={setSelectedType}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Types</SelectItem>
                    <SelectItem value="quote">Quotes</SelectItem>
                    <SelectItem value="invoice">Invoices</SelectItem>
                    <SelectItem value="receipt">Receipts</SelectItem>
                    <SelectItem value="contract">Contracts</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </CardContent>
        </Card>
      </motion.div>

      {/* Tax Summaries */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
      >
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <DollarSign className="h-5 w-5" />
              Tax Summary for {selectedYear}
            </CardTitle>
          </CardHeader>
          <CardContent>
            {taxSummaries.length === 0 ? (
              <p className="text-muted-foreground text-center py-8">No data for this period</p>
            ) : (
              <div className="space-y-4">
                {taxSummaries.map((summary, index) => (
                  <div key={index} className="flex items-center justify-between p-4 rounded-lg bg-secondary/50 hover:bg-secondary transition-colors">
                    <div>
                      <p className="font-medium">{summary.month}</p>
                      <p className="text-sm text-muted-foreground">{summary.documentCount} documents</p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-muted-foreground">Revenue</p>
                      <p className="font-semibold text-green-600 dark:text-green-400">
                        ${summary.totalRevenue.toFixed(2)}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-muted-foreground">Expenses</p>
                      <p className="font-semibold text-red-600 dark:text-red-400">
                        ${summary.totalExpenses.toFixed(2)}
                      </p>
                    </div>
                    <div className="text-right">
                      <p className="text-sm text-muted-foreground">Est. Tax</p>
                      <p className="font-semibold">
                        ${summary.totalTax.toFixed(2)}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </motion.div>

      {/* Documents List */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.3 }}
      >
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <FileText className="h-5 w-5" />
              Documents ({filteredDocuments.length})
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <p className="text-center text-muted-foreground py-8">Loading documents...</p>
            ) : filteredDocuments.length === 0 ? (
              <p className="text-center text-muted-foreground py-8">
                No documents found for the selected filters
              </p>
            ) : (
              <div className="space-y-2">
                {filteredDocuments.map((doc) => {
                  const Icon = getDocumentIcon(doc.type)
                  return (
                    <div
                      key={`${doc.type}-${doc.id}`}
                      className="flex items-center justify-between p-4 rounded-lg border border-border hover:bg-secondary/50 transition-colors"
                    >
                      <div className="flex items-center gap-3 flex-1">
                        <Icon className="h-5 w-5 text-muted-foreground" />
                        <div>
                          <p className="font-medium">{doc.title}</p>
                          <div className="flex items-center gap-3 text-sm text-muted-foreground">
                            <span>{new Date(doc.date).toLocaleDateString()}</span>
                            {doc.clientName && <span>â€¢ {doc.clientName}</span>}
                            {doc.amount && <span>â€¢ ${doc.amount.toFixed(2)}</span>}
                          </div>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="text-xs px-2 py-1 rounded-full bg-primary/10 text-primary capitalize">
                          {doc.type}
                        </span>
                        <Button
                          size="sm"
                          variant="outline"
                          onClick={() => handleDownload(doc)}
                          className="gap-2"
                        >
                          <Download className="h-4 w-4" />
                          View
                        </Button>
                      </div>
                    </div>
                  )
                })}
              </div>
            )}
          </CardContent>
        </Card>
      </motion.div>
    </div>
  )
}

