'use client';

import { useState, useRef, useEffect } from 'react';
import { Input } from './input';
import { useAutofill } from '@/lib/hooks/useAutofill';
import { cn } from '@/lib/utils';

interface AutocompleteInputProps extends React.InputHTMLAttributes<HTMLInputElement> {
  fieldName: string;
  context?: string;
  onValueSelect?: (value: string) => void;
}

export function AutocompleteInput({
  fieldName,
  context,
  value,
  onChange,
  onValueSelect,
  onBlur,
  className,
  ...props
}: AutocompleteInputProps) {
  const { suggestions, recordEntry } = useAutofill(fieldName, context);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [highlightedIndex, setHighlightedIndex] = useState(0);
  const inputRef = useRef<HTMLInputElement>(null);
  const suggestionsRef = useRef<HTMLDivElement>(null);

  const filteredSuggestions = suggestions.filter(s =>
    s.toLowerCase().includes((value as string || '').toLowerCase())
  );

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    onChange?.(e);
    setShowSuggestions(true);
    setHighlightedIndex(0);
  };

  const handleSuggestionClick = (suggestion: string) => {
    const event = {
      target: { value: suggestion }
    } as React.ChangeEvent<HTMLInputElement>;
    onChange?.(event);
    onValueSelect?.(suggestion);
    recordEntry(suggestion);
    setShowSuggestions(false);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (!showSuggestions || filteredSuggestions.length === 0) return;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setHighlightedIndex(prev =>
          prev < filteredSuggestions.length - 1 ? prev + 1 : 0
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        setHighlightedIndex(prev =>
          prev > 0 ? prev - 1 : filteredSuggestions.length - 1
        );
        break;
      case 'Enter':
        if (filteredSuggestions[highlightedIndex]) {
          e.preventDefault();
          handleSuggestionClick(filteredSuggestions[highlightedIndex]);
        }
        break;
      case 'Escape':
        setShowSuggestions(false);
        break;
    }
  };

  const handleInputBlur = (e: React.FocusEvent<HTMLInputElement>) => {
    // Delay to allow clicking on suggestions
    setTimeout(() => {
      setShowSuggestions(false);
      if (value && typeof value === 'string' && value.trim().length > 0) {
        recordEntry(value);
      }
    }, 200);
    onBlur?.(e);
  };

  useEffect(() => {
    // Click outside to close suggestions
    const handleClickOutside = (event: MouseEvent) => {
      if (
        suggestionsRef.current &&
        !suggestionsRef.current.contains(event.target as Node) &&
        inputRef.current &&
        !inputRef.current.contains(event.target as Node)
      ) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <div className="relative">
      <Input
        ref={inputRef}
        value={value}
        onChange={handleInputChange}
        onFocus={() => setShowSuggestions(true)}
        onBlur={handleInputBlur}
        onKeyDown={handleKeyDown}
        className={className}
        {...props}
      />
      {showSuggestions && filteredSuggestions.length > 0 && (
        <div
          ref={suggestionsRef}
          className="absolute z-50 w-full mt-1 bg-popover border border-border rounded-md shadow-lg max-h-60 overflow-auto"
        >
          {filteredSuggestions.map((suggestion, index) => (
            <div
              key={index}
              className={cn(
                'px-3 py-2 cursor-pointer text-sm',
                'hover:bg-accent hover:text-accent-foreground',
                index === highlightedIndex && 'bg-accent text-accent-foreground'
              )}
              onClick={() => handleSuggestionClick(suggestion)}
            >
              {suggestion}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

src/lib/hooks/useAutofill.ts.content:
import { useState, useEffect } from 'react';

interface AutofillSuggestion {
  value: string;
  frequency: number;
  lastUsed: Date;
}

/**
 * Hook for AI-powered autofill that learns from previous entries
 * Analyzes historical data to provide intelligent suggestions
 */
export function useAutofill(fieldName: string, context?: string) {
  const [suggestions, setSuggestions] = useState<string[]>([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    loadSuggestions();
  }, [fieldName, context]);

  const loadSuggestions = async () => {
    setLoading(true);
    try {
      // Load from localStorage for now - can be enhanced with API calls later
      const storageKey = `autofill_${fieldName}_${context || 'default'}`;
      const stored = localStorage.getItem(storageKey);

      if (stored) {
        const data: AutofillSuggestion[] = JSON.parse(stored);
        // Sort by frequency and recency
        const sorted = data
          .sort((a, b) => {
            const scoreA = a.frequency + (new Date(a.lastUsed).getTime() / 1000000000);
            const scoreB = b.frequency + (new Date(b.lastUsed).getTime() / 1000000000);
            return scoreB - scoreA;
          })
          .slice(0, 5)
          .map(s => s.value);

        setSuggestions(sorted);
      }
    } catch (error) {
      console.error('Failed to load autofill suggestions:', error);
    } finally {
      setLoading(false);
    }
  };

  const recordEntry = (value: string) => {
    if (!value || value.trim().length === 0) return;

    try {
      const storageKey = `autofill_${fieldName}_${context || 'default'}`;
      const stored = localStorage.getItem(storageKey);
      let data: AutofillSuggestion[] = stored ? JSON.parse(stored) : [];

      // Update or add entry
      const existing = data.find(s => s.value === value);
      if (existing) {
        existing.frequency += 1;
        existing.lastUsed = new Date();
      } else {
        data.push({
          value,
          frequency: 1,
          lastUsed: new Date()
        });
      }

      // Keep only top 20 entries to avoid bloating storage
      data = data
        .sort((a, b) => b.frequency - a.frequency)
        .slice(0, 20);

      localStorage.setItem(storageKey, JSON.stringify(data));
    } catch (error) {
      console.error('Failed to record autofill entry:', error);
    }
  };

  return {
    suggestions,
    loading,
    recordEntry
  };
}

